css:
@-moz-document domain("todoist.com")
{
/* background colors navigation
*/
body {font-family: -apple-system,".SFNSDisplay-Regular","Segoe UI","Helvetica",Arial,sans-serif !important;-webkit-font-smoothing: antialiased;letter-spacing: inherit;} 

/* improved task counter clarity
*/

.counter  {color: #285a96 !important; opacity: 0.9 !important;font-weight:500 !important; font-size:85% !important;}
*.counter_count {color: #285a96 !important;opacity: 0.9 !important;font-weight:500!important;font-size:85%!important;}

.counter span:before {
    content: '('!important;
}
.counter span:after {
    content: ')'!important;
}

.counter_count span:before {
    content: '('!important;
}

*.counter_count span:after {
    content: ')'!important;
}





#content {background-color: #ffffff !important;}
#page_background  {background-color: #fafafa !important;}

/* accent background colors (search box, tick box)
*/

input {background-color: #DBE6EE;}

.task_item .task_content_item {
border-bottom: 1px solid #DBE6EE !important;
}

.ist_checkbox {
    transition: background 100ms ease-in;
    transition-property: background;
    transition-duration: 50ms;
    transition-timing-function: ease-in;
    transition-delay: initial;
}
a.ex_link {
       cursor: pointer !important;
    text-decoration: none;
    padding-left: 22px!important;
    Color:#65B6EC!important;}


.ex_link, .task_item a:before {
   background: url("https://image.ibb.co/hhM64F/netlink3.png") 0 0 no-repeat;
    background-position: 0 -191px!important;
  }

.task_item span a {
    color: #65B6EC;}

.ist_checkbox {
    margin-top: 0px!important;}



