@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.torrentleech.org") {
  @media screen and (max-width: 900px) {
    #torrents {
      width: 85%;
      margin:0;
     
    }
    .col-xs-2 {
      
      width: 0% !important;
    }
    
    td .title {
      font-size: 1.3em;
    }
    th:nth-of-type(2), td:nth-of-type(2) {display: block; width: 80%;}
    td:nth-of-type(4), th:nth-of-type(4) { display: none;}
    td:nth-of-type(6),th:nth-of-type(6) { display: none;}
    td:nth-of-type(7),th:nth-of-type(7) { display: none;}
    td:nth-of-type(8),th:nth-of-type(8) { display: none;}
    td:nth-of-type(5),th:nth-of-type(5)  { display: none;}
    td:nth-of-type(9), th:nth-of-type(9) { display: none;}
	
    .fa-download {
    font-size: 3rem !important;
  	}
    
  }
  
    }
