css:
@-moz-document domain("www.kekenet.com")
{
/*将网站都放大125%*/
html {
	zoom:125%;
}

/*去掉扫一扫微信公众号，APP扫一扫二维码*/
#weixinwz1, #weixinwz {
display:none;
}
/*去掉广告*/
.topad {
    padding-bottom: 2px;
    background: #fff;
    display: none;
}
/*去掉首页轮播*/
.lastPage_right > div:first-child {
display:none;
}
div.one_t {
    clear: both;
    margin-bottom: 8px;
    display: none;
}

iv.h_time {
    float: left;
    width: 600px;
    height: 310px;
    overflow: hidden;
	}
	
/*去掉新增节目*/
dl.r_th, dl.r_d_th {
    clear: both;
 	height: 0px;
    line-height: 28px;
    overflow: hidden;
    background: white;
    border-bottom: 0px solid #38a3db;
}

dl.r_th, dl.r_d_th ,ul.r_rec {
    clear: both;
    width: 94%;
    margin: 8px auto;
	display:none;
  
}
div.h_one_r {
    clear: both;
 	height:220px;	  
    overflow: hidden;
     margin-top: 20px;
}



/*去掉“今日更新”旁边的广告图片*/
div.h_news_d {
	display:none;
}	

/*调整“今日更新”宽度*/
div.h_time {
    float: left;
    width: 600px;
    height: 310px;
    overflow: hidden;
}
/*调整“今日更新，编辑推荐”的链接*/
#con1 .f14 {
   font-size: 12px;
   margin-left:20px;
}
ul.home_rec li {
    text-align: left;
    clear: both;
    height: 24px;
    line-height: 24px;
    width: 490px;
    overflow: hidden;
    font-size: 12px;
}

	/*便于编写代码，将所有的div都加上了边框,*/
.clear_div , .h_time {
	/*border:1px solid red;*/
}

}
@-moz-document url-prefix("http://my.kekenet.com/"),url-prefix("http://word.kekenet.com/"),url-prefix("http://xue.kekenet.com/"),url-prefix("http://talk.kekenet.com/"),url-prefix("http://www.kekenet.com/Article/"),url-prefix("http://www.kekenet.com/broadcast/")
{
/*将网站都放大125%*/
html {
	zoom:125%;
}
#weixinwz1 {
    display: none;
}

/*去广告*/
#bdc_ad + div {
	display:none;
}
  /*去掉文章听力下面的空白*/
#show_box {
    width: 100%;
    height: 50px;
    border: 1px solid #e0e0e0;
    overflow: hidden;
    position: relative;
}
 /*去掉二维码*/ 
#qrcode_box {
    position: absolute;
    bottom: 0;
    text-align: center;
    height: 0px;
    width: 100%;
    z-index: 100;
    background-image: linear-gradient(rgba(255,255,255,0.7),rgba(255,255,255,1));
 }
/*去掉右边两个广告二维码栏*/
.lastPage_right {
    float: right;
    width: 0px;
    margin-left: 6px;
}
    
}
