css:
@-moz-document domain("forums.overclockers.com.au") {
html {
    background-color: rgb(0,0,0);
}

body {
    color: rgb(190,190,190);
}

::selection {
  background: #b40; /* WebKit/Blink Browsers */
}

.emCtrl:hover, .emCtrl:focus, .ugc a:hover, .ugc a:focus {
    box-shadow: 5px 5px 7px #000;
 }

#header {
    background-color: #410;
}

html {
    color: rgb(200, 0, 0);
    background: rgb(10, 10, 10);
}
#content .pageContent {
    background-color: rgb(10, 10, 0);
}

.sectionMain {
    background-color: rgb(80, 20, 0);

}

.ugc a:hover, .ugc a:focus {
    color: #F00;
    background: #700}

li {
    color: #aaa;
    background-color: #310;
}

.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: rgb(50, 10, 0);
  	color: rgb(255,255,255);
}

.primaryContent a {
    color: #990000;
}

.node .nodeLastPost {
    background: black;
}

a:link, a:visited {
    color: #FF0000;
    
}

.Menu .primaryContent {
    background-color: rgba(0, 0, 0, 0.96);
}

.Menu .sectionFooter {
    background-color: rgba(0, 0, 0, 0.9);
}

.Menu .secondaryContent {
    background-color: rgba(100, 20, 0, 0.96);
}
.Menu {
    background-color: #000000; 
    border: 1px solid #ff0000;
    border-top: 5px solid #ff0000;
}
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    color: #ffff00;
    background: #000000 ;
  
    text-shadow: 1px 1px 2px #2b485c;
}

.blockLinksList a, .blockLinksList label {
    color: #FF3000;

}
.navTabs .navTab.selected .tabLinks {
    background: #000000 ;

}


.navTabs .navTab.selected .tabLinks a {
    text-shadow: 1px 1px 1px #000;
}


.navTabs {
    font-size: 11px;
    background-color: #410;
    border: 1px solid #000;
    border-bottom: 1px solid #ff0000;
    color: #FFF

}

.primaryContent {
    background-color: rgb(50, 20, 20);

}
.messageUserBlock {
    background: #220000 ;
    border: 1px solid #ff0000;

}
.messageUserBlock div.avatarHolder {
    background-color: #810;

}

.bbCodeQuote .quoteContainer {
    background: #100000 ;

}

.bbCodeQuote .attribution {
    color: rgb(255,255,255);
    background: #550000 ;
    border-bottom: 1px solid #AA0000;
}

.avatar img, .avatar .img, .avatarCropper {
    background-color: rgb(0, 0, 0);    padding: 2px;
    border: 1px solid #000;
}
.breadcrumb {
    background: #000 ;
    border: 1px solid #ff0000;

}

.breadcrumb .crust a.crumb {
    background-color: #410;
}

.breadcrumb .crust .arrow span {
    border-left-color: #410;
 }


.breadcrumb .crust .arrow {
    border-left-color: #F00;
}

.redactor_toolbar {
    background: #000 ;
    border-bottom: 1px solid #FF0000;
 
}
.redactor_toolbar li {
    background: #A00;
}

.bbCodeQuote .quoteContainer .quoteExpand {
    background: linear-gradient(to bottom, rgba(100, 0, 0, 0) 0%, #410 80%);
    }

.PageNav a.currentPage {
    color: #000;
    background-color: #a00;
    border-color: #000;
 }
.PageNav a {
    color: #ff0000;
    text-decoration: none;
    background-color: #410;
    border: 1px solid #F00;
    border-radius: 3px;
    text-align: center;
    width: 19px;
}

.textCtrl {
    color: #fff;
    background: #900;
    border-top-color: #900;
    border-right-color: #900;
    border-bottom-color: #900;
    border-left-color: #900;
}

#QuickSearch {
    background-color: #900;
}
.formPopup {
    background-color: #900;
}

#logo img {
    filter: hue-rotate(160deg) brightness(.705) contrast(4.5);
    content: url(https://forums.overclockers.com.au/styles/default/xenforo/ocau_xenforo_logo.png);
}

.navTabs .navTab.selected .navLink {
    color: #fff;
    background: #410 ;
    border: 1px solid #000;
    text-shadow: 0px 0px 3px #000;
}
.navTabs .navTab.PopupClosed .navLink {
    color: #f00;
}

.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: #fff;
    background-color: #A00;
    border: 1px solid #000;
    text-shadow: 1px 1px 0px #000;
 }

.tabs {
    font-size: 11px;
    background: #000 ;
    padding: 0 10px;
    border-bottom: 1px solid #a00;

}

.tabs li a, .tabs.noLinks li {
    color: #ccc;
    background: #410  repeat-x top;
    border: 1px solid #f00;
  
}
.tabs li.active a, .tabs.noLinks li.active {
    background-color: #000;
}

.discussionListItem {
    background-color: #210;
    border-bottom: 1px solid #f00;
}

.posterAvatar, .discussionListItem .stats {
    background: #310  repeat-x top;
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
    background: #310  repeat-x top;
}

.discussionList .sectionHeaders {
    font-size: 11px;
    color: #fff;
    background: #400  repeat-x top;
	border-top: 1px solid #f00;
    border-bottom: 1px solid #f00;

}
.discussionList .sectionHeaders a, .discussionList .sectionHeaders a span {
    color: white;
}
 .secondaryContent {
    background: #000;
    padding: 10px;
    border-bottom: 1px solid #d7edfc;
}

.nodeList .categoryStrip {
    color: #fff;
    background: #000 repeat-x top;
    border-top: 1px solid #f00;
    border-bottom: 1px solid #f00;
 
    border-bottom-color: #f00;
    border-radius: 5px;
 }

.nodeList .categoryStrip .nodeTitle a {
    color: #f00;
}

.message .newIndicator {
    color: #fff;
    background: #000 ;
    border: 1px solid #f00;
}

.button {
    color: #FFF;
    background: rgb(0,0,0) ;
    border: 1px solid rgb(221, 0, 0);
    border-top-color: rgb(255, 0, 0);
    border-bottom-color: rgb(179, 0, 0);
    box-shadow: 0px 1px 4px 0px rgb(0,0,0);
}
.button.primary {
    background-color: #410;
}

.button:hover, .button[href]:hover, .buttonProxy:hover .button {
    color: #000;
    text-decoration: none;
    background-color: rgb(190,0,0);
    border-color: rgb(255,0,0);
    border-top-color: red;
    border-bottom-color: red;
}

.breadcrumb .crust:hover a.crumb {
    background-color: #a00;
}

.breadcrumb .crust:hover .arrow span {
    border-left-color: #A00;
}

a.callToAction span {
    color: #ffffff;
    background: #000 ;
}

a.callToAction {
    background: #000 ;
    border: 1px solid #f00;
    box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
}


a.callToAction:hover span {
    background-color: #A00;
}

a.callToAction:active span {
    background-color: #410;
}

.footer .pageContent {
    color: #a5cae4;
    background-color: #410;
}

.sectionFooter {
    color: #ddd;
    background: #410 ;
    border-bottom: 1px solid #f00;
}

#DiscussionListOptionsHandle a {
    color: #fdd;
    background-color: #410;
    border: 1px solid #f00;

}


.footer a, .footer a:visited {
    color: #a00;
  
}
.bbCodeBlock pre, .bbCodeBlock .code {
    background: #000;
  }
.bbCodeBlock .type {
    color: #000;
    background: #A00;
    border-bottom: 1px solid #a00;
}
.subHeading {
    color: #000;
    background: #f00;
}

.textCtrl:focus, .textCtrl.Focus {
    background: rgb(0,0,0) ;
    border-top-color: rgb(150,0,0);
    border-bottom-color: rgb(230,0,0);
}
.formPopup .controlsWrapper {
    background: #000;
}
.heading, .xenForm .formHeader {
    background-color: #a00;
}
.prefix.prefixPrimary {
    color: #000;
    background-color: #A00;
    border-color: #F00;
}
.messageUserBlock .arrow span {
    border-left-color: #f00;
 }

.messageUserBlock .arrow {
    border-left-color: #000;
 }

.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    background-color: #a00;
}
.blockLinksList a:active, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    background-color: #b00;
}
.navPopup .PopupItemLinkActive:hover {
    background-color: #a00;
}
.navPopup .PopupItemLinkActive:active {
    background-color: #b00;
}

.navPopup .listItem {
    border-bottom: 1px solid #a00;
}
textarea.textCtrl.Elastic {
    
    background: #300;
}
.redactor_toolbar li.redactor_btn_group ul {
    background: #000;
    border: 1px solid #f00;
}

.navigationSideBar a:hover {
    background: #a00;
    color: #f00;
    text-decoration: none;
}
#headerMover #headerProxy {
    background-color: #410;
    
}

.navTabs .navTab.PopupClosed:hover {
    background-color: #a00;
}

.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: #f00;
}
.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading {
    color: #a00;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    color: #f00;
    border-bottom: 1px solid #f00;
}

.tagList .tag {
    background: #a00;
    border: 1px solid #410;
    color: #111 !important;
}

.tagList .tag .arrow {
    border-right-color: #a00;
}

.tagList .tag .arrow:after {

    border-right-color: #a00;
}

.pollBlock .question .questionText {
    color: #f00;
}


.pollBlock .questionMark {

    background: #f00;
    color: #000;

}

.pollBlock .pollOption label:hover {
    background: #a00;
   
}

.tagList .tag:hover {
    background-color: #f00;
}

.tagList .tag:hover .arrow:after {

    border-right-color: #f00;
}
.xenOverlay .formOverlay a.button {
    color: #888;
}

a:link, a:visited, a:focus, a:active {
    color: #fee !important;
}

.profilePage .tabs.mainTabs {

    background: #000;
}

.profilePage .tabs.mainTabs li.active a {
    background-color: #610;
}
.hasJs .bbCodeSpoilerText {
    background-color: #000;
    border: 1px solid #d7edfc;
}


.messageText {
	font-family: Helvetica,Arial,sans-serif;
}


html {
	font-family: Helvetica,Arial,sans-serif;
 }

.redactor_toolbar li {
    background: #bbb;
}

body .itemPageNav a {
    background: #000 ;

}

body .itemPageNav a:hover {
    background-color: #f00;
}

.button.primary {
    color: #fbb;
}

.navTabs .navLink, .navTabs .SplitCtrl {
background-color: black ;
border-color: black;
}

.messageUserBlock .extraUserInfo {
    background-color: rgb(0, 0, 0);
 }


.xenTooltip {
    background: rgba(0,0,0,0.9);
}

#exposeMask{
    background-color: rgb(0, 0, 0) !important;
}

div.ribbonBox .ribbon-wrapper .ribbon {
    background-color: rgb(0, 0, 0);
}


.messageNotices li {
    background: rgb(40, 40, 40);
}

}
