@-moz-document domain("seasonvar.ru") {
/* html body */
html body {padding-top: 1px !important; background-color: #5D4843;}

/* body */
body {padding-top: 1px !important;}

 /*Цвет*/
.uppage {background-color: #9E0505; color: #34D00E;}
.rside-result {background-color: #7E645D;}
.lside-serial a.block {color: #ce2270;}
.pgs-marks-bn .pgs-marks-el a {background-color: #6ACFD5;}
.doptxt {background-color: #5d4843 !important;}
.content-wrap {background-color: #5D4843; height: 690px;}
.tabs-result>li.act {background-color: #5D4843;}
.pgs-seaslist .tabs-title li, .pgs-seaslist .tabs-title li:hover {background: #de3c3c; color: rgb(255, 238, 7);}
.pgs-seaslist .tabs-result {background-color: #5D4843;}
.pgs-mark_line-set span,.tabs-result { background-color: #392E29;}
.pgs-sinfo,.pgs-seaslist a {color: #fff;}
.pgs-seaslist span,.lside-serial a {color: #fff;}
.pgs-sinfo-actor-title,.pgs-sinfo-actor-title,.pgs-marks-name,.pgs-marks-mess, .pgs-marks-time,.pgs-marks-target label input[type=checkbox]+span {color: #fff;}
.pgs-sinfo_list.rating span,.pgs-stags-form-lnk,.pgs-marks-txt {color: #80C7E4;}
.pgs-mark_line-cur { background-color: #17437E; color: #fff;border-radius: 5px 5px 0 0;}
.pgs-mark_line-switch .html {color: #fff; background-color: #9e0505;}
.pgs-sinfo-actor a {color: #fbff07;}
.pgs-seaslist a:hover, .pgs-seaslist span
{color:#80C7E4}
.pgs-sinfo-actor a:hover,.pgs-mark_line-cur span[data-click]:hover
{color:#2CC50A}
.btn-abs {background-image: linear-gradient(#87f771, #23a00b); border-right: 1;}
 /*Конец Цвет*/

 /*Бордер кнопок Отметка*/
.pgs-mark_line-set span {border-style: solid; border-color: #f2a31b;}
.header_txt {font-size: 12px;border-top-left-radius: 5px;border-top-right-radius: 5px;background-color: #967b75;color: #ffed00;
	text-shadow: 0px 0px 0 hsla(0,0%,100%,.9);}
.pgs-marks-el a {text-decoration: none;border-radius: 5px;display: block;box-shadow: 0 0 5px rgb(255, 255, 253);}
 /*Конец Бордер*/

/* noindex */
noindex,
.lside-player div div {
    display: none !important;
    position: absolute;
    left: -9999px;
}
noindex,
.td-for-left-block,
#___plusone_1td,
#player_msoc,
div [align="center"] {
    display: none !important;
    position: absolute;
    left: -9999px;
}
.pgs-player-inside,
.pgs-player-inside object {
    height: 600px;
}
.middle.left {
    border-left-width: 20px !important;
    padding-left: 20px !important;
}
.middle {
    background-color: #edeef0;
    box-shadow: 0 0 0 0;
}
/* конец noindex */

/* border */
.news,
.rside-title,
.tabs-title,
.tabs-result {
    border-bottom: 0;
    border: 0;
}
.pgs-mark_line-set span,
.pgs-mark_line-switch.act .html,
.pgs-mark_line-switch .flash,
.rside-title li.act,
.rside-title li:hover,
.tabs-title li.act,
.tabs-title li:hover {
    background-image: linear-gradient(#9e85e5, #1e016e);
    border-right: 1;
}
.head-btn .btn-abs.today {
    background-color: #f6bc23;
}
/* конец border */

/* border-radius */
.wrapper,
.tabs-title-fulwidth li:last-child,
.tabs-title.one li:last-child,
.tabs-title li.act,
.tabs-title li:hover,
.tabs-title,
.tabs-title-fulwidth li:last-child,
.tabs-title.one li:last-child,
.tabs-result,
.pgs-seaslist .tabs-result,
.content-wrap,
.pgs-mark_line-switch .flash,
.pgs-mark_line-switch .html,
.pgs-mark_line-set span,
.pgs-sinfo-img,
.pgs-sinfo-img img,
.headblock-search-txt,
.headblock,
.head-btn,
.btn,
.head-btn[data-menu]:after,
.head-btn[data-menu] img,
.rside-title li:first-child,
.pst,
.pst img,
.poster:after,
.poster:before,
.news-head,
.rside-title li:last-child,
.middle,
.news-head:before {
    border-radius: 0;
}
/* конец border-radius */

 /*Скрыть*/
.lside.sidebar,
.header_icon,
.header_txt,
.pgs-back,
.pgs-stags-wrap,
.pgs-sinfo-menu,
.one.tabs-title,
.pgs-sinfo-actor-title,
.tabs-close:nth-of-type(6) > .tabs-title,
.tabs-close:nth-of-type(7) > .tabs-close-btn,
.tabs-close:nth-of-type(7) > .one.tabs-title,
.pgs-afterplay.tabs-result,
.pgs-review.tabs-result,
.footer,
.act.tabs-close-btn,
.pgs-head,
.news-head:before,
.infoBox.svnotify {
    display: none !important;
}
/* конец Скрыть  */

/* box-shadow */
.header,
.uppage,
.btn-abs,
.pgs-mark_line-set span,
.pst,
.content-wrap,
.pgs-seaslist .tabs-result,
.news-head,
.news-head:after {
    box-shadow: 0 0 0 0;
}
/* конец box-shadow */

/* background: url  */
.header_line:before,
.header_line,
.header_line:after {
    background: url(https://www.s.comm/)
}
.header_line {
    background-color: #edeef0;
}
/* конец background: url  */
.header_line {
    background-color: #392E29;
}
.middle {
    background-color: #392E29;
    box-shadow: 0 0 0 0;
}
}

@-moz-document url-prefix("http://seasonvar.ru/serial") {
/*Скрыть*/
.tabs-title, .header_icon a, .header_icon span, .pgs-back, .tabs-result-comments, .pgs-afterplay, .pgs-review, .tabs-close-btn,.btn-r, .rside-t_en {display: none !important; visibility: hidden !important;}
 /*Конец Скрыть*/

.middle.left {
   background-color: #392E29 !important;
}
.pgs-nearcarusel {
       padding-top: 20px !important;
}
.pgs-nearcarusel-wrap {
   	height: 220px;
}
.header_line {
    background-color: #392E29;
}
.pgs-mark_line-switch .html {
    background-image: linear-gradient(#ff0000, #9e0505);
    box-shadow: 2px 3px 5px 4px black;
	padding-right: 10px;
	padding-left: 5px;
	border-radius: 3px;
    margin-right: 5px;
    height: 55px;
}
.pgs-mark_line-switch .flash {
    background-image: linear-gradient(#9e85e5, #1e016e);
    box-shadow: 2px 3px 5px 4px black;
	padding-right: 10px;
	padding-left: 5px;
	border-radius: 3px;
    margin-right: 5px;
    height: 55px;
}
.pgs-mark_line-cur {background-image: linear-gradient(#9e85e5, #1e016e);}
.pgs-mark_line-set span {
    text-transform: none;
    text-decoration: underline;
    background-color: #a80076;
    height: 55px;
}




}

@-moz-document url("http://seasonvar.ru/?mod=pause") {
.tabs-title li {
    text-shadow: 0 1px 0 #000;
    color: #fbff5d;
    background-image: linear-gradient(#A7A1A2, #383F4A);
}
.pgs-marks-target {
    text-align: left;
	}

.pgs-marks-target label input[type=checkbox]+span {
    position: relative;
    background-image: linear-gradient(#A7A1A2, #383F4A);
    box-shadow: 2px 3px 5px 4px black;
	padding-right: 10px;
	padding-left: 5px;
	 border-radius: 3px;
}

div.pgs-marks-target label span {
    font-size: 24px;
}

.pgs-marks-bn .pgs-marks-el a {
    background-color: #56322a;
}

.rside-title li {
    background-image: -webkit-linear-gradient(bottom,#eee,#cfcfcf);
    background-image: linear-gradient(0deg,#277111 0,#3cf741);
    text-shadow: 0 0px 0 #000;
    color: #000;
}

.pgs-marks-name {font-size: 36px;}
.pgs-marks-current {font-size: 26px;}
}

@-moz-document url("http://seasonvar.ru/") {
.news {background-color: #9a9393; color: black;}
.news-w {background-color: #989494;}
.news_s {color: #9ff4fd;}
.news a:nth-child(odd) {background-color: #827c7c;}
.news-w {background-color: #989494; box-shadow: inset 0 0 6px 1px black;}
.news_n {padding-left: .8em;}
.rside-title li {
    background-image: -webkit-linear-gradient(bottom,#eee,#cfcfcf);
    background-image: linear-gradient(0deg,#277111 0,#3cf741);
    text-shadow: 0 0px 0 #000;
    color: #000;}
}

@-moz-document url-prefix("http://seasonvar.ru/search") {
.pgs-search-info p {color: #00daff; font-weight: 700;}

.pgs-search-title {color: #0eff00;}

.pgs-search-info a:first-child {color: #ffe200;}

.pgs-search-info span {color: white;}

.pgs-search-info a:first-child+br+a {color: #0eff00;}
}
