@-moz-document domain("keep.google.com") {
/*===========================================================
=				Google Keep - Easy Arabic					=
=============================================================

  @author: Amer Bakeer
  @date: 29 Oct 2017

*/ 

/*
 * Droid Arabic Naskh (Arabic) http://www.google.com/fonts/earlyaccess
 */
@font-face {
  font-family: 'Droid Arabic Naskh';
  font-style: normal;
  font-weight: 400;
  src: url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.eot);
  src: url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.eot?#iefix) format('embedded-opentype'),
       url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.woff2) format('woff2'),
       url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.woff) format('woff'),
       url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.ttf) format('truetype');
}
@font-face {
  font-family: 'Droid Arabic Naskh';
  font-style: normal;
  font-weight: 700;
  src: url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.eot);
  src: url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.eot?#iefix) format('embedded-opentype'),
       url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.woff2) format('woff2'),
       url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.woff) format('woff'),
       url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.ttf) format('truetype');
}

/* BODY: *********** */
body
{
    background-color: #cdcdcd !important;
}


/* FONT ********************************************************************************** */

/* Note Titles Font: *********** */
.notranslate.IZ65Hb-YPqjbf.r4nke-YPqjbf,
/* Top Bar Font: */
.gb_Bd.gb_7d.gb_9d,
.gb_Bd.gb_7d.gb_9d input,
.gb_6d .gb_Qc,
.gb_6d .gb_Te,
.x5ghY-MJoBVe-sKfxWe
{
	font-family: 'Droid Arabic Naskh', 'Tahoma' !important;
}

/* Notes Font: *********** */
.IZ65Hb-YPqjbf,
.gb_jc .gb_mc
{
	font-family: 'Segoe UI', 'Droid Arabic Naskh', 'Tahoma' !important;
	/*unicode-bidi: plaintext;*/
}


/* Top Bar ********************************************************************************** */
.gb_Bd.gb_7d.gb_9d,
.gb_Ed.gb_de.gb_Nd
{
	background-color: #fff !important;
    
}
/* */

  
  
/* Side Bar: ********************************************************************************** */
.gb_Ce.gb_kc.gb_g.gb_lc.gb_mc *
{
	font-family: 'Droid Arabic Naskh', 'Tahoma' !important;
}

.gb_oc,
.gb_pc
{
	font-weight: bold;
	padding-bottom: 1px;
	padding-top: 1px;
    font-size:115%;
}

li.gb_rc.gb_xc {
    padding: 0;
}


/* Notes: ********************************************************************************** */
/* Item: */
.IZ65Hb-YPqjbf
{
	font-size: 110%;
}

.gb_jc .gb_mc
{
	font-weight: normal !important;
	font-size: 100%;
}

/* Note Title: */
.notranslate.IZ65Hb-YPqjbf.r4nke-YPqjbf {
	font-weight: bold;
	font-size: 120%;
	border-bottom: 2px solid rgba(0, 0, 0, 0.2) !important;
	padding-bottom: 8px !important;
	color: #fff !important;
	background-color: rgba(0, 0, 0, 0.7) !important;
}
.notranslate.IZ65Hb-YPqjbf.r4nke-YPqjbf a
{
    color: #fff !important;
    text-decoration: none  !important;
}

/* Note Title Pin: */
.IZ65Hb-nQ1Faf svg {
    fill: rgba(255,255,255,.54) !important;
}
 
.IZ65Hb-bJ69tf .IZ65Hb-nQ1Faf svg ,
.IZ65Hb-nQ1Faf svg:hover {
    fill: rgba(255,255,255,1) !important;
}
  
/* Note Body: */
.IZ65Hb-s2gQvd {
	background-color: rgba(255, 255, 255, 0.6);
}
  
/* Highlight items */
.CmABtb:nth-of-type(odd) {
	background-color: rgba(255, 255, 255, 0.14);
}

.CmABtb:nth-of-type(even) {
	background-color: rgba(0, 0, 0, 0.04);
}

.CmABtb {
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

/* Splitter before completed items:*/
.CmABtb.NYTeh-tJHJj.RNfche {
    background-color: rgba(0, 0, 0, 0.3);
    height: 3px;
    padding:0;
}
.NYTeh-hgDUwe {
    /* display: none; */
    opacity: 0;
}
  /* Completed Items: */
.NYTeh-tJHJj *
{
    display: none;
}
  
/* Highlight the completed items: */
.IZ65Hb-RW7GDd .CmABtb-YPqjbf {
	color: #888;
	border-bottom: 1px dashed rgba(0, 0, 0, 0.4);
	/*background-color: rgba(0, 0, 0, 0.05);*/
}
  
/* Footer top line: */
.IZ65Hb-yePe5c {
    border-top: 1px solid rgba(0,0,0,0.6);
    margin-top: 0;
}

/* ***************************************************************************************** */
}
