css:
@-moz-document domain("www.freechatnow.com") {
body
{background:#222;
color:lightgray;}



.Responsive #content .pageContent {
    padding-left: 10px;
    padding-right: 10px;
    background: #222;
    color: lightgray;
}

.profilePage .primaryUserBlock {
    border-top: 1px solid lightgray;
    background: #222;
}

.profilePage .primaryUserBlock .primaryContent, .profilePage .primaryUserBlock .secondaryContent {
    padding-left: 20px;
    background: #222;
    border: none;
    color: lightgray;
}

.profilePage .tabs.mainTabs {
    padding: 0 20px;
    margin-top: -23px;
    min-height: 23px;
    height: auto;
    position: relative;
    background: #222;
}

.profilePage .tabs.mainTabs li.active a {
    background-color: black;
    color: lightgray;
}

.profilePage .tabs.mainTabs li a {
    padding-left: 22px;
    padding-right: 22px;
    color: lightgray;
    background: black;
    line-height: 22px;
    height: 22px;
    font-weight: bold;
}

#header .pageWidth .pageContent {
    zoom: 1;
    background: #222;
    color: lightgray;
}

#headerMover #header {
    width: 100%;
    position: absolute;
    background: #222;
    top: 0px;
    left: 0px;
    color: lightgray;
}

.navTabs {
    font-size: 11px;
    background-color: #222;
    padding: 0 25px;
    border: 1px solid lightgray;
    border-bottom: 1px solid lightgray;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    height: 25px;
    color: lightgray;
}

.navTabs .navTab.selected .tabLinks {
    background: #222;
    width: 100%;
    padding: 0;
    border: 1px solid lightgray;
    overflow: hidden;
    zoom: 1;
    position: absolute;
    left: 0px;
    top: 27px;
    height: 25px;
    background-position: 0px -25px;
}

.formPopup .primaryControls input.textCtrl {
    margin-bottom: 0;
    background: #222;
    color: lightgray;
}

.formPopup .controlsWrapper {
    background: #222;
    color: lightgray;
    border-radius: 5px;
    padding: 5px;
    margin: 5px 0;
    font-size: 11px;
}

#QuickSearch.active {
    padding-bottom: 5px;
    background: #222;
    color: lightgray;
}

.formPopup {
    width: 250px;
    background-color: #222;
}

.formPopup .controlsWrapper .textCtrl {
    width: 222px;
    background: #222;
    color: lightgray;
}

#QuickSearch input.button.primary {
    float: left;
    width: 110px;
    background: #222;
    color: lightgray;
}

#QuickSearch .submitUnit .button {
    min-width: 0;
    background: #222;
    color: lightgray;
}

#QuickSearch {
    display: block;
    position: absolute;
    right: 20px;
    top: -18px;
    margin: 0;
    background-color: #222;
    border-radius: 5px;
    padding-top: 5px;
    _padding-top: 3px;
    z-index: 7500;
    color: lightgray;
}

.profilePage .mast .section.infoBlock {
    padding: 2px;
    margin-bottom: 20px;
    background: #222;
    border: 1px solid #d7edfc;
    border-radius: 4px;
}

.profilePage .mast .section.infoBlock .primaryContent:last-child, .profilePage .mast .section.infoBlock .secondaryContent:last-child {
    border: none;
    background: #222;
}

.breadcrumb {
    font-size: 11px;
    background: #222;
    border: 1px solid lightgray;
    border-radius: 5px;
    overflow: hidden;
    zoom: 1;
    max-width: 100%;
    height: 24px;
}

.breadcrumb .crust:first-child a.crumb, .breadcrumb .crust.firstVisibleCrumb a.crumb {
    padding-left: 10px;
    border-top-left-radius: 5px;
    background: #222;
    border-bottom-left-radius: 5px;
}

.breadcrumb .crust a.crumb > span {
    display: block;
    text-overflow: ellipsis;
    word-wrap: normal;
    white-space: nowrap;
    background: #222;
    overflow: hidden;
    max-width: 100%;
}

.breadcrumb .crust .arrow span {
    border: 12px solid transparent;
    border-right: 1px none black;
    border-left-color: #f0f7fc;
    -moz-border-right-colors: #f0f7fc;
    display: block;
    position: absolute;
    left: -13px;
    background: #222;
    top: -12px;
    z-index: 51;
    white-space: nowrap;
    overflow: hidden;
    text-indent: 9999px;
    width: 0px;
    height: 0px;
}

.breadcrumb .crust:last-child a.crumb {
    font-weight: bold;
    background: #222;
}

.breadcrumb .crust a.crumb {
    cursor: pointer;
    text-decoration: none;
    padding: 0 10px 0 18px;
    margin-bottom: -1px;
    border-bottom: 1px solid #a5cae4;
    outline: 0 none;
    -moz-outline-style: 0 none;
    background: #222;
    display: block;
        line-height: 24px;
}

.subHeading {
    font-size: 11px;
    color: lightgray;
    background: #222;
    padding: 5px 10px;
    margin: 3px auto 0;
    border-top: lightgray;
    border-bottom: lightgray;
}

.textWithCount.subHeading .text {
    color: lightgray;
}

.textWithCount.subHeading .count {
    margin: -3px 0;
    padding: 2px 6px;
    border-radius: 5px;
    border: lightgray;
    background-color: #222;
    color: lightgray;
}

.profilePage .mast .followBlocks .primaryContent.avatarHeap {
    border: 1px solid lightgray;
    background: #222;
}
.profilePage .mast .sectionFooter {
    border-left: 1px solid #d7edfc;
    border-right: 1px solid #d7edfc;
    text-align: right;
    background: #222;
}

.profilePage .profilePoster textarea {
    height: 54px;
    width: 100%;
    background: #222;
    box-sizing: border-box;
    resize: vertical;
    color: lightgray;
}

.messageSimple .messageInfo {
    margin-left: 65px;
    background: #222;
    color: lightgray;
}

.profilePage .profilePoster {
    padding-bottom: 10px;
    background: #222;
    position: relative;
    color: lightgray;
}

.messageSimple {
    overflow: hidden;
    zoom: 1;
    padding: 0;
    background: #222;
    padding-bottom: 10px;
    margin: 10px 0;
    color: lightgray;
    border-color: lightgray;
}

body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, button, textarea, p, blockquote, th, td {
    margin: 0;
    padding: 0;
    background: #222;
    color:lightgray;
}

.messageSimple .comment, .messageSimple .secondaryContent {
    overflow: hidden;
    zoom: 1;
    margin-top: 2px;
    background: #222;
    border: lightgray;
}
.section {
    background-color: #222;
    margin: 10px auto;
}

.footer .pageContent {
    font-size: 11px;
    color: gray;
    background-color: #222;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
    overflow: hidden;
    zoom: 1;
}
html {
    background-color: #222;
    overflow-y: scroll !important;
    color: lightgray;
}

.xenForm .tabs, .xenFormTabs {
    padding: 5px 30px 0;
    background: #222;
    color: lightgray;
}

.tabs li.active a, .tabs.noLinks li.active {
    background-color: #222;
    padding-bottom: 1px;
    border-bottom: 1px none lightgray;
}

.tabs li a, .tabs.noLinks li {
    color: lightgray;
    text-decoration: none;
    background: #222;
    padding: 0 8px;
    margin-right: -1px;
    margin-bottom: -1px;
    border: 1px solid #a5cae4;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    display: inline-block;
    line-height: 22px;
    cursor: pointer;
    outline: 0 none;
    white-space: nowrap;
    word-wrap: normal;
    height: 22px;
}

.ctrlUnit > dd > * > li:first-child > .textCtrl:first-child {
    margin-top: -3px;
    background: #222;
    color: lightgray;
}

.xenForm .ctrlUnit > dd .textCtrl {
    box-sizing: border-box;
    width: 100%;
    background: #222;
    color: lightgray;
}

.xenForm .ctrlUnit.fullWidth > dd > input, .xenForm .ctrlUnit.submitUnit > dd > input, .xenForm .ctrlUnit.fullWidth > dd > select, .xenForm .ctrlUnit.submitUnit > dd > select, .xenForm .ctrlUnit.fullWidth > dd > textarea, .xenForm .ctrlUnit.submitUnit > dd > textarea, .xenForm .ctrlUnit.fullWidth > dd > ul, .xenForm .ctrlUnit.submitUnit > dd > ul {
    margin-top: 0;
    background: #222;
    color: lightgray;
}

#AccountMenu .menuHeader {
    position: relative;
    background: #222;
    color: lightgray;
}

#AccountMenu .menuColumns {
    overflow: hidden;
    zoom: 1;
    padding: 2px;
    background: #222;
}

.Menu .sectionFooter {
    background-color: #222;
    color: lightgray;
}

.sectionFooter {
    overflow: hidden;
    zoom: 1;
    font-size: 11px;
    color: #65a5d1;
    background: #222;
    padding: 4px 10px;
    border-bottom: 1px solid #a5cae4;
    line-height: 16px;
}

#AccountMenu .statusPoster textarea {
    width: 245px;
    margin: 0;
    resize: vertical;
    background: #222;
    overflow: hidden;
    color: lightgray;
}

.submitUnit .button {
    min-width: 100px;
    background: #222;
    color: lightgray;
}

.Menu {
    /* background-color: #ffffff; */
    font-size: 11px;
    border: 1px solid #6cb2e4;
    border-top: 5px solid #222;
    overflow: hidden;
    box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    min-width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;}

.navTabs .navTab.account .navLink {
    font-weight: bold;
    color: gainsboro;
    background: #222;
}

.Menu .secondaryContent {
    background-color: #222;
}

.secondaryContent {
    background: #222;
    padding: 10px;
    border-bottom: 1px solid #d7edfc;
}

.Menu .primaryContent {
    background-color: #222;
}

#free-chat-now-section {
    color: lightgray;
    background-color: #222;
}

#logo-section {
    color: lightgray;
    background: #222;
    padding-top: 10px;
    padding-bottom: 10px;
}

#live-chat-section {
    color: white;
    background-color: #222;
}

#mobile-chat-section {
    color: white;
    background-color: #222;
}

#chat-room-list > li > .wrapper span {
    display: inline-block;
    position: relative;
    top: 50%;
    transform: translateY(-50%);
    color: white;
    font-weight: bold;
    background: #222;
}

#chat-room-list > li > .wrapper {
    display: block;
    height: 100%;
    width: 100%;
    margin: 5px;
    padding: 2px;
    border: 5px solid lightgray;
    border-radius: 5px;
    background-color: #222;
}

#video-chat-section {
    color: white;
    background-color: #222;
}

#instant-messaging-section {
    color: #00416b;
    background-color: #222;
}

#footer-menu-section {
    color: white;
    background-color: #222;
    padding-top: 15px;
    padding-bottom: 15px;
}

#copyright-section {
    color: white;
    background-color: #222;
    padding-top: 10px;
    padding-bottom: 20px;
}

#header-menu-section {
    color: white;
    background-color: #222;
}

.nodeList {
    zoom: 1;
    background: #222;
    color: lightgray;
}

.nodeList .categoryStrip {
    font-size: 11px;
    color: lightgray;
    background: #222;
    padding: 5px 10px;
    margin: 3px auto 0;
    border-top: 1px solid lightgray;
    border-bottom: 1px solid lightgray;
    padding: 5px 10px;
    margin: 0;
    border-bottom-color: lightgray;
    border-radius: 5px;
    min-height: 6px;
}

.node .nodeInfo.primaryContent, .node .nodeInfo.secondaryContent {
    padding: 0;
    background: #222;
}

.nodeList .categoryStrip .nodeTitle a {
    color: lightgray;
}

.nodeList .categoryStrip .nodeDescription {
    font-size: 10px;
    color: lightgray;
}

.node .nodeLastPost .lastThreadMeta {
    display: block;
    background: #222;
}

.node .nodeLastPost {
    background: #222;
    padding: 10px;
    border-bottom: 1px solid #d7edfc;
    font-size: 11px;
    padding: 3px 10px;
    margin: 10px;
    border: 1px solid #d7edfc;
    border-radius: 3px;
    position: absolute;
    top: 0;
    right: 0;
    line-height: 14px;
    white-space: nowrap;
    word-wrap: normal;
    overflow: hidden;
    width: 210px;
    color: lightgray;
    height: 28px;
}

.sidebar .statusPoster textarea {
    width: 100%;
    margin: 0;
    background: #222;
    box-sizing: border-box;
    resize: vertical;
    overflow: hidden;
    color: lightgray;
}

.discussionListItem .posterAvatar .avatar {
    padding: 5px;
    display: block;
    background: #222;
    width: 36px;
    height: 36px;
    width: auto;
    height: auto;
}

.discussionList .sectionHeaders {
    font-size: 11px;
    color: lightgray;
    background: #222;
    padding: 5px 10px;
    margin: 3px auto 0;
    border-top: 1px solid lightgray;
    border-bottom: 1px solid lightgray;
}

.discussionList .sectionHeaders a {
    display: block;
    color: lightgray;
    outline: none;
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
    background: #222;
}

#DiscussionListOptionsHandle a {
    font-size: 11px;
    color: lightgray;
    padding: 0 20px;
    margin: 0 auto;
    border: #222;
    border-top: 1px none black;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    display: inline-block;
    outline: 0;
    position: relative;
    right: -50%;
}


.messageUserBlock div.avatarHolder {
    background-color: #222;
    padding: 10px;
    border-radius: 4px;
    color: lightgray;
    position: relative;
}

.message .messageInfo {
    padding: 0;
    margin-left: 140px;
    border-bottom: 1px none black;
    background: #222;
    zoom: 1;
    color: lightgray;
}

.blendedEditor .redactor_box.activated .redactor_toolbar {
    max-height: none;
    color: lightgray;
    background: #222;
}

.blendedEditor .redactor_box.activated .redactor_btn_group ul, .blendedEditor .redactor_box.activated iframe, .blendedEditor .redactor_box.activated .redactor_smilies li {
    opacity: 1;
    background: #222;
}
.bbCodeQuote .attribution {
    color: lightgray;
    background: #222;
    border-bottom: 1px solid lightgray;
}

.bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    font-style: italic;
    font-size: 9pt;
    background: #222;
    padding: 10px;
    color: lightgray;
    border-radius: 4px;
}

.bbCodeQuote {
    border-color: lightgray;
    overflow: auto;
}
.messageUserBlock .extraUserInfo {
    font-size: 10px;
    background-color: #222;
    padding: 4px 6px;
    border-radius: 4px;
}

.redactor_toolbar {
    font-size: 0;
    background: #222;
    padding: 0 0 0 2px;
    margin: 0;
    border-bottom: 1px solid #d7edfc;
    position: relative;
    left: 0;
    top: 0;
    line-height: 0;
    list-style: none;
    overflow: hidden;
}

.column.row.row, .row.row.columns {
    float: none;
    background: #222;
}

a.callToAction span {
    font-weight: bold;
    font-size: 11px;
    font-family: Calibri, 'Trebuchet MS', Verdana, Geneva, Arial, Helvetica, sans-serif;
    color: lightgray;
    background: #222;
    padding: 0 15px;
    border-radius: 3px;
    display: block;
    /* text-shadow: 0px 0px 3px rgb(29, 125, 193); */
}

#DiscussionListOptionsHandle a {
    font-size: 11px;
    color: lightgray;
    padding: 0 20px;
    margin: 0 auto;
    background: #222;
    border: #222;
    border-top: 1px none black;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    display: inline-block;
    outline: 0;
    position: relative;
    right: -50%;
}

.navigationSideBar .primaryContent {
    font-size: 11px;
    background: #222;
}

.heading, .xenForm .formHeader {
    font-weight: bold;
    font-size: 11pt;
    color: lightgray;
    background-color: #222;
    padding: 5px 10px;
    margin-bottom: 3px;
    border-bottom: 1px solid rgb(29, 125, 193);
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}

.event {
    overflow: hidden;
    zoom: 1;
    background: #222;
}
.event .content .description em {
    color: lightgray;
}

.memberListItem {
    overflow: hidden;
    zoom: 1;
    background: #222;
}





}
