css:
@-moz-document url("https://habitica.com/") {
body {
    background-color: #474747;
}

h2 {
    color: white;
}

.tasks-list {
    background: #333 !important;
    color: white;
    padding: 1px !important;
}

.task-content {
    background: #222 !important;
    color: white !important;
}

.markdown {
    color: white !important;
}

.task-habit-disabled {
	background: #222;
}

.todo .left-control {
    background: #9358f4 !important;
}

.todo .task-control {
	background: #c5a3ff !important;
}
}

@-moz-document url("https://habitica.com/") {
.column-background {
	display: none !important;
}

.create-task-area {
	display: none !important;
}

.tasks-navigation {
    display: none !important;
}

.user-tasks-page {
	margin: 1rem;
}

.tasks-column {
	padding: 0.5rem;
}
}
