css:
@-moz-document domain("turkerhub.com") {
body {background-color: #272727;}

/* Top Bar */
.Menu {border-top-color: #202020;}
.Menu .primaryContent {background: #272727;}
.Menu .secondaryContent {background: #313131;}
.primaryContent {background: #313131; border-bottom: 1px solid #555;}
.listItemText h3 {color: #fff;}
.secondaryContent.noItems {color: #d2d2d2;}
.Menu .sectionFooter {background-color: #272727; border-top: 1px solid #555;}
.primaryContent.menuHeader h3 {color: #fff;}
.blockLinksList a, .blockLinksList label {color: #d2d2d2;}
.alertsPopup>ol.secondaryContent {background-color: #313131 !important; border-bottom: 1px solid #555;}
.Alert.listItem.new.unviewed.PopupItemLink.PopupItemLinkActive {background-color: #313131 !important;}
.Alert.listItem.new.unviewed.PopupItemLink.PopupItemLinkActive:hover, .Alert.listItem.new.PopupItemLink.PopupItemLinkActive:hover {background-color: #555 !important; border-bottom: none;}
.navPopup .listItem {border-bottom: 1px solid #555;}
.textCtrl {background: #272727; border: 1px solid #555; color: #d2d2d2;}
.textCtrl:focus {background: #202020; border: 1px solid #555; color: #d2d2d2;}
.navTabs .navTab.selected .tabLinks {background: none;}

/* Forum Homepage */
.nodeList .node.level_1 {border-left: 1px solid #000; border-right: 1px solid #000;}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {background: #272727; border-bottom: 1px solid #333;}
.node .nodeLastPost {background: #272727;}
.container .xengallerySideBar .section .secondaryContent, .resourceListSidebar .secondaryContent, .sidebar .secondaryContent {border: 1px solid #000;}
/* Forum Homepag: Sidebar Categories */
.sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {background: #272727; border-bottom: 1px solid #555;}
/* Forum Homepage: Progress Bar */
.DonationGoal .ProgressMeter {background: #313131; border: 1px solid #555;}
.ProgressCounter {color: #000; font-weight: bold;}
/* Forum Homepage: New Posts */
.threadListItem>.messageInfo>.messageContent>.title {background: #272727;}
.threadListItem {border-top: 1px solid #555;}

/* Thread Page */
.discussionListItem .posterAvatar {background: #272727; border-right: 1px solid #555;}
.discussionListItem .stats {background: #272727; border-right: 1px solid #555; border-left: 1px solid #555;}
.discussionListItem {background: #272727; border-bottom: 1px solid #555;}
.discussionListItem>.listBlock.main:hover {background: #202020;}
.discussionListItem .stats dd, .discussionListItem .stats dt{color: #b3b3b3;}
.discussionList .sectionFooter {background: #202020; border: 1px solid #555;}
#DiscussionListOptionsHandle a {background: #272727; border-bottom: 1px solid #000; border-left: 1px solid #000; border-right: 1px solid #000;}
/* Thread Page: Individual Thread Page Numbers */
body .itemPageNav a:hover {background: none; background-color: #333; border: 1px solid #000; color: #d2d2d2;}
body .itemPageNav a {background: none; background-color: #202020; border: 1px solid #000; color: #d2d2d2;}

/* Main Thread Content */
#content .pageContent, .messageContent, .messageInfo.primaryContent {background: #3c3c3c; color: #b3b3b3;}

.messageList .message {border-top: 1px solid #555;}

/* New Message Indicator */
.message .newIndicator {background: #870000; border: 1px solid #9d0202;}

/* Avatar Section */
.messageUserBlock, .avatarHolder, .messageUserBlock div.avatarHolder, .userText, .extraUserInfo, .messageUserBlock .arrow {border: 1px solid #383838; background: #383838;}
.messageUserBlock .arrow span {border: none;}

/* Likes Box */
.dark_postrating.likesSummary.secondaryContent {background: #333; color: white;}
.message .likesSummary {border-color: #222;}
.dark_postrating_outputlist li strong, .dark_postrating_thread_rating strong {color: #ccc;}

.breadcrumb .crust a.crumb, .discussionList .sectionFooter, .discussionListItem .title a, .styliumSidebarBlockToggle .fa, .node .nodeLastPost .lastThreadTitle a, .node .nodeText .nodeTitle a, .sidebar .section .secondaryContent h3 a, #content .container .xengallerySideBar .section h3, #content .container .xengallerySideBar .section h3 a, .sidebar .section .secondaryContent h3, .resourceListSidebar > .secondaryContent > h3, .resourceListSidebar > .secondaryContent > h3 a, .navigationSideBar a:hover, .primaryContent a, .node .unread .nodeText .nodeTitle, .node .subForumList .unread .nodeTitle, .subForumsMenu .node .unread .nodeTitle, .navTabs .navTab.selected .SplitCtrl:before, #logo .fa, .profilePage .mast .section.infoBlock h3 {color: #999;}

/* Quick Reply Box */
html, redactor_box {color: #f4f4f4;}
.quickReply {border-top: 1px solid #555;}

/*.redactor_toolbar {background: #272727 !important; color: white;}
.redactor_btn_group {background: #d2d2d2;}
*/

/* Links */
a:link, a:visited {color: #e0e0e0;}
a:hover {color: #fff; text-decoration: underline;}

.footer a, .footer a:visited, #styliumAdvancedFooter h3, .discussionListItems .unread .title a, .discussionListItem .lastPostInfo .username, .node .nodeLastPost .lastThreadMeta a, .sidebar .avatarList .username, .navTabs .visitorTabs .navTab.selected .navLink, .messageUserBlock a.username, .attachment .attachmentInfo .filename a, .ctrlUnit.sectionLink dt a, .newsFeed > a, .linkGroup a, .chooserColumns li a:hover .title, .message .messageMeta .control, .breadcrumb .crust:hover a.crumb, .breadcrumb .crust:last-child a.crumb, .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus, body .xenOverlay.memberCard .userInfo h3 a, .xenOverlay.memberCard .userLinks a, .pollBlock .question .questionText, .PageNav a:focus, .node .subForumList li a {color: #e0e0e0;}

.title:hover, .message:hover .messageMeta:hover .control:hover {color: #fff; text-decoration: underline;}

.ugc a:link {color: #e0e0e0;}
.ugc a:visited {color: #e0e0e0;}
.ugc a:hover, .ugc a:focus {background: none; text-decoration: underline; color: #fff;}
.emCtrl:hover, .ugc a:hover {box-shadow: none;}

/* Block Quotes, Code */
.bbCodeQuote .attribution {background: #272727 !important; border-bottom: 1px solid #555;}
.attribution.type {background: #272727; color: #d2d2d2;}
.bbCodeQuote .quoteContainer {background: #313131; color: #d2d2d2;}
.bbCodeQuote, .bbCodeBlock {border: 1px solid #000;}
.bbCodeQuote .quoteContainer .quoteExpand {background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgb(0, 0, 0) 80%); color: #fff;}
.bbCodeBlock pre, .bbCodeBlock .code {background: #313131;}
.bbCodeBlock .type {background: #272727 !important; border-bottom: 1px solid #555; color: #d2d2d2;}


/* Spoilers */
.hasJs .bbCodeSpoilerText {background: #313131; color: #d2d2d2; border: 1px solid #000;}

/* Attached Files */
.attachedFiles {border: 1px solid #000;}
.attachedFiles .attachedFilesHeader {background: #272727 !important; color: #d2d2d2; border-bottom: 1px solid #555;}
.attachedFiles .attachmentList {background: #313131; color: #d2d2d2;}
.attachment .boxModelFixer {background: #313131; color: #ccc; border: 1px solid #000;}
.attachment .attachmentInfo dd, .attachment .attachmentInfo dt {color: #ccc;}
.lightBox .userTextUpper, .lightBox .userTextLower {background: #fff;}

/* Poll Box */
.sectionMain {background: #272727; color: #d2d2d2; border: 1px solid #000;}
.pollBlock .pollOption label:hover {background-color: #333;}

/* Message Notices */
.messageNotices li {background: #484848; color: #d2d2d2; border: 1px solid #000;}

/* Ratings Box Popup */
#styliumSetting .styliumTertiaryColor, .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label, .blockLinksList a:active, .blockLinksList a.selected, .blockLinksList label:active, .blockLinksList label.selected, a.callToAction, .button.primary, .Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen, .Popup .PopupControl:hover, .Popup.PopupContainerControl:hover, .navTabs .navTab.selected.PopupOpen .navLink, .navTabs .navTab.PopupClosed:hover, .sidebar .visitorPanel .secondaryContent, .navigationSideBar .heading, .heading, .xenForm .formHeader, .PageNav a.currentPage, .PageNav a:hover {background-color: #2d2d2d;}
.xenOverlay .section .subHeading, .xenOverlay .sectionMain .subHeading {background: #3c3c3c;}
.dark_postrating_detail, .dark_postrating_header strong {background: #3c3c3c; color: #fff;}
.sectionFooter {background-color: #454545;}
.dark_postrating_column {border: 1px solid #6c6c6c;}
.dark_postrating_header {border-bottom: 1px dotted #6c6c6c;}

/* Thread Watchers Box */
#uaThreadViewContainer h3, #uaThreadReadContainer h3, #uaThreadViewContainer h3 .footnote, #uaThreadReadContainer h3 .footnote {border-bottom: 1px solid #555; color: #d2d2d2;}
.secondaryContent {background: #272727; border: none;}

/* Breadcrumbs */
.breadBoxTop {background: #272727; border-bottom: 1px solid #555; margin-top: 20px;}
.breadBoxBottom {background: #272727; border-top: 1px solid #555; margin-bottom: 20px;}
.breadcrumb {background: #272727;}

/* Page Footer */
#styliumSetting .styliumSecondaryColor, #styliumAdvancedFooter, .footer, #logoBlock, #QuickSearch.active, .chooserColumns .icon {background: #272727;}
#styliumSetting .styliumPrimaryColor, .discussionList .sectionHeaders, .footerLegal, .nodeList .categoryStrip, #header, #navigation .pageContent, .button.smallButton, .button, .subHeading, .node .nodeIcon, .PageNav a, .xmgCarouselContainer .sectionMain .titleStrip {background: #171717;}

/* Arrowchat - CURRENTLY DISABLED, WORK IN PROGRESS 
#arrowchat_base {background: none; background-color: #2d2d2d; border: 0;}

.arrowchat_chatrooms_title {background: #272727;}
.arrowchat_chatrooms_title.arrowchat_chatboxtabtitlemouseover3 {background: #272727;}
#arrowchat_chatroom_chat {background: #3c3c3c; color: #d2d2d2; border-right: 1px solid #555;}
.arrowchat_chatroom_message_content {background: #272727;}
#arrowchat_tabpopup {box-shadow: none;}
.arrowchat_chatroom_message_area {background: #484848; color: #d2d2d2;}
#arrowchat_chatroom_room_list {background: #272727;}
.arrowchat_chatroom_room_list {border-top: 1px solid #555; border-bottom: 0;}
.arrowchat_group_text {background: #2d2d2d; color: #fff;}
.arrowchat_chatroom_room_name {color: #d2d2d2;}
*/
}
