css:
@-moz-document domain('ddmix.net'), url-prefix('http://ddmix.net') {

		html {
    background: #333;
    color: #1785dd;
}

#main-nav {
    background: -webkit-linear-gradient(#0055cc, #333);
}

#main-nav .menu>li>a {
    float: left;
    position: relative;
    color: #888;
    padding: 0px 11.6px 0px 11.6px;
    line-height: 40px;
    font-family: "Open Sans",sans-serif;
    letter-spacing: -0.006em;
}

input, textarea, select, button, .btn {
    display: inline-block;
    padding: 4px;
    margin: 0;
    outline: none;
    background-color: #333;
    border: 1px solid #555;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    outline: none;
}

.login-submit .button-primary, button, .btn {
    cursor: pointer;
    width: auto;
    text-align: center;
    font-weight: bold;
    padding: 4px 12px;
    background-color: #F5F5F5;
    background-image: -khtml-gradient(linear,left top,left bottom,from(#F9F9F9),to(#F0F0F0));
    background-image: -moz-linear-gradient(#F9F9F9,#F0F0F0);
    background-image: -ms-linear-gradient(#F9F9F9,#F0F0F0);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#F9F9F9),color-stop(100%,#E5E5E5));
    background-image: -webkit-linear-gradient(#F9F9F9,#F0F0F0);
    background-image: -o-linear-gradient(#F9F9F9,#F0F0F0);
    background-image: linear-gradient(#333,#555);
    -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,0.25);
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.25);
}

.widget-title {
    border-bottom: 2px solid #1785dd;
    margin: 0;
    font-size: 15px;
    font-family: "Open Sans",sans-serif;
    font-weight: bold;
    letter-spacing: -0.025em;
}

.widget-header {
    border-bottom: 1px solid rgb(60, 90, 113);
    text-transform: uppercase;
    margin: 0 0 8px;
}

.widget-comments li {
    background: url(http://ddmix.net/images/blt-comment.png) no-repeat 0 11px;
    padding: 5px 0 5px 16px;
    overflow: hidden;
}

.swa-activity-list .swa-activity-content {
    border-bottom: 1px solid #1c97f9;
    margin-bottom: 3px;
    padding-top: 2px;
    margin-top: 3px;
}


.widget li {
    padding: 4px 0 19px 12px;
    border-bottom: 1px solid #1c97f9;
    position: relative;
    top: -1px;
}

.widget-comments .excerpt {
    display: block;
    color: #888;
    margin: 0;
}

.loop-nav {
    border-top: 2px solid #0055cc;
}

.loop-nav-inner {
    border-top: 1px solid #333;
    padding: 20px 0;
    margin-bottom: -20px;
}

.pag-nav a, .pag-nav span {
    color: #1785dd;
    margin: 0 2px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    display: inline-block;
    padding: 6px 10px;
    background-color: #888;
    background-image: -ms-linear-gradient(top,#EEE,#E7E7E7);
    background-image: -moz-linear-gradient(top,#EEE,#E7E7E7);
    background-image: -o-linear-gradient(top,#EEE,#E7E7E7);
    background-image: -webkit-gradient(linear,left top,left bottom,from(#EEE),to(#E7E7E7));
    background-image: -webkit-linear-gradient(top,#0055cc,#333);
    background-image: linear-gradient(top,#EEE,#E7E7E7);
