css:
@-moz-document domain(blackhatworld.com) {

.discussionListItems .unread .title a {
    color: #cecece;

}

a:link, a:visited {
    color: #ababab;
    text-decoration: none;
}
.sectionMain {
    background-color: #000000;

}
.mainContent {
    background-color: #6e798a;
    border-radius: 25px;
}

.discussionList .main {
    background-color: #393b45;
}

.discussionListItem {
    background-color: #d8d9de;

}
.discussionList .stats {
    background-color: #584848;
}

#content .pageContent {
    background-color: #393b45;
    margin: 30px;
}

.mainContent {
    margin-left: 250px;
    margin-right: 20px;
    padding-bottom: 5px;
}
.section {
    margin: 10px -50px 10px 10px;
}
.secondaryContent {
    padding: 30px;
}
div.discussionList.section.sectionMain {
    margin-right: 10px;
}

.navTabs {
    margin-left: 30px;
    margin-right: 30px;
}

.formPopup .textCtrl,
.formPopup .button {
    width: 220px;

}

input#QuickSearchQuery.textCtrl {
    padding: 6px;
}

.discussionListItem .lastPostInfo .username {
    color: #393b45;
}
.discussionListItem .lastPostInfo .dateTime {
    color: #393b45;
}

.titleBar {
    margin-bottom: 5px;
    padding-top: 5px;
    margin-left: 33%;

}
#content > div > div > div.mainContainer > div > h2 {
    display: none;
}
.messageUserBlock h3.userText {
    margin-left:20px
}

.messageList {
    margin-right: 62px;
}

.pageNavLinkGroup {
    margin: 10px 10px;
}
.message .messageMeta {
    margin-left: -10px!important;
    margin-right: -10px!important;
}

.messageText {
    color: #efefef;
}

.messageList .message {
    background-color: #25262d;
}

.messageInfo.primaryContent {
    background-color: #393b45;
}
.messageUserBlock .userBanner {
    margin-top: 10px;
}

userBanner.bannerBlue {
    background-color: #308df5;
}
.username .style17 {
    color: #3697ff;
}

a:link, a:visited {
    color: #efefef;
    text-decoration: none;
}
}
