css:
@-moz-document domain("moodle.insa-toulouse.fr") {
.navbar-inner {
	background-color: #4F4D50;
	background-image: none;
}
.navbar .brand,
.usermenu .login,
.usermenu .moodle-actionmenu .toggle-display .userbutton,
.navbar .langmenu .dropdown-toggle {
	color: #fff;
}
.navbar .langmenu .dropdown-toggle:hover,
.menubar:hover,
.popover-region:hover {
	color: #fff;
	background-color: #E42719;
}
.dropdown-menu,
.moodle-actionmenu[data-enhanced].show .menu {
	background-color: #E4DDE4;
}
.dropdown-menu,
.menu-action-text .moodle-actionmenu[data-enhanced].show .menu:hover {
	color: #000;
}
.navbar .usermenu span a {
	color: #000;
}
.block,
#region-main,
.navbar,
.userpicture,
.breadcrumb {
	box-shadow: 2px 2px 4px #cfcfcf;
}
#page-site-index {
	background-color: #E9EAEB;
}
h2 {
	color: #E42719;
	text-shadow: 1px 1px 3px #888;
}
.block .header h2 {
	color: #fff;
	text-shadow: 1px 1px 2px #888;
}
.block .header .title {
	background-color: #E42719;
	box-shadow: 2px 2px 4px #cfcfcf;
	border-radius: 2px;
}
.block {
	padding-top: 0px;
}

.breadcrumb, .block {
	background-color: #fff;
}

.block .content {
	padding-top: 16px;
}

h1 {
	color: #E42719;
	text-shadow: 2px 2px 4px #888;
}
a {
	color: #03437f;
}
a:hover, a:focus {
	text-decoration: none;
	color: #c61600;
}
body {
	color: #212121;
	background-color: #E9EAEB;
	font-family: Arial,Helvetica,sans-serif;
}
}
