@-moz-document domain("virtualpopstar.com") {
/* DO NOT TOUCH */

.header_container {
    background-color: transparent;
    background-position: center top;
    background-repeat: repeat-x;
    height: 159px;
}

.bar_top {
    background-image: none;
    background-repeat: repeat-x;
    height: 16px;
}

.header_bg_overlay {
    background-repeat: no-repeat;
    height: 159px;
    position: absolute;
    width: 100%;
    z-index: 0;
    background-position: center top;
    visibility: hidden;
}

.header_bg {
    background-image: url(http://i.imgur.com/fmGttcP.png);
    height: 159px;
    background-repeat: no-repeat;
    width: 960px;
    margin: auto;
}
}

@-moz-document domain("virtualpopstar.com") {
/* THIS REMOVES THE BLOOD IN THE HEADER */

HTML {
    background-image: url(http://en.virtualpopstar.com/layout/nl/images/bg.jpg?v=55729);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center center;
}

/* THIS REMOVES THE BLOOD ON YOUR PROFILE */

.profile-avatar-bg {
    background-image: url(https://i.imgur.com/jAkfNft.jpg);
}

/* THIS REMOVES THE BLOOD ON THE BFFS REQUEST PAGE */

.bffinvites-bff {
    background-image: url(https://www.transparenttextures.com/patterns/asfalt-light.png);
}

/* THIS REMOVES THE BLOOD ON THE MANAGER PAGE AND AFTER YOU PERFORM */

img[src*="/layout/nl/images/manager-bg.jpg"]{ content: url("https://i.imgur.com/mE9kaGK.jpg"); }
img[src*="/layout/nl/images/stage_live_shade.jpg"]{ content: url("https://i.imgur.com/ipK0Wsx.jpg"); }

/* THIS REMOVES THE BLOOD ON THE AVATAR ON THE HOME PAGE */

.block_mid {
    background-position: -9999px -9999px;
    background-repeat: no-repeat;
}

/* THIS REMOVES THE BLOOD FROM YOUR WARDROBE */
    
.darkbox_full_custom {
    background-image:url(http:virtualpopstar.com/layout/nl/images/avatarmanagement.jpg);
 background-repeat: no-repeat;
background-position: -9999px -9999px;}
}
