css:
@-moz-document domain("tweetdeck.twitter.com") {.compose-text-container {
        border-radius: 0;
    background-color: #14171a!important;

}
.compose-text-container textarea {
        color:#e1e8ed!important;
}
.compose {
    background-color: #222426!important;
    z-index:100!important;
}
.compose-text {
    background: none;
    color: white;
}
.column-message,
.js-column-drag-handle,
.margin-v--14,
.compose-header,
.compose-remember-state,
.column-number,
.js-compose-message-header,
.lst-modal {
    display: none!important;
}

.is-wide-columns .app-columns {
    padding-left: 0;
}
.is-wide-columns .column {
    margin-right: 1px;
}

.compose {
    background-color: #222426;
}
.tweet-button {
    width: 40px;
}
.btn-on-blue {
    background-color: #292f33;
}

.btn-positive, .btn-positive-alt, .btn-fav.s-favorited, .s-following .follow-btn {
    background-color: #292f33;
    border: none;
    box-shadow: none;
    border-radius: 0;
}
.is-wide-columns .column {
    width: 33%;
}

.tweet-button {
    width: 40px!important;
    padding: 0!important;
    text-align: center!important;
}

/* Mastodon */
.column {
    background-color: #282c37!important;
}
.compose {
    background-color: #282c37!important;
     background-color: #313543!important; /* bright */
}
.column-header {
    background:#313543!important;
    border:none!important;
}

.compose-text-container {
        background:#1f222b!important;
    border-radius:0!important;
}

.stream-item {
    background-color: #282c37!important;
    border-bottom: 1px solid #393f4f!important;
}
.btn-on-blue, .btn-positive {
    background-color: #707b97!important;
    border-radius: 0!important;
    border:none!important;
}
.app-header {
    background-color: #313543!important;
}
.column-nav-item,
.app-navigator,
.app-search-fake{
        background-color: #313543!important;
    border-radius:0!important;
    box-shadow:none!important;
    color:#8899A6!important;
}
.column-nav-item i,
.app-navigator i,
.app-search-fake i {
        color:#8899A6!important;
}
 .column {
    width: calc(33.33% - 6px)!important;
}
}
