css:
@-moz-document domain("myth-weavers.com") {

.page {
    border: 1px solid #222;
}

.page, .page td {
    font-size: 9px;
    color: #08c;
    background-color: #272727;
    font-family: Tahoma, Arial, sans-serif;
}

.page input, .page textarea, .page select {
    color: #dcb681;
}

.page #stats td.header, .page #stats td.footer, .page #stats td.title {
    color: #08c;
    background-color: rgb(70, 70, 70);
}

.page #attacks td.header, .page #attacks td.footer {
    color: #08c;
    background-color: rgb(70, 70, 70);
}

input {
    background-color: #272727;
}

.page input.unsaved, .page textarea.unsaved, .page td.unsaved, .page th.unsaved {
    background-color: #555 !important;
}

.page #feats input {
    border-top: 1px solid #004061;
}

.page #gear input {
    border-top: 1px solid #004061;
}

.page #skills tr.skillslot input.text {
    border-top: 1px solid #004061;
}

.page #skills a {
    color: #dcb681;
    background-color: #474747;
}

.page #gear tr.title td {
    color: #0089ad;
}

.page #skills tr.title td {
    color: #0089ad;
}

.page #gear tr.footer td {
    color: #dcb681;
    background-color: #222;
}

.page #languages td input {
    border-top: 1px solid #004062;
}

.page #currency tr.title td {
    color: #0089ad;
}

.page #languages tr.title td {
    color: #0089ad;
}

.page #skills tr.footer td {
    color: #dcb675;
    background-color: #222222;
}

.page #gear a {
    color: #d1b681;
    background-color: #565656;
}

.page #spells tr.title td {
    color: #0089ad;
}

.page #spellsaves tr.title td {
    color: #0089ad;
}

.page #spellsaves tr.header td {
    background-color: black;
    color: #dcb681;
}

.page #spellsaves td.level, .page #spellsaves td.noinput {
    background-color: #555;
    color: #dcb681;
}

.page #spells table.spelllist tr.header td {
    background-color: #000;
}

.page #spells a {
    color: #dcb681;
    background-color: black;
}

.page #spells table.spellsknown input {
    border-top: 1px solid #004062;
}

.page #stats td.tag {
    color: #0089ad;
}

.page #attacks td.tag {
    color: #0089ad;
}

.page table.weapon tr.header td, .page table.armor tr.header td {
    color: #0089ad;
}

.page #feats tr.title td {
    color: #0089ad;
}

input[disabled], select[disabled], textarea[disabled], input[readonly], select[readonly], textarea[readonly] {
    background-color: #333;
}

}
