css:
@-moz-document domain("ukbouldering.com") {
  * {
  box-sizing: border-box;
}

body {
  border-top: 8px solid #001b44;
  font-size: 1rem;
  background: #f5f5f5;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica,
    Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol';
}

h3.catbg,
h3.catbg2,
h3.titlebg,
h4.titlebg,
h4.catbg {
  text-shadow: none;
}

h3.catbg a:link,
h3.catbg a:visited,
h4.catbg a:link,
h4.catbg a:visited,
h3.catbg,
.table_list tbody.header td,
.table_list tbody.header td a {
  color: #131313;
}

h3.catbg a:hover,
h4.catbg a:hover,
.table_list tbody.header td a:hover {
  color: #131313;
}

td {
  display: block;
}

#main_content_section > table > tbody > tr > td:first-child {
  display: none;
}

div.cat_bar {
  border: 1px solid #dedede;
  padding: 12px 16px;
  height: auto;
}

#top_section {
  position: relative;
}

#top_section:after {
  position: absolute;
  display: block;
  content: 'ukbouldering.com';
  font-weight: bold;
  left: 50%;
  transform: translateX(-50%);
  font-size: 2.25rem;
}

.forumtitle {
  display: none;
}

table {
  border-spacing: 0;
}

div.title_bar:before,
div.title_bar:after,
div.cat_bar:before,
div.cat_bar:after,
#header:before,
#header:after {
  box-shadow: none;
}

.sp_article_icon,
.generic_icons {
  display: none;
}

.dropmenu li,
.buttonlist ul li {
  margin: 0;
}

.buttonlist {
  padding: 0;
  margin-top: 24px;
}

.buttonlist ul {
  display: flex;
  padding: 0;
  background: white;
}

.buttonlist ul li {
  float: none;
}

.dropmenu li a,
.buttonlist ul li a {
  background: none;
  background-image: none;
  border: 0;
  border-radius: 0;
  box-shadow: none;
  text-transform: none;
  padding: 6px 18px;
  border-right: 1px solid #ebebeb;
  display: inline-block;
  font-size: 0.9rem;
}

.dropmenu li a.active,
.buttonlist ul li a.active {
  background: #001b44;
  font-weight: 500;
  border-radius: 0;
  border: 0;
  border-right: 1px solid #ebebeb;
}

.buttonlist ul li a:hover,
.buttonlist ul li:hover a,
.buttonlist ul li a:focus,
.dropmenu li a:hover,
.dropmenu li:hover a,
.dropmenu li a:focus {
  transition: all 0.1s ease-in;
  background: #001b44;
  color: white;
  border-radius: 0;
  box-shadow: none;
  border: 0;
  border-right: 1px solid #ebebeb;
}

.poster {
  border-radius: 0;
  box-shadow: none;
}

#main_menu ul {
  right: 0;
  position: relative;
  float: none;
}

.dropmenu li {
  left: 0;
}

.breadCrumb {
  float: none;
  padding: 18px;
  height: auto;
  width: 100%;
}

#footer_section {
  display: none;
}

.poster h4,
.poster h4 a {
  background: none;
  background-image: none;
  border: none;
  border-radius: 0;
  font-size: 0.8rem;
  box-shadow: none;
}

.poster li.avatar,
.poster li.avatar1 {
  border: 0;
}

.generic_icons {
  display: none !important;
}

.breadCrumb ul li:first-child {
  display: none;
}

.breadCrumb ul {
  height: auto;
}

#header {
  height: auto;
  border: solid 1px #dedede;
}

#main_menu ul {
  float: none;
}

#sp_main {
  border-spacing: 0;
}

@media (min-width: 768px) {
  #sp_main > tbody > tr {
    display: flex;
  }

  #sp_center {
    margin-right: 50px;
  }

  #sp_right {
    width: 400px !important;
    flex-grow: 0;
  }
}

}
