css:
@-moz-document domain("wenzhang.baidu.com") {
.date,
.desc,
.ok-ico,
.pic {
	display: none
}
.article-h4,
.unit-list .unit .content h4,
.unit-list h4 {
	font-size: 14px;
	word-break: keep-all;
	white-space: nowrap;
	overflow: hidden;
	-o-text-overflow: ellipsis;
	text-overflow: ellipsis
}
.unit-list .unit {
	height: 75px;
	width: 180px;
	margin-top: 15px;
}
.unit-list .unit .content h4,
.unit-list h4 {
	line-height: 20px
}
.article .mask {
	background-color: #032030!important;
	height: 87px!important;
	width: 193px!important
}
.unit-list .page-url a {
	display: block!important;
	color: #11CD86!important
}
.page-url {
	color: #000
}
.edit-tag-panel .my-tag-tips,
.edit-unit-panel .my-tag-tips {
	max-height: 90pt
}
.unit-list .unit .mask .tags {
	bottom: 18px;
	height: 30px;
}
.__unitList {
	width: 1820px !important
}
}
