css:
@-moz-document domain("resetera.com") {
    /* latin-ext */

    
    
    .MultiQuoteItem .messageGradient {
        position: absolute;
        top: 0px;
        left: 0px;
        right: 0px;
        height: 120px;
        background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
        background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
        background: -o-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
        background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
    }
    .redactor_toolbar li.redactor_btn_group ul {
        background-color: rgb(56, 56, 56);
        padding: 1px;
        margin: 0;
        overflow: hidden;
        *zoom: 1;
        box-shadow: none;
    }
    .redactor_box {
        font-family: "Fira Sans", sans-serif;
        border: 1px solid rgb(99, 99, 99);
        line-height: 0;
        position: relative;
        overflow: hidden;
    }
    .bbCodeBlock pre,
    .bbCodeBlock .code {
        background-color: rgb(44, 44, 44);
    }
    
    .discussionListItems .unread .title a:hover {
        font-weight: bold;
        color: #bc99d7;
    }
    .Responsive .redactor_toolbar li a {
        text-indent: 0px;
        text-align: center;
        background-image: none !important;
        background: #848484;
        /* color: white; */
    }
    .redactor_toolbar {
        font-size: 0;
        background-color: rgb(56, 56, 56);
        padding: 0 0 0 2px;
        margin: 0;
        border-bottom: 1px solid rgb(34, 34, 34);
        position: relative;
        left: 0;
        top: 0;
        line-height: 0;
        list-style: none;
        overflow: hidden;
    }
    .bbCodeBlock .type {
        font-size: 11px;
        font-family: 'Fira Sans';
        color: rgb(205, 205, 205);
        background-color: rgb(90, 74, 102);
        padding: 5px 8px;
    }
    .pollBlock .pollOption label:hover {
        background: rgb(122, 60, 170);
        color: rgb(255, 255, 255);
        border-radius: 2px;
    }
    .pollBlock .questionMark {
        background: rgb(122, 60, 170);
    }
    .pollBlock .question .questionText {
        float: left;
        font-size: 16pt;
        color: rgb(213, 209, 215);
    }
    .textCtrl {
        font-size: 13px;
        font-family: "Fira Sans", 'Trebuchet MS', Verdana, Geneva, Arial, Helvetica, sans-serif;
        color: #e7e7e7;
        background-color: rgb(93, 93, 93);
        padding: 6px 5px;
        margin-bottom: 2px;
        border: 1px solid rgb(204, 204, 204);
        outline: 0;
    }
    .ugc a:link,
    .ugc a:visited {
        color: rgb(189, 137, 255);
        padding: 3px;
        transition: all 0.4s ease-in-out;
    }
    .tabs {
        border-bottom: 1px solid #8E50BE
    }
    .username.author {
        display: none;
    }
    body .itemPageNav a {
        color: rgb(255, 255, 255);
    }
    html {
        color: white;
    }
    .profilePage .tabs.mainTabs li.active a {
        background-color: #8e50be;
    }
    .profilePage .tabs.mainTabs {
        background: rgb(51, 51, 51);
    }
    .profilePage .profileContent {
        background: rgb(51, 51, 51);
        padding: 10px 20px;
        color: #c8c8c8;
    }
    .tabs li a,
    .tabs.noLinks li {
        background-color: rgb(78, 80, 83);
    }
    .profilePage .mast,
    .profilePage .mainProfileColumn,
    .profilePage .primaryUserBlock {
        border: none;
    }
    .button.primary {
        color: white;
        background-color: rgb(122, 60, 170);
        border: 1px solid #8E50BE
    }
    #QuickSearch {
        background-color: #5d5d5d;
    }
    .formPopup .controlsWrapper {
        background: #5d5d5d;
        margin: 5px 0;
        font-size: 11px;
        border: 3px solid #5d5d5d;
    }
    body {
        background-color: #222;
        color: white;
    }
    #header {
        background-color: #222;
    }
    #content .pageContent {
        color: rgb(152, 152, 152);
        background-color: #222;
    }
    .threadListSeparator, threadListSeparator sticky {
        padding: 10px 10px;
        line-height: 6px;
        height: 6px;
        background-color: rgb(98, 70, 119);
        border-color: #3a2947;
        border-top-style: none;
        border-left-style: none;
        border-right-style: none;
        color: #bcbcbc;
    }
    body .itemPageNav a {
        color: rgb(255, 255, 255);
    }
    body .itemPageNav a:hover, .pageNav a:hover {
        color: #8E50BE;
        text-decoration: none;
        background-color: rgb(255, 255, 255);
        border-color: rgb(255, 255, 255);
    }
    .messageUserBlock a.username {
        color: rgb(255, 255, 255);
    }
    .discussionListItems .unread .title a {
        font-weight: bold;
        color: white;
    }
    .messageList .message {
        background-color: #3b3b3b;
        color: white;
        border: none;
    }
    .titlebar h1 {
        color: rgb(212, 212, 212);
    }
    .messageUserInfo {
        background-color: rgb(59, 59, 59);
        float: left;
        width: 140px;
    }
    .messageText {
        font-size: 10pt;
        font-family: "Fira Sans";
        color: rgb(233, 233, 233);
        line-height: 1.4;
        margin-bottom: 30px;
    }
    .message .messageMeta .control {
        color: rgb(153, 153, 153);
        padding: 5px;
        background: #0000;
    }
    .message .messageMeta .control:hover {
        color: rgb(255, 255, 255);
    }
    .messageList li.message:nth-child(2n),
    .messageList li.message:nth-child(2n) .messageUserInfo {
        background-color: rgb(78, 78, 78);
    }
    
    .bbCodeQuote .quoteContainer {
        background-color: rgb(38, 38, 38);
    }
    .bbCodeQuote .attribution {
        color: rgb(186, 186, 186);
        background-color: rgb(63, 63, 63);
        border: 0 none #000;
    }
    .bbCodeQuote {
        background-color: #e3e2e2;
        border: 1px groove #595757;
        overflow: auto;
    }
    .quickReply {
        background-color: rgb(56, 56, 56);
        padding: 10px;
        border: 1px solid rgb(99, 98, 98);
    }
    .bbCodeQuote .quoteContainer .quoteExpand {
        background-color: #000;
    }
    .bbCodeQuote .quoteContainer .quoteExpand {
        color: rgb(201, 201, 201);
        background: -webkit-linear-gradient(top, rgba(40, 40, 40, 0) 0%, rgb(90, 90, 90) 80%);
        background: -moz-linear-gradient(top, rgba(40, 40, 40, 0) 0%, rgb(90, 90, 90) 80%);
        background: -o-linear-gradient(top, rgba(40, 40, 40, 0) 0%, rgb(90, 90, 90) 80%);
        background: linear-gradient(to bottom, rgba(41, 41, 41, 0) 0%, rgb(90, 90, 90) 80%);
    }
    .PageNav a {
        color: rgb(255, 255, 255)!important;
        background-color: rgb(78, 42, 111);
        border: 1px solid rgb(88, 52, 121);
        border-radius: 3px;
    }
    .PageNav a.currentPage {
        color: rgb(52, 39, 68);
        background-color: rgb(134, 90, 137);
        border-color: rgb(144, 100, 147);
        position: relative;
    }
    .titleBar h1 {
        font-weight: bold;
        font-size: 14pt;
        color: rgb(228, 228, 228);
        padding-bottom: 12px;
        overflow: hidden;
        zoom: 1;
    }
    .navTabs {
        background-color: #292929;
    }
    .navTabs .navTab.selected .navLink {
        color: #8E50BE
    }
    .navTabs:hover .navTab.selected:hover .navLink:hover {
        color: #8E50BE
    }
    a[href]:hover {
        color: #8E50BE;
        text-decoration: none;
    }
    .breadcrumb {
        background-color: #8E50BE
    }
    .nodeList .categoryForumNodeInfo,
    .nodeList .forumNodeInfo,
    .nodeList .pageNodeInfo,
    .nodeList .linkNodeInfo {
        background-color: rgb(51, 51, 51);
        border-right: 1px solid #292929;
        border-bottom: 1px solid #292929;
        border-left: 1px solid #292929;
        padding: 0;
    }
    .sidebar {
        float: right;
        font-size: 11px;
        background-color: #333;
        width: 100%;
    }
    .nodeList > li:last-child {
        border-bottom: 1px solid #2F2D2D;
    }
    .nodeList > li:first-child {
        border-top: 1px solid #292929;
    }
    .secondaryContent {
        color: rgb(207, 207, 207);
        background-color: rgb(51, 51, 51);
        padding: 10px;
    }
    .nodeList .categoryStrip,
    .subHeading,
    .discussionList .sectionHeaders,
    .sidebar .section .primaryContent h3,
    .sidebar .section .secondaryContent h3,
    .profilePage .mast .section.infoBlock h3,
    .footer .pageContent {
        background-color: #8E50BE
    }
    .node .nodeLastPost {
        color: rgb(119, 119, 119);
        background-color: rgb(51, 51, 51);
        border: 1px solid rgb(51, 51, 51);
        border-radius: 3px;
    }
    .discussionListItem .posterAvatar,
    .discussionListItem .stats {
        background-color: rgb(51, 51, 51);
    }
    .discussionListItem .stats dl {
        padding: 0 0px;
        border-left: 0px solid #e4e4e4;
        border-right: 0px solid #e4e4e4;
    }
    .discussionListItem,
    .primaryContent {
        background-color: rgb(51, 51, 51);
        border-right: 0px solid #3c3c3c;
        border-bottom: 1px solid #3c3c3c;
        border-left: 1px solid #3c3c3c;
    }
    body .itemPageNav a {
        color: rgb(44, 41, 41);
        background-color: rgb(164, 65, 255);
        padding-bottom: 2px;
        border: 1px solid rgb(164, 65, 255);
        border-radius: 3px;
    }
    a.callToAction span {
        font-weight: bold;
        font-size: 11px;
        font-family: 'Fira Sans', sans-serif;
        color: rgb(255, 255, 255);
        background-color: rgb(86, 60, 107);
        padding: 2px 15px;
        border-width: 1px;
        border-style: none;
        border-radius: 3px;
        display: block;
    }
    .sectionFooter {
        overflow: hidden;
        zoom: 1;
        font-size: 11px;
        color: rgb(255, 255, 255);
        background-color: rgb(33, 33, 33);
        background-repeat: repeat-x;
        background-position: top;
        padding: 4px 10px;
        line-height: 16px;
    }
    .heading .ToggleTrigger {
        padding: 10px;
    }
    .heading,
    .xenForm .formHeader {
        font-weight: bold;
        font-size: 11pt;
        color: rgb(255, 252, 252);
        background-color: rgb(103, 103, 103);
        padding-right: 10px;
        padding-left: 10px;
        padding-top: 10px;
        padding-bottom: 10px;
    }
    .textHeading,
    .xenForm .sectionHeader {
        font-weight: bold;
        color: rgb(224, 224, 224);
        padding-bottom: 2px;
        margin: 10px auto 5px;
        border-bottom: 1px solid rgb(39, 39, 39);
    }
    .navTabs .navTab.PopupClosed:hover {
        background-color: rgb(81, 81, 81);
    }
    .navTabs .navTab.PopupClosed:hover {
        background-color: rgb(74, 74, 74);
        color: white;
    }
    .navTabs .navTab.PopupClosed .navLink:hover {
        color: rgb(208, 147, 255);
    }
    .Menu .primaryContent,
    .Menu .secondaryContent {
        background-color: #3c3c3c;
        color: white;
    }
    .blockLinksList a {
        color: rgb(245, 245, 245);
    }
    html {
        font-family: 'Fira Sans';
        background-color: rgb(34, 34, 34);
        -webkit-font-smoothing: antialiased;
        text-rendering: optimizeLegibility;
        overflow-y: scroll !important;
    }
    .Menu {
        background-color: rgb(114, 114, 114);
        font-size: 11px;
        border: 1px solid #3c3c3c;
        border-top: 10px solid #3c3c3c;
        overflow: hidden;
        box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.5);
        min-width: 200px;
        *width: 200px;
        position: absolute;
        z-index: 7500;
        display: none;
        border-radius: 3px;
        border-right: 5px solid #3c3c3c;
    }
    .hasJs .bbCodeSpoilerText {
        display: none;
        color: white;
        background-color: black;
        padding: 2px 2px 2px 0.5em;
        margin-top: 5px;
        margin-right: 140px;
        margin-bottom: 5px;
        border: black;
        overflow: auto;
    }
    .Menu .sectionFooter {
        background-color: rgb(60, 60, 60);
    }
    .Menu .sectionFooter a {
        color: #eee;
    }
    .secondaryContent a {
        color: rgb(179, 179, 179);
    }
    .navPopup .PopupItemLinkActive:hover {
        margin: 0 -8px;
        padding: 5px 8px;
        border-radius: 3px;
        background-color: rgb(60, 44, 69);
        cursor: pointer;
    }
    .navPopup .listItem {
        overflow: hidden;
        zoom: 1;
        padding: 5px 0;
        border-bottom: 1px solid rgb(71, 71, 71);
    }
    .LoggedIn .discussionListItem .unreadLink,
    .LoggedIn .discussionListItem .ReadToggle {
        color: rgb(127, 81, 186)!important;
    }
    .primaryContent a {
        color: rgb(255, 255, 255);
    }
    .button {
        color: rgb(255, 255, 255);
        background-color: rgb(135, 135, 135);
        border: 1px solid rgb(99, 99, 99);
        border-radius: 3px;
    }
    .button .primary {
        color: rgb(255, 255, 255);
        background-color: rgb(135, 135, 135);
        border: 1px solid rgb(99, 99, 99);
        border-radius: 3px;
    }
    .button:hover {
        color: rgb(255, 255, 255);
        color: rgb(239, 232, 248);
        border: 1px solid rgb(122, 60, 170);
        border-radius: 3px;
        background: #965dc2;
    }
    .messageList .newMessagesNotice {
        margin: 10px auto;
        padding: 5px 10px;
        border-radius: 5px;
        border: 1px solid rgb(113, 48, 120);
        font-size: 11px;
        color: #edbdf5;
    }
    .bbCodeQuote {
        border-style: groove;
    }
    .messageText {
        font-size: 11.5pt;
        letter-spacing: .26px;
        color: rgb(233, 233, 233);
        line-height: 1.30em;
        margin-bottom: 30px;
    }
    .redactor_toolbar li.redactor_btn_group ul {
        background-color: rgb(56, 56, 56);
        padding: 1px;
        margin: 0;
        overflow: hidden;
        *zoom: 1;
        box-shadow: none;
    }
    .redactor_box {
        border: 1px solid rgb(99, 99, 99);
        line-height: 0;
        position: relative;
        overflow: hidden;
    }
    .bbCodeBlock pre,
    .bbCodeBlock .code {
        background-color: rgb(44, 44, 44);
    }
    .Responsive .redactor_toolbar li a {
        text-indent: 0px;
        text-align: center;
        background-image: none !important;
        background: #848484;
    }
    .redactor_toolbar {
        font-size: 0;
        background-color: rgb(56, 56, 56);
        padding: 0 0 0 2px;
        margin: 0;
        border-bottom: 1px solid rgb(34, 34, 34);
        position: relative;
        left: 0;
        top: 0;
        line-height: 0;
        list-style: none;
        overflow: hidden;
    }
    .formPopup {
        background-color: #5d5d5d;
    }
    
    /*Quote attritbutoon*/
      .bbCodeQuote .attribution[style="background-color: rgb(223, 211, 237);"] {
        background-color: #74558c  !important;
          color:white;
      }
    
      .bbCodeQuote .quoteContainer {
        padding: 10px;
      }
    
      .bbCodeQuote .quoteContainer[style="background-color: rgb(223, 211, 237);"] {
        background-color: #74558c  !important;
          color:white;
      }
    .alerts .alertText {
        margin-left: 32px;
        padding: 0 5px;
        color: grey;
    }
    a.username {
        color: rgb(225, 225, 225)!important;
    }
    .discussionListItem .itemPageNav {
        visibility: visible;
    }
    body .itemPageNav a {
        color: rgb(255, 255, 255);
        background-color: rgb(134, 90, 137);
        padding-bottom: 2px;
        border: 1px solid rgb(148, 104, 152);
        border-radius: 3px;
    }
    a:link, a:visited {
        color: rgb(215, 215, 215);
        text-decoration: none;
    }
    a[href]:hover {
        color: rgb(212, 141, 254);
        text-decoration: none;
    }
    .discussionListItems .unread .title a:hover {
        font-weight: bold;
        color: #bc99d7;
    }
    
    
    /* Alerts */
    /* Change the alert color and remove the inner border on the popup in the bottom left */
    
    #StackAlerts .stackAlertContent {
        background: rgb(34, 34, 34) !important;
        border: none 0px rgb(51, 51, 51) !important;
    }
    .navPopup .listPlaceholder ol.secondaryContent.Unread{
    background:#8E50BE;
    
    }
    
    /* Change the outer border color around the alert popup in the bottom left */
    .PageNav a {
        color: rgb(255, 255, 255) !important;
        background-color: #8E50BE;
        border: 1px solid #8E50BE;
        border-radius: 3px;
    }
    .messageList .message:target, .messageList .message:target .messageUserInfo {
        background-color: #8E50BE;
    }
    .PageNav a:hover {
        color: rgb(255, 255, 255) !important;
        background-color: #8E50BE;
        border: 1px solid white;
        border-radius: 3px;
    }
    
    
    #StackAlerts .stackAlert {
        border: solid 2px rgb(51, 51, 51) !important;
    }
    .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo{
    color:#e3e3e3;
    }
    .node .subForumList li .nodeTitle::before {
        content: "\f075";
        font-size: 13px;
        font-family: FontAwesome;
        color: rgb(126, 91, 153);
        margin-right: 5px;
    }
    }
