css:
@-moz-document /*domain([[instance]]),*/ domain(awoo.space), domain(mastodon.social)
{
.emojione:hover {
    transform: scale(2.5) !important;
    z-index: 100000 !important;
    background-color: #1f232b;
    padding: 3px;
    border-radius: 5px;
    position: absolute;
    transition: transform 0.3s ease-in-out !important;
    /* the 0.3s is the animation time for growing the emojo, it can be set to 0 */
}

.emojione {
    transition: transform 0.2s ease-in-out;
    position: relative;
    /* the 0.2s is the animation time for shrinking the emojo, it can be set to 0 */
}
}
