css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.resetera.com")
{
  html
  {
    background: #232323;
    color: #ccc;
  }

  .pageWidth
  {
    width: 75%;
  }

  .titleBar h1, p, span, dl, dt, label, .idstats dd, .idstats dt
  {
    color: #ddd;
  }

  a
  {
    color: #ddd;
  }

  a:hover
  {
    color: #eee;
  }

  a:link, a:visited, a[href]:hover
  {
    color: lightseagreen;
  }

  .PageNav a
  {
    background: lightseagreen;
    color: #fff;
    ;border: none;
  }

  .ugc a:link, .ugc a:visited
  {
    color: lightseagreen;
  }

  #header, .nodeList .categoryStrip, .breadcrumb
  {
    background: #2a2239;
  }

  .secondaryContent
  {
    background: #444;
  }

  .navTabs
  {
    background: #392f46;
  }

  .navTabs .navTab.selected .navLink
  {
    color: lightseagreen;
  }

  input.button.disabled
  {
    color: #333;
  }

  #content .pageContent
  {
    background: none;
  }

  .discussionListItem
  {
    background: #2c2c2c;
    border: thin solid #3c3c3c;
    margin-top: 6px;
  }

  /*.discussionListItem:nth-child(even), .messageList li.message:nth-child(even), .messageList li.message:nth-child(even) .messageUserInfo
  {
    background: #3d3b40;
  }*/

  .discussionListItem a
  {
    color: #ccc;
  }

  .discussionListItems .unread .title a
  {
    font-weight: normal;
  }

  .discussionListItem .stats dl
  {
    border: none;
  }

  .LoggedIn .discussionListItem .titleText
  {
    padding-left: 24px;
  }

  .messageList .message, .messageUserInfo
  {
    background: #3a373a;
    color: #ccc;
  }

  .messageUserBlock h3.userText a,.messageText
  {
    color: #ccc;
  }

  .messageText
  {
    font-size: 11pt;
    letter-spacing: .25px;
    font-family: 'Lato', sans-serif;
    line-height: 1.30em;
    margin-bottom: 30px;
  }

  .bbCodeBlock pre, .bbCodeQuote, .bbCodeQuote .attribution, .bbCodeQuote .quoteContainer
  {
    background: #2c2c2c!important;
    color: #ccc;
  }

  .discussionListItem .posterAvatar, .discussionListItem .stats
  {
    background-color: rgba(255,255,255,.1);
  }

  .nodeList .node.level_1, .nodeList > li:first-child, .nodeList > li:last-child
  {
    border: none;
  }

  .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo
  {
    background: #2d2d2d;
    border: none;
    color: #ccc;
  }

  .sidebar, .node .nodeLastPost
  {
    background: #333;
  }

  .node .nodeLastPost
  {
    border: solid thin #666;
  }

  a.callToAction span
  {
    background: lightseagreen;
  }

  .bbCodeQuote .quoteContainer .quoteExpand
  {
    color: lightseagreen;
    background: -moz-linear-gradient(top, rgba(0,0,0,0) 0%, rgba(0,0,0,0.65) 100%);
 /* FF3.6-15 */
    background: -webkit-linear-gradient(top, rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%);
 /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%);
 /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */;
  }

  .quickReply
  {
    background: #666;
    border: none;
  }

  .quickReply .messageUserInfo
  {
    background: none;
  }

  .sticky
  {
    border-left: 4px solid lightseagreen;
  }

  .fa-thumb-tack
  {
    color: lightseagreen!important;
  }

  .fa-eye
  {
    color: #b96600!important;
  }

  .fa-lock
  {
    color: #ccc!important;
  }

  a.primaryContent
  {
    background: #232323;
    border: none;
    border-bottom: 2px solid #333;
  }

  .alerts .primaryContent
  {
    background: #444;
    color: #ccc;
    border: none;
    margin-bottom: .35em;
  }

  .button
  {
    background: lightseagreen;
    border: none;
  }

  .button:hover
  {
    background: #1b9891;
  }

  .button:active
  {
    background: #26d9d0;
  }

  .hasJs .bbCodeSpoilerText
  {
    color: #ccc;
    background-color: #232323;
    padding: .5em;
    border: none;
    border-left: 4px solid lightseagreen;
  }

  li.message
  {
    border-bottom: 1px solid #434343!important;
  }

  .threadListSeparator, threadListSeparator sticky
  {
    margin-top: 6px;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 9pt;
    background-color: #292929;
    color: #ccc;
    border: none;
  }

  .node .subForumList li .nodeTitle::before
  {
    color: #ccc;
  }

  .sectionFooter
  {
    margin-top: 12px;
    border-top: 1px solid lightseagreen;
    background: none;
  }

  .messageList .message:target, .messageList .message:target .messageUserInfo
  {
    background: #555;
  }
}
