css:
@-moz-document domain("resetera.com") {
html,
body[class^="node"],
div#content,
footer {
    background-color: rgb(15, 15, 15);
    color: rgb(255, 255, 255);
}
div#headerMover {
    margin-bottom: 33px;
}
footer {
    position: fixed;
    bottom: -33px;
    width: 100%;
    z-index: 9999;
}
.discussionListItem .posterAvatar,
.discussionListItem .stats,
.discussionListItem .main,
.discussionListItem .lastPost {
    background-color: rgb(37, 37, 37);
    color: rgb(255, 255, 255);
}
.discussionList .sectionHeaders {
    border: 1px solid rgb(174, 129, 200);
    border-bottom: 1px solid rgb(242, 242, 242);
}
.redactor_box,
.redactor_dropdown {
    background-color: rgb(255, 255, 255);
    color: rgb(15, 15, 15);
}
.redactor_dropdown a:link,
.redactor_dropdown a:visited {
    color: rgb(15, 15, 15);
}
div#header {
    background-color: rgb(0, 0, 0);
}
.navTabs,
.formPopup .controlsWrapper {
    background-color: rgb(37, 37, 37);
    color: rgb(255, 255, 255);
}
.pollBlock .question .questionText,
.message .messageMeta .control,
.message .editDate {
    color: rgb(255, 255, 255);
}
.pollResult .bar,
.navPopup .listPlaceholder ol.secondaryContent.Unread {
    background-color: rgb(78, 22, 153);
}
.messageList#messageList {
    padding-right: 0;
}
.messageList .message {
    background-color: rgb(37, 37, 37);
    padding-right: 10px;
}
.messageUserInfo {
    background-color: rgb(37, 37, 37);
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo,
.searchResult {
    background-color: rgb(37, 37, 37);
    color: rgb(255, 255, 255);
}
.idstats dd,
.idstats dt {
    color: rgb(255, 255, 255);
}
.messageList li.message:nth-child(even),
.messageList li.message:nth-child(even) .messageUserInfo {
    background-color: rgb(74, 74, 74);
}
.messageList .message:target, .messageList .message:target .messageUserInfo {
    background-color: rgb(97, 72, 126);
}
a.PageNavPrev,
a.PageNavNext,
.searchResult .snippet a {
    color: rgb(255, 255, 255);
}
.PageNav a,
.PageNav a:link,
.PageNav a:visited,
body .itemPageNav a:hover {
    color: rgb(255, 255, 255);
    background-color: rgb(90, 90, 90);
}
#QuoteSelected a {
    color: rgb(255, 255, 255);
}
.PageNav a.currentPage {
    background-color: rgb(37, 37, 37);
}
.button:hover,
.button[href]:hover,
.buttonProxy:hover .button,
.xenOverlay .formOverlay a.button:hover {
    color: rgb(255, 255, 255);
    border: 1px solid rgb(255, 255, 255);
    margin-bottom: 0px;
}
.messageText,
.messageUserBlock h3.userText,
.messageUserBlock a.username {
    color: rgb(255, 255, 255);
}
.bbCodeQuote .quoteContainer,
.bbCodeBlock pre,
.bbCodeBlock .code {
    background-color: rgb(53, 53, 53);
    border: 1px groove #e3e2e2;
}
.bbCodeQuote .quoteContainer .quoteExpand {
    background: -webkit-linear-gradient(top, rgba(255, 252, 252, 0) 0%, rgb(174, 129, 200) 80%);
    background: -moz-linear-gradient(top, rgba(255, 252, 252, 0) 0%, rgb(174, 129, 200) 80%);
    background: -o-linear-gradient(top, rgba(255, 252, 252, 0) 0%, rgb(174, 129, 200) 80%);
    background: linear-gradient(to bottom, rgba(255, 252, 252, 0) 0%, rgb(174, 129, 200) 80%);
}
.node .nodeLastPost,
.sidebar {
    color: rgb(255, 255, 255);
    background-color: rgb(53, 53, 53);
}
.primaryContent {
    background-color: rgb(255, 255, 255);
    color: rgb(37, 37, 37);
}
.profilePage .profileContent {
    background-color: rgb(37, 37, 37);
}
.profilePage .primaryContent {
    background-color: rgb(37, 37, 37);
    color: rgb(255, 252, 252);
    padding: 0 10px;
}
.secondaryContent {
    color: rgb(255, 255, 255);
    background-color: rgb(37, 37, 37);
}
.Menu .primaryContent,
.Menu .secondaryContent,
.Menu .sectionFooter {
    color: rgb(255, 255, 255);
    background-color: rgb(37, 37, 37);
    border-bottom: 1px solid rgb(37, 37, 37);
}
.navPopup .PopupItemLinkActive:hover {
    background-color: rgb(54, 54, 54);
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.nodeList .categoryStrip .nodeTitle,
.nodeList .categoryStrip {
    background-color: rgb(174, 129, 200);
}
a[href]:hover,
.navigationSideBar a[href]:hover {
    color: rgb(218, 173, 244);
    text-decoration: none;
}
.profilePage .tabs.mainTabs li a:hover {
    background-color: rgb(52, 39, 68);
    color: rgb(218, 173, 244);
}
.navTabs .navTab.selected .navLink {
    color: rgb(174, 129, 200);
}
.breadcrumb,
.footer .pageContent {
    background-color: rgb(174, 129, 200);
}
.breadcrumb .crust a.crumb:hover > span,
.footer a[href]:hover {
    text-decoration: none;
    color: rgb(246, 230, 255);
}
a:link,
a:visited,
.blockLinksList a,
.blockLinksList label {
    color: rgb(193, 193, 193);
}
.ugc a:link,
.ugc a:visited {
    color: rgb(202, 157, 228);
}
.ugc a:hover, .ugc a:focus {
    color: rgb(127, 81, 186);
    text-decoration: none;
}
.navigationSideBar a:link,
.navigationSideBar a:visited {
    color: rgb(140, 140, 140);
}
.footer a {
    color: rgb(255, 255, 255);
}
.thread_view .titleBar {
    background-color: rgb(15, 15, 15);
    color: rgb(255, 255, 255);
}
.titleBar h1 {
    color: rgb(255, 255, 255);
}
#content .pageContent {
    color: rgb(255, 255, 255);
    background-color: rgb(15, 15, 15);
}
.quickReply,
.quickReply .messageUserInfo {
    background-color: rgb(15, 15, 15);
    border: none;
}
}
