css:
@-moz-document domain("resetera.com") {
body {
    font-family: -apple-system,".SFNSText-Regular",BlinkMacSystemFont,"San Francisco","Roboto","Segoe UI","Oxygen","Ubuntu","Cantarell","Fira Sans","Droid Sans","Helvetica Neue","Helvetica","Lucida Grande",sans-serif;
    background-color: #0F0F0F;
    color: #ccc;
}

html, #header {
    background-color: #0F0F0F;
}

#content .pageContent {
    background-color: #323232;
    color: #ccc;
}

.titleBar h1 {
    color: #ccc;
    font-size: 20px;
}

#pageDescription {
    color: #888;
}

#pageDescription a.username {
    color: #9e79c3;
}

.navTabs {
    background-color: #161616;
}

.navTabs .navTab.selected .navLink {
    color: #9e79c3;
}

.breadBoxTop {
    padding-top: 0;
}

.breadBoxTop>nav {
	margin-bottom: 10px;
}

.breadBoxTop .breadcrumb {
    border-radius: 0px;
    border: none;
}

.messageList .message {
    background-color: #2A2A2A;
}

.messageUserInfo {
    background-color: #2a2a2a;
}


.userBanner.bannerSecondary {
    background-color: #7739B4;
}
.messageUserBlock a.username {
    color: #9e79c3;
}

.userBanner strong {
    color: #aaa;
}

.messageList li.message:nth-child(even), .messageList li.message:nth-child(even) .messageUserInfo {
    background-color: #222;
}

.messageText {
    font-size: 16px;
    color: #ccc;
    font-family: -apple-system,".SFNSText-Regular",BlinkMacSystemFont,"San Francisco","Roboto","Segoe UI","Oxygen","Ubuntu","Cantarell","Fira Sans","Droid Sans","Helvetica Neue","Helvetica","Lucida Grande",sans-serif;
}


.bbCodeQuote .attribution,
.bbCodeQuote .quoteContainer {
    background-color: #171717;
    color: #989898;
    padding: 0;
    font-style: italic;
}

.bbCodeQuote .attribution {
    margin-bottom: 10px;
}

.bbCodeBlock.bbCodeQuote aside {
    background-color: #171717;
    padding: 6px 8px;
}

a[href]:hover {
    color: #9e79c3;
}

.pageNavLinkGroup {
    padding: 0 8px;
}

.PageNav a {
    background-color: #7739B4;
    border-color: #7739B4;
}

.quickReply {
    background-color: #191919;
}

.quickReply .messageUserInfo {
    background-color: inherit;
}

.nodeList .forumNodeInfo {
    background-color: #262626;
    color: #a0a0a0;
    border: none;
    padding: 15px 10px !important;
}

.nodeList .nodeTitle a {
    color: #e6e6e6;
}

.node .nodeControls {
    top: 7px;
}

.nodeList .nodeLastPost {
    background-color: inherit;
    color: #a3a3a3;
    top: 15px;
}

.nodeList .nodeLastPost a {
    color: inherit;
}

.nodeList > li:first-child,
.nodeList > li:last-child {
    border-color: transparent;
}

.nodeList .nodeText .idstats dl>dd>strong {
    color: #e6e6e6
}

aside .section {
    margin-top: 0;
}

ol.nodeList.sectionMain {
    margin-top: -63px;
}

.sidebar .section .secondaryContent h3,
.breadcrumb .crust:first-child a.crumb {
    border: none;
    border-radius: 0;
}

.nodeList .node.level_1 {
    margin-bottom: 0;
}

.PageNav .pageNavHeader {
    display: inline-block;
    margin-right: 20px;
}

.discussionListItem,
.discussionListItem .stats {
    background-color: #1D1D1D;
    border: none;
}

.discussionListItem .stats dl {
    /*border-color: #3f3f3f;*/
    border-color: transparent;
}

.discussionListItem .stats .major {
    margin-top: 0;
}

.discussionListItem .stats .minor {
    margin-bottom: 0;
}


.discussionListItem .title a {
    color: #a0a0a0;
}

.sectionFooter {
    background-color: #1D1D1D;
    color: #ccc;
}

.pageContent .titleBar h1 {
    text-align: center;
}
}
