css:
@-moz-document domain(docs.aws.amazon.com) {
  html, body, #main, #left-column, #pagetoc,
  ul.awstoc, li.awstoc {
    background-color: #3b3b3b;
  }

  body, div, p, 
  a.awstoc.selected {
    color: #cacaca;
  }

  a, p a, li a, dt a {
    color: #669bd0;
  }
  
  h1 {
  	color: #dadada;
  }

  h2 {
    color: #de8c38;
  }

  h3 {
      color: #3fa2bd;
  }

  a:visited {
    color: #c38a37;
  }

  #service-name, a.pagetoc.selected, a.awstoc.selected {
    color: #f5f5f5;
  }

  .hljs-number, .hljs-date, .hljs-regexp, 
  .hljs-literal, .hljs-hexcolor, .smalltalk .hljs-symbol, 
  .smalltalk .hljs-char, .go .hljs-constant, .hljs-change, 
  .lasso .hljs-variable, .makefile .hljs-variable, 
  .asciidoc .hljs-bullet, .markdown .hljs-bullet, 
  .asciidoc .hljs-link_url, .markdown .hljs-link_url,
  .hljs, .hljs-subst, .hljs-tag .hljs-title, .nginx .hljs-title {
      color: #f5f5f5;
  }
  
  pre, .hljs {
      background-color: #5a5a5a;
      color: #f5f5f5;
  }
  
  #search-select {
      width: 100%;
      padding: 5px 2px;
      height: 30px;
  }
  
  #search-query {
      width: calc(100% - 32px);
      padding: 6px 4px 5px;
      border: thin solid darkgray;
      margin-top: 10px;
  }
  
  #search-button {
      position: relative;
      top: 5px;
      transform: scale(1.3);
      left: -2px;
  }
  
}
