css:
@-moz-document domain(forum.hellroom.ru) {
.catbg2,
.catbg,
tr.catbg td,
.catbg3,
tr.catbg3 td {
    background: #333336 !important;
}

.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td {
    background: #333336 !important;
    color: #ffffff !important;
    border: none !important;
    vertical-align: middle;
}

#menubar {
    background-color: #2c774c !important;
    color: #ffffff !important;
}

.titlebg a:link,  .titlebg a,   .titlebg a:visited,  .titlebg a:hover {
    color: #ffffff !important;
}
  
.titlebg2 a:link,
.titlebg2 a:visited {
    color: white;
}
.branding {
    background: #2c774c !important;
}

a:link,
a:visited {
    color: #31af67;
}

#menubar a {
    color: #ffffff !important;
}

#menubar a:hover {
    text-decoration: underline;
}

/* color: #31af67 !important;*/

body > table {
    margin: 0 auto !important;
}

body > table td {
    background: none !important;
    font-size: 12px !important;
}

.maintab_active_back a {
    font-weight: bold;
    color: #31af67 !important;
}

.maintab_back a:hover, .catbg a:hover, .catbg2 a:hover {
	color: #31af67 !important;   
}

.maintab_active_first,
.maintab_active_last,
.maintab_first,
.maintab_last {
    width: 0 !important;
    display: none !important;
}

.mirrortab_first,
.mirrortab_last {
    display: none !important;
}

.mirrortab_back,
.maintab_back {
    background: none !important;
}

.mirrortab_back a:hover {
	color: #31af67 !important;      
}

.maintab_active_last {
    display: none !important;
}

a:hover {
    color: #31af67;
    text-decoration: underline;
}

body {
    background: #070708 !important;
}

.nav,
.nav:link,
.nav:visited {
    color: #cccccc !important;
}

.windowbg,
#preview_body {
    background-color: #333336;
}

.windowbg {
 	vertical-align: middle !important;   
}

.windowbg2 {
    background-color: #222226;
}

.windowbg3 {
    background-color: #262629;
}

body,
td,
th,
tr {
    color: #ffffff !important;
}

.tborder {
    border: none !important;
    background-color: #222226 !important;
}

.bordercolor{
    background-color: #111114 !important;
}

hr, .hrcolor {
    height: 1px;
    border: 0;
    color: #111114 !important;
    background-color: #111114 !important;
}

.catbg,
.catbg2,
.catbg3 {
    border: none !important;
}

.quoteheader, .codeheader, .quoteheader a {
 	color: #cccccc !important;   
}

.quoteheader a:hover {
    color: #31af67 !important;
}

.code {
    background-color: #111114;
    border: 1px solid #2c774c !important;
    color: #cccccc !important;
}

.quote {
    background: #444446 !important;
    border: 1px solid #111114 !important;
    color: #cccccc !important;
}

.spoilerbody {
    background: none !important;
    margin: 10px auto 1px auto !important;
    width: 95% !important;
    color: #ffffff !important;
}

input[name="search"],
input[name="subject"],
#shoutbox_message,
textarea.editor {
	background-color: #070708 !important;
    color: #ffffff !important;
    border: 1px solid #222226 !important;
    border-radius: 3px;
}

input[name="search"] {
    width: 340px !important;   
}

input[type="submit"], input[type="button"], .spoilerbutton {
	background-color: #2c774c!important;
    color: #ffffff !important;
    border: 2px solid #31af67 !important;
    border-radius: 3px;
    padding: 1px;
    margin: 1px;
    font-weight: bold;
}

a.nav:hover {
	color: #31af67 !important;
    text-decoration: none !important;
}

td[style="padding:0.1em 0 0 0.2em"] img {
    background: #2c774c!important;
    border: 2px solid #31af67 !important;
    border-radius: 3px;
}
}
