css:
@-moz-document domain("azure.com"), regexp(".*portal.azure.com")
{
  #terminal-container .terminal {
    font-family:  "Ubuntu Mono", Consolas,"Liberation Mono", Menlo, Monaco;
    font-weight: normal;
    background-color: RGB#272C33;
    color: #DBDFE4;
}
.terminal .xterm-viewport {
    background-color: #272C33;
}
#terminal-container {
    background-color: #272C33;
    font-size: 16px !important;
}
.terminal .xterm-color-1 {
    color: #DF6C75;
}
.terminal .xterm-color-4 {
    color: #2a566b;
}
.terminal .xterm-color-9 {
    color: #db5252;
}
.terminal .xterm-color-10 {
    color: #98C379;
}
.terminal .xterm-color-12 {
    color: #61AFEF;
}
.terminal .xterm-color-14 {
    color: #56B5C1;
}
.terminal .xterm-bg-color-2 {
    background-color: #98C379;
}
.terminal .xterm-color-11 {
    color: #E4C07A;
    background-color: transparent;
}
}
