css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr"), domain("eldarya.com.br"), domain("eldarya.com.br")
{

/*Leiftan Familiar*/  
 
  /*Images - pictures*/
  #pet-headerPortrait-base
  {
    background: url("https://78.media.tumblr.com/cc2aad424d2a91605c670ff0126f9cf0/tumblr_oyg6zigyeT1trxrtqo1_500.png") -15px top no-repeat !important;
    background-size: auto 100px !important;
  }

  #pet-portrait
  {
    background: url("https://78.media.tumblr.com/cc2aad424d2a91605c670ff0126f9cf0/tumblr_oyg6zigyeT1trxrtqo1_500.png") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"]
  {
    display: none !important;
  }

    /*Infos*/

  #pet-info-name:before
  {
    content: "Leiftan" !important;
    font-size: 20px !important;
  }

  #pet-info-name
  {
    font-size: 0 !important;
  }

  #pet-info-evolve > .on
  {
    font-size: 0 !important;
  }

  .pet-info-evolve-icon
  {
    font-size: 13px !important;
  }

  .pet-info-evolve-icon:after
  {
    content: " Oh... Leiftan, aun puedo quererte?";
  }

  #pet-info-racegender:before
  {
    content: "Daemon" !important;
    font-size: 16px !important;
    line-height: 20px !important;
  }

  #pet-info-racegender
  {
    font-size: 0 !important;
    margin-top: 5px;
  }

  img[src*="/static/img/f.png"]
  {
    background: url("http://www.eldarya.fr/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
  }
 
 /*Nourriture - food*/
  
  #pet-info-food > img
  {
    background: url("https://78.media.tumblr.com/918b2e1fa593e7c326b09d29ead98476/tumblr_onphubmQiO1trxrtqo5_r1_75sq.png") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
  }

  #pet-info-food
  {
    font-size: 0 !important;
  }

  #pet-info-food:before
  {
    content: "Gardiennes?)" !important;
    font-size: 16px !important;
  }

  #pet-info-food > span
  {
    font-size: 16px !important;
  }

  /*Ezarel sur la page du profil*/

  #player-display-pet
  {
    background: url("https://78.media.tumblr.com/0e00d7008d2e7fa4ec512e08b7aa590b/tumblr_oyfzc16Kwm1trxrtqo1_1280.png") no-repeat;
    background-size: auto 100%;
    height: 600px !important;
    width: 1000px !important;
  }

  #player-display-pet > img
  {
    display: none !important;
  }
 }
