css:
@-moz-document domain("resetera.com") {
*, h1,h2,h3 {
color: #e6e6e6!important;
    
}



*:hover{
    -o-transition: .2s;
    -ms-transition: .2s;
    -moz-transition: .2s;
    -webkit-transition: .2s;
    transition: .2s;
}

.threadListSeparator, .threadListSeparator sticky{
    background-color:#5a3479!important;
    
}

/*User Quote Highlights */

[data-author="REPLACE WITH YOUR USERNAME"].bbCodeQuote .attribution, [data-author="REPLACE WITH YOUR USERNAME"].bbCodeQuote .quoteContainer
{
background-color: #1c2b1b!important;
}




html{
 background-color:rgb(48, 48, 48);   
}


#header {
    background:#303030!important;
}

.secondaryContent, .sidebar {
background-color:rgb(48, 48, 48);
    
    
}



.Menu .secondaryContent{
    background-color:rgb(48, 48, 48);
}

.breadcrumb {
    background-color: rgb(34, 34, 34)!important;
    border: 0px none #222222;
    padding-left:12px;
}
.breadcrumb .crust a.crumb > span {
    color: #CCCCCC !important;
}
.breadcrumb .crust:first-child a.crumb,
.breadcrumb .crust.firstVisibleCrumb a.crumb {
    border-bottom-left-radius: 0px !important;
    padding-left: 0px !important;
}
.Popup .PopupControl.PopupOpen,
.Popup.PopupContainerControl.PopupOpen {
    background-color: rgb(34, 34, 34) !important;
}
.Popup .PopupControl:hover,
.Popup.PopupContainerControl:hover {
    background-color: #5a3379!important
}

.Menu {
    border-color: rgb(51, 51, 51) !important;
}

#content .pageContent {
    background: #0f1013 !important;
    color: rgb(135, 135, 135) !important;
}
.discussionList .sectionHeaders,
.sectionFooter {
    background:#5a3379!important;
    color: #a3a6c1 !important;
}
.discussionList .sectionHeaders dd a[href]:hover {
    background:#5a3379!important;
}

.PageNav a {
    background-color: rgb(34, 34, 34) !important;
    border: 1px solid rgb(51, 51, 51) !important;
}



.DiscussionListOptions {
    border: 1px solid rgb(51, 51, 51) !important;
}
.discussionListItem,
.discussionListItem .stats,
.discussionListItem .stats dl {
    background-color: rgb(37, 38, 40) !important;
    border-color: rgb(51, 51, 51) !important;
}
.discussionListItem .itemPageNav {
    visibility: visible;
}
.pairs dt,
.pairsInline dt,
.pairsRows dt,
.pairsColumns dt,
.pairsJustified dt {
    color: rgb(135, 135, 135) !important;
    border: 0px none rgb(34, 34, 34) !important;
}
.nodeList .categoryStrip,
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    /* background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;*/
    
    background: #5a3379!important;
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    /*background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;
    background-color: transparent !important;*/
    
    background: rgb(34, 34, 34) !important;
    color: #a3a6c1 !important;
    border: 1px solid rgb(51, 51, 51) !important;
}
.nodeList .forumNodeInfo {
    color: #CCCCCC !important;
}
.nodeList > li:first-child,
.nodeList > li:last-child,
.node .nodeLastPost {
    border: 0px none rgb(34, 34, 34) !important;
}
body .itemPageNav a {
    background-color: rgb(34, 34, 34) !important;
    border: 1px solid rgb(51, 51, 51) !important;
}
a.callToAction span {
    color: rgb(255, 255, 255) !important;
    background-color: #5a3379!important;
    border: 1px solid rgb(51, 51, 51) !important;
}
#DiscussionListOptionsHandle a {
    background: #0f1013 !important;
}
.discussionListItems .unread .title a {
    font-weight: normal;
}
.navPopup .listItem:hover {
    background: #5a3379!important
}
.navPopup .listItem {
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
/* Help */

.heading,
.xenForm .formHeader {
    font-weight: normal !important;
    font-size: 12px;
    background: #5a3379!important;
    padding: 10px;
}
.subHeading {
    background: #5a3379!important;
}
.textHeading,
.xenForm .sectionHeader {
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
.dataTable tr.dataRow th {
    background: #222222 !important;
    color: #CCCCCC !important;
    border-top: 0px solid rgb(51, 51, 51) !important;
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
.bbCode > dl > dd {
    /*background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;*/
    
    border: 1px solid rgb(51, 51, 51) !important;
    background: rgb(47, 52, 61);
}
/* Misc */

.node .nodeLastPost{
 background-color:rgb(34, 34, 34);   
}

.footer, .footerLegal{
    background-color:#303030!important;
}

#QuickSearch,
#QuickSearch.active {
    background-color: transparent;
    
    border: 0px solid rgb(51, 51, 51) !important;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0);
}
.formPopup .primaryControls {
    padding: 0;
}
.textCtrl {
    border: 1px solid rgb(51, 51, 51) !important;
    background: #222222 !important;
    color: #CCCCCC !important;
}
.formPopup {
    background-color: rgb(34, 34, 34) !important;
}
.formPopup .controlsWrapper {
    background: rgb(34, 34, 34) !important;
}
.formPopup .secondaryControls {
    padding-bottom: 1px;
}
.searchResult {
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}

.button{
background-color:transparent!important;
    border-color:#0f0f0f;
}

.blockLinksList a,
.blockLinksList label {
    color: #CCCCCC !important;
}
.blockLinksList a,
.blockLinksList label:active {
    background-color: transparent !important;
}
.xenOverlay .formOverlay {
    background: rgba(0, 0, 0, 0.75)
}
.textHeading,
.xenForm .sectionHeader {
    color: #FFFFFF !important;
}
.xenForm fieldset,
.xenForm .formGroup {
    border-top: 0px solid rgb(51, 51, 51) !important;
}
.xenForm fieldset + .ctrlUnit,
.xenForm .formGroup + .ctrlUnit,
.xenForm .submitUnit {
    border-top: 0px solid rgb(51, 51, 51) !important;
}
.textHeading,
.xenForm .sectionHeader {
    color: #CCCCCC !important;
    font-weight: normal !important;
}
.navTab .accountPopup img {
    border: 0px solid rgb(51, 51, 51) !important;
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    color: #CCCCCC !important;
}
.profilePage .mast .sectionFooter {
    border-left: 0px solid rgb(126, 141, 165) !important;
    border-right: 0px solid rgb(126, 141, 165) !important;
}
/* Sign In */

#SignupButton {
    background: transparent !important;
    padding: 0px;
}
#SignupButton .inner {
    background-color: #5a3379!important;
}
.xenOverlay #pageLogin {
    background: rgb(34, 34, 34) !important;
    color: #CCCCCC !important;
}
.heading,
.xenForm .formHeader {
    color: #CCCCCC !important;
}

.primaryContent {
    background-color: #222222 !important;
    border-bottom: rgb(51, 51, 51) !important;
    border-left: 0px none;
    border-right: 0px none;
    padding-top: 5px;
    padding-left: 5px;
    padding-bottom: 5px;
}

.breadcrumb {
    background-color: rgb(34, 34, 34)!important;
    border: 0px none #222222;
}
.breadcrumb .crust a.crumb > span {
    color: #CCCCCC !important;
}
.breadcrumb .crust:first-child a.crumb,
.breadcrumb .crust.firstVisibleCrumb a.crumb {
    border-bottom-left-radius: 0px !important;
    padding-left: 0px !important;
}
.Popup .PopupControl.PopupOpen,
.Popup.PopupContainerControl.PopupOpen {
    background-color: rgb(34, 34, 34) !important;
}
.Popup .PopupControl:hover,
.Popup.PopupContainerControl:hover {
    background-color: #5a3379!important
}
.navTabs .navTab.PopupOpen .navLink {
    color: #a3a6c1 !important;
}
.navTabs .navTab.PopupOpen .navLink:hover {
    color: #7d7f84 !important;
}
.navTabs .navTab.selected.PopupOpen .navLink {
    background-color: transparent !important;
}

.node .nodeInfo.primaryContent, .node .nodeInfo.secondaryContent{
    background-color: #3f3f3f;
}

#headerMover #headerProxy{
 background-color:#2f2f2f;   
}



.navTabs {
    margin: 0; padding: 0;
    text-transform: none!important;
    position: relative;
   /* width: -webkit-fill-available!important;*/
    z-index: 1000;
    left:110px!important;
    top:22px;
    padding-left: 7%;
    padding-right: 7%;
/*    background-color:#212121;*/
    background-color:transparent;
    color:transparent;
    box-shadow:none;
}

#header .pageWidth .pageContent:after{
clear:none;
}



#content, #content .pageContent { 
background-color:#2f2f2f!important;

}
.userBanner {
    color: #FF8B01 !important;
}
.userBanner.bannerSecondary {
    background-color: #5a3379!important;
    border-color: #5a3379!important;
}
.messageList .message {
    background-color: rgb(34, 34, 34);
    border: 1px solid rgb(51, 51, 51);
    border-bottom: 0px none rgb(51, 51, 51);
}
.messageList li.message:nth-child(2n),
.messageList li.message:nth-child(2n) .messageUserInfo {
    background-color: rgb(40, 40, 40)
}
.messageText {
    color: #CCCCCC !important;
}
.messageUserInfo {
    background-color: rgb(34, 34, 34)!important
}
.message .messageMeta .control {
    background-color: rgb(47, 52, 61)
}
.quickReply,
.redactor_toolbar {
    background-color: rgb(34, 34, 34) !important;
    border: 1px solid rgb(51, 51, 51) !important;
    padding-bottom: 2px;
}
.redactor_box {
    border: 1px solid rgb(51, 51, 51) !important;
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
.button,
.button.primary {
    color: rgb(255, 255, 255) !important;
    background-color: #5a3379!important;
    border: 1px solid rgb(51, 51, 51) !important;
    height: 25px !important;
    margin-right: 5px;
}
.button:hover,
.button.primary:hover {
    color: #FF8B01 !important;
}
.footer .pageContent {
    background: #5a3379!important;
}
.messageList .newMessagesNotice {
    background: #5a3379!important;
    border: 1px solid rgb(51, 51, 51) !important;
}
.PageNav a.currentPage {
    background: #5a3379!important;
}
.xenTooltip {
    background: #5a3379!important;
}
.hasJs .bbCodeSpoilerText {
    background: #5a3379!important;
    color: #CCCCCC !important;
    border: 1px solid rgb(51, 51, 51) !important;
    padding-top: 6px;
}

/* Remove username, and time posted at the bottom of each post */

.username.author {
    display: none;
}
.authorEnd {
 display:none;   
}


/* Quotes */

.bbCodeQuote .attribution,
.bbCodeQuote .quoteContainer {
    background: rgb(52, 47, 61) !important;
    border-color: #3c414d !important;
    color: #CCCCCC !important;
}
.bbCodeQuote {
    border: 1px none #333333 !important;
}
.bbCodeBlock .type {
    background: rgb(52, 47, 61) !important;
}
.bbCodeBlock pre,
.bbCodeBlock .code {
    background: rgb(52, 47, 61) !important;
}
.bbCodeQuote img {
    max-height: 400px;
}



/* Show the whole quote and hide the gradient/click to expand text */

.bbCodeQuote .quoteContainer .quote {
    max-height: none;
    overflow: hidden;
    padding-bottom: 1px;
}

.bbCodeQuote .quoteContainer .quoteExpand
{		
background:transparent!important;
    display:none;
}


.xenForm { 
     max-width: 1200px;   
}

/* Threads */

.titleBar h1,
.idstats dt,
.idstats dd {
    color: #CCCCCC !important;
}


/*Animations */

.secondRow{
    -o-transition: .2s;
    -ms-transition: .2s;
    -moz-transition: .2s;
    -webkit-transition: .2s;
    transition: .2s;
    opacity: 0.30;
}




.secondRow:hover{
    opacity: 1;
}
.navPopup .listPlaceholder ol.secondaryContent.Unread{
	background-color:#5a3379!important
}

.xenOverlay.timedMessage{
    background:#303030!important;
}

#StackAlerts .stackAlertContent{ 
 background-color:#222222!important;   
}

.listBlock{
 	padding-left:10px;  
    padding-bottom:3px;
}



#unreadLink, .fa-circle{ 
    color:rgb(127, 81, 186)!important;
    
}

body {
    background-color:#222222;
}

.discussionListItem[class~="sticky"]  .stats, .discussionListItem[class~="sticky"] .stats dl {
    background-color:#1c2b1b!important;
}

.sticky {
    background-color:#1c2b1b!important;
}


.redactor_dropdown {
    background-color:#222222;
    border: 0px;
    box-shadow: 0 1px 1px #222;
}


/* Button Replacement */

redactor_btn_container_bold,redactor_btn_container_switchmode,redactor_btn_container_removeformat,redactor_btn_container_undo,redactor_btn_container_redo,redactor_btn_container_image,redactor_btn_container_insert,redactor_btn_container_smilies,redactor_btn_container_media,redactor_btn_container_unorderedlist,redactor_btn_container_indent,redactor_btn_container_orderedlist,redactor_btn_container_outdent,redactor_btn_container_alignment,redactor_btn_container_createlink,redactor_btn_container_unlink
{

}



/* New Update */ 

.messageUserBlock .avatar img,.avatar .img.m {
    border-radius:36px;
    
}

.avatar .img.m{
    border-radius:45px!important
    
}

.miniMe {
    border-radius:45px!important
    
}

#logo img{
    position: relative;
    padding-top: 10px;  
    z-index:9999;
}

#searchBar, #QuickSearch{
 top:15px;  
right:100px;
    z-index:9999;
   
}

#QuickSearch .active{
     border-radius:30px;
}

#primaryControls, #secondaryControls {
    z-index:9999;
     border-radius:30px;
}


#headerMover{
    
    background-color:#2f2f2f;
}

.navTabs .navTab.PopupOpen .accountPopup img{
    border-radius:45px!important
    
}

.userBanner.bannerSilver{
    background-color:transparent;
    border-color:transparent;
}

/*.navTab .accountPopup img { border-radius: 3%!important }*/


/* Mobile Fixes */

.messageList li.message:nth-of-type(2), .messageList li.message:nth-of-type(2) .messageUserInfo, .messageInfo li.message:nth-of-type(2){
 	background-color:#282829!important;
    	border: 0px!important;
}

.Responsive .messageList .message{
border:1px solid #282829;
    background-color:rgb(34, 34, 34)!important;
}

.Responsive .messageList .message:nth-of-type(2){
 background-color:#282829!important;
 border:1px solid #282829;
    
}

.profilePage .tabs.mainTabs{
    background: #303030!important;
}

.tabs li a, .tabs.noLinks li{
    background-color:rgb(90, 52, 121)!important;
}

.tabs li a:hover, .tabs.noLinks li:hover{
    background-color:rgb(141, 81, 190)!important;
}

.ugc a:link, .ugc a:visited{
	color: rgb(127, 81, 186)!important;
	font-weight: bold!important;
}

.profilePage .profileContent{
   background: rgb(48, 48, 48);
}
}
