css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("02ch.net") {
body {
	font-family: "Trebuchet MS",Trebuchet,tahoma,serif;
	background-color: rgb(35,25,55);	
    color: rgb(205,156,156);
	background: url(https://i.imgur.com/1AXHYbQ.png)no-repeat fixed 100% 100%!important;
	background-size: 275px!important;
}

.footer  {		
	margin-bottom: 460px!important;
}
html {
	background:rgb(35,25,55)!important;
}
a {
	color: rgb(255,137,81);
}
a:hover {
	color: #5566bb;
}
textarea,input, select {
  box-sizing: content-box;
  appearance: none;
  background-color: rgb(190,170,170);
  color: #444;
  border: 2px solid #545454;
  border-radius: 5px;
}
textarea:hover, textarea:focus, input:hover, input:focus {
    background-color: rgb(180,160,160);
    color: #444;
}
input[type="submit"] {
		
  background-color: rgb(230,170,170);
}
	
.theader {
	background-color: rgb(205,156,156)!important;
	color: rgb(40,30,60)!important;
	border-color: #545454!important; 
}

.image { 
	border: none;
	float: left;
	margin: 2px 20px;
}
.adminbar {
	clear:both;
	float:right;
	font-size: .8em;
  text-align:right;
}
.adminbar a {
	font-weight: bold;
}
.logo {
	clear:both;
	text-align:left;
	font-size:2em;
	font-weight: bold;
	color: rgb(255,137,81);
	/*width:100%;*/
}
.eye {
    margin: 0;
    padding: 0;
    text-align:center;
    font-size: .8em;
    font-weight: bold;
    color: #FF6600;
}
.eye-board {
}
.eye-feed {
    position: relative;
    float: right;
    top: 0;
    right: 0;
}
.eye-self {
    position: fixed;
    float: right;
    bottom: 20px;
    right: 10px;
}
.eye-thread {
}
.counter {
	width: 50px;
	font-weight: bold;
	color:#FF6600;
} 
.sepblock table {
	margin: 0px;
	text-align:left;
	width:100%
}
.separator {
	background:#EEEEEE;
	border-top:1px solid #BBBBBB;
	font-size:1px;
	width:100%;
	padding:0;
	margin:0;
	height:1px;
	overflow:hidden;
}
.logo2 {
	text-align:center;
}
.theader, .passvalid {
	background:#DDDDDD;
	text-align:center;
	padding:2px;
	color:#2266AA;
	clear: both;
	font-weight: bold;
	margin-bottom: .5em;
	border: solid 1px #CCCCCC;
	-moz-border-radius: 5px;
}
.postarea {
}
.post {
    margin: 4px 0;

    min-width: 35em;
    overflow: visible;

    word-wrap: break-word;
}
.rules {
}
.postblock {
	background:transparent;
	color:rgb(205,156,156);
	font-weight:bold;
}
.footer {
	text-align:center;
	font-size:12px;
	font-family:serif;
	margin: 1em 0 0 0;
}
.dellist {
	font-weight: bold;
	text-align:center;
}
.delbuttons {
	text-align:center;
	padding-bottom:4px;
}
.managehead {
	background:#DDDDDD;
	color:#002244;
	padding:0px;
}
.postlists {
	background:#FFFFFF;
	width:100%;
	padding:0px;
	color:#800000;
}
.row1 {
	background:#DDDDDD;
	color:#002244;
}
.row2 {
	background:#CCCCCC;
	color:#002244;
}
.unkfunc {
	background:inherit;
	color:#789922;
}
.reflink {
	font-size: .8em;
	font-weight: bold;
}
.filesize {
	text-decoration:none;
	color: #655;
	font-size: .8em;
}

.postername {
	/*
	color:#004A99;
	font-weight:bold;
	display:none;
	*/
}
.postertrip {
	color:#228854;
}
.oldpost, .notabene {
	color:#CC1105;
	font-weight:bold;
}
.omittedposts {
	color:#655;
}
.reply {
	background: rgb(50,37,70);
	border: solid 2px #655;
	padding: 0;
	margin: 0;
	border-radius: 5px;
	-moz-border-radius: 5px;
	background-image: url(https://i.imgur.com/HySJb2b.png)!important;
	background-position: top right!important;
	background-size: 10em!important;
	background-repeat: no-repeat;
}
.reply blockquote, blockquote :last-child {
	margin-top: 5px;
	margin-bottom: 3px;
}
	
blockquote {
	margin: .5em .5em .5em 1em;
}

.reply blockquote p {
	margin-right: 30px!important;
}
.doubledash {
	display: none;
 	vertical-align:top;
	clear:both;
	float:left;
}
.replytitle, .filetitle {
	font-size: 1.2em;
	color: rgb(255,137,81);
	font-weight:bold;
}
.commentpostername {
	/*
	color:#004A99;
	font-weight:800;
	display:none;
	*/
}
.thumbnailmsg {
	font-size: .6em;
	color:#655;
}
hr {
	border-style: solid none none none;
	border-width: 1px;
	border-color: #655;
}
table {
	border-style: none;
}
table td {
	border-style: none;
}
.nothumb {
	background-color: rgb(35,25,55);	
	border-style: dotted;
	margin: .3em .5em;
}
.abbrev {
	color:#655;
}
.highlight {
	background:#EEDACB;
	color:#333333;
	border: 2px dashed #EE6600;
}
dl.menu dt {
	background: rgb(60,47,80);
	border: solid 1px #655;
	-moz-border-radius: 5px;
	margin-top: 1em;
	padding-left: .5em;
	cursor: pointer;
	
}
dl.menu dd {
	margin-left: .5em;
	padding-left: .5em;
	border-left: solid 1px #CCCCCC;
}
dl.menu dd.hidden {
	display: none;
}
.spoiler {
 background: #555;
 color: #555;
}
.spoiler blockquote {
    background: #555;
    color: #555;
}
.spoiler:hover {
 background: rgb(205,156,156);
 color: rgb(40,30,60);
}
.spoiler blockquote:hover {
    color:#789922;
}
.spoiler blockquote blockquote:hover {
    color:#406010;
}
.spoiler blockquote blockquote blockquote:hover {
    color:#204010;
}
.underline {
 text-decoration: underline;
}
.captcha {
    color:#404040;
}
img[src*="captcha"] {
    background: rgb(180,160,160);
}
img[src="img/cover04.png"] {
	margin-bottom: -450px!important;
}
.red {
	color:#ff0000;
}

}
