css:
@-moz-document domain(voyager.tempworks.com)
{
    /*
     * Name:    Voyager Dark Theme
     * Author:  Jacob Thompson
     * Version: v1
	 
	 *Because Evan said no.
			 
     */

/*General*/
	 html, form, body, div
	{
		background: #3A4040 !important;
		color: #dcdcdc !important;
	}

	
	
/******************Case queue******************

/*Page Header*/
	.page-header 	
	{
		border-bottom: 1px solid #151b1e !important;
	}
	
	.global-head .logo:hover
	{
		background-color: rgba(175, 175, 175, 0.35)
	}
	
	.global-head .newCase:hover
	{
		background-color: rgba(175, 175, 175, 0.35)
	}

	.uv-icon svg
	{
		background: #151b1e !important;
	}
	
/*Search Bar*/
	.global-head .search-form .form-control
	{
		background: #4A4F52 !important;
	}

	.global-head .global-head-container, .global-head .search-form
	{
		background: #161B1E !important;
		color: #dcdcdc !important;
	}

/*Buttons*/
	.btn-default, .input-sm
	{
		background: #3A4040 !important;
		color: #b8baba !important;
	}
	
	.btn-group-select-page
	{
		margin: -5px 0;
		background: transparent !important;
	}
	
	.btn-group .selected
	{
		background: #272727 !important;
	}
	
/*Cases dropdown*/
	.dropdown-menu
	{
		background-color: #3c3c3c !important;
	}
	
	.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus
	{
		background-color: #5f5f5f !important;
	}
	
	.dropdown-menu>li>a
	{
		background-color: #3c3c3c !important;
	}	
	
/*Nav Bar*/
	.filters-nav li a
	{
		background: #3A4040 !important;
		color: #b8baba !important;
		background-color: #313131 !important;
	}
	
	.chosen-container-single .chosen-single
	{
		background: #3A4040 !important;
		color: #b8baba !important;
	}
	
	.filter-section
	{
		background: #3A4040 !important;
		border-bottom: 1px solid #151b1e !important;
		color: #00ff00 !important;
	}

	.filter-block 
	{
		color: #b8baba !important;
	}
	
	.filters-nav li a:hover
	{
		background: #4a4a4a !important;
	}
	
	
/*Case Number*/
	.black
	{
		color: #a3a3a3;
	}
/*Case title*/
	.list-1 li .item-title a
	{
		color: #d3d3d3 !important;
	}
	
/*Customer name*/
	.label-customer 
	{
		color: #b8baba !important;
	}
	
/*Case Details */
	.list-1 li .item-details, .list-1 li .item-details div
	{
		color: #a3a3a3 !important;
		background: transparent !important;
	}
/*Case Details far right*/
	.item-details-farright
	{
		background: transparent !important;
	}
	
/*Case priority*/

	.list-1, .item-details-right
	{
		/*background: transparent !important;*/
		background: #3c3c3c !important
	}
	
	.list-1 li.delayedMedium
	{
		background-color: #2b2b2b !important;
	}
	
	.list-1 li.delayedHigh
	{
		background-color: #151b1e !important;
	}

/*Case Hover*/
	.list-1 li:hover
	{
    background: rgba(115, 115, 115, 0.67)
	}
	
	.list-1 li.delayedMedium

/*Case Status*/
	.label-status-closed
	{
		background: #333333 !important;
		color: #BDBDBD !important;
	}
	
	.label-status-rip
	{
		background: #333333 !important;
		color: #CC0099 !important;
	}
	
	.label-status-req
	{
		background: #333333 !important;
		color: #D1B2D1 !important;
	}
	
	.label-status-new
	{
		background: #333333 !important;
		color: #0066FF !important;
	}
	
	.label-top-priority
	{
		background: #333333 !important;
		color: #d71418 !important;
	}
	
	.label-high-priority
	{
		background: #333333 !important;
		color: #eb6420 !important;
	}
	
	.label-medium-priority
	{
		background: #333333 !important;
		color: #fbca04 !important;
	}
	
	.label-low-priority
	{
		background: #333333 !important;
		color: #078e07 !important;
	}
	
	.label-question
	{
		background: #333333 !important;
		color: #84b6eb !important;
	}
	
	.label-bug
	{
		background: #333333 !important;
		color: #e32727 !important;
	}
	
	.label-status-onhold
	{
    background: #333333 !important;
	color: #8EAEBD !important;
	}
		
	.label-success
	{
		background: #333333 !important;
		color: #5cb85c;
	}
	

	
/******************Cases******************/
	
/*Claim Case button*/
	.btn-primary
	{
		color: #cccccc !important;
		background-color: #3b4041 !important;
		border-color: #cccccc !important;
	}

/*Alert*/
	.alert-warning
	{
		background-color: #6f6f6f !important;
	}
	
/*Boxes*/
	.box-1
	{
		border: 1px solid #000000 !important;
	}
	
	.box-1 .box-1-head
	{
		border-bottom: 1px solid #000000 !important;
		background: #5a5a5a !important;
	}
	
	div[data-role="editor-container"]
	{
		border: 1px solid #000000 !important;
		/*background: #6d6d6d !important;*/
	}
	
	div.child
	{
		background: #00ff00 !important;
	}
	
	hr
	{
		border-top: 1px solid #000000
	}
	
	.list-2>li
	{
		border-bottom: 1px solid #000000 !important;
	}
	
	.list-3 li.active a i
	{
		color: #000000 !important;
	}
	
	.col-lg-5, .col-xs-4, .col-xs-1, .col-xs-3, .col-xs-9
	{
		background-color: transparent !important;
	}
	
/*New text box focas border color*/	
	div[data-role="editor-container"]:focus
	{
		border-color: none !important;
		box-shadow: inset 0 1px 1px #c5c5c5 !important;	}
		/*box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(0, 255, 93, 0.88) !important;*/

/*System Info*/
	.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td
	{
		border-color: #000000 !important;
		background-color: #565656 !important;
	}
	
	.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th
	{
		background-color: #696969 !important;
	}
	
	.table
	{
		color: #dcdcdc !important;
	}
	
	.editable-empty, .editable-empty:hover, .editable-empty:focus
	{
		color: #ffffff !important;
	}
	
	.editable-click, a.editable-click, a.editable-click:hover
	{
		border-bottom: dashed 1px #000000 !important;
	}

/*Hovering over stuff*/
	.list-2>li:hover
	{
		background: #5a5a5a !important;
	}
	
	.list-2.bridge-admins>li:hover
	{
		background: #5a5a5a !important;
	}

/*Add Contact box*/
	.col-xs-10
	{
		color: #000000 !important;
	}
	

/*pencil icon*/
	.fa-pencil:before
	{
		color: #a2a2a2 !important;
	}
	
/*Customer Name*/	
	a
	{
		color: #dcdcd7 !important;
	}

/*Text box toolbar*/
	div[data-role="editor-toolbar"] .btn-group>.btn
	{
		background: #3a3a3a !important;
	}
	
	div[data-role="editor-toolbar"] .btn-group>.btn.active
	{
		background: #151b1e !important;
	}

	
	div:empty:not(:focus):before
	{
		color: #dadada !important;
	}

/*Resolution box*/
		.form-control
		{
			background-color: #797979 !important;
			color: #dadada !important;
			border: 1px solid #000 !important;
		}

/*Code Box*/
	pre
	{
		color: #333 !important;
		background-color: #656565 !important;
		border: #000 !important;
		
	}

/*Cal icon background in rep time*/
	.input-group-addon
	{
		background-color: #797979 !important;
		border: 1px solid #000 !important;
	}

/*Rep time*/

	.section-header
	{
		border-bottom: 1px solid #000 !important;
	}

	.chosen-container-single .chosen-single
	{
		border: 1px solid #000  !important;
		background-color: #797979 !important;
		color: #dcdcdc !important;
	}
	
	.alert-info
	{
		    border-color: #000000 !important;
	}
	
	.chosen-container .chosen-results li.highlighted
	{
		background-image: none !important;
		background-color: #a2a2a2 !important;
	}
	
	.btn
	{
		background: #3c4042 !important;
	}
	
	.table-bordered
	{
		border: 1px solid #000 !important;
	}

/*Close case*/

	.fuelux .wizard
	{
		border: 1px solid #000000 !important;
	}
	
	.fuelux .step-content
	{
		border: None !important
	}
	
	.fuelux .wizard ul li
	{
		color: #dcdcdc !important;
		background: #252525 !important;
	}
	
	.fuelux .wizard ul li.active .chevron:before
	{
		border-left: 14px solid #a9a9a9 !important;
	}
	
	.fuelux .wizard ul li .chevron:before
	{
		border-left: 14px solid #505050 !important;
	}
	
	.fuelux .label-info, .fuelux .badge-info
	{
		background-color: #3e3e3e !important;
	}

	
}
