css:
@-moz-document domain("www.resetera.com") {
.breadBoxTop, .breadBoxBottom {
    padding: 16px 0;
}

#navigation .pageContent {
    height: auto;
}

#headerMover #headerProxy {
    height: 142px;
}

.navTabs {
    box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.1);
}

.navTab .accountPopup img {
    width: 28px;
    height: 28px;
    margin-top: 8px;
}

.navTabs .navTab.PopupClosed:hover {
    background-color: transparent;
}

#QuickSearch .secondaryControls {
	position: absolute;
    top: 35px;
    left: -1px;
    width: 252px;
    box-sizing: border-box;
    background-color: rgb(255, 255, 255);
    border: 1px solid rgb(127, 81, 186);
    border-top: 0;
    padding: 0;
}

#QuickSearch.active {
    box-shadow: 0px 0px 6px rgba(0,0,0, 0.2);
    padding-bottom: 0;
    border-bottom: 0;
}

.formPopup .secondaryControls .controlsWrapper {
    margin-top: 0;
    padding: 8px;
    background: #ededed;
}

.formPopup .secondaryControls .ctrlUnit.submitUnit {
    box-sizing: border-box;
    padding: 4px 8px;
}

#QuickSearch::after {
    height: 35px;
}

.breadcrumb {
    padding: 3px 0;
    border-radius: 4px;
}

.breadcrumb .crust a.crumb {
    border-bottom: 0;
}

.breadcrumb .crust .arrow {
    color: rgb(205, 205, 205);
    top: -1px;
}

a.CatTrigger, a.OverlayTrigger.jumpMenuTrigger {
    display: none;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    background-color: rgb(51, 36, 71);
}

.sidebar {
    background-color: #ededed;
}

.nodeList > li.category:last-child {
    border-bottom: 0;
}

.nodeList>.node:last-child>.forumNodeInfo {
    border-bottom: 0;
}

.sidebar .section {
    margin: 42px auto 16px auto;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    margin: 0;
}

.secondaryContent.statsList {
    background-color: rgb(255,255,255);
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    padding: 0;
}

.statsList .pairsJustified {
    padding: 10px;
    border-bottom: 1px solid #E4E4E4;
    border-left: 1px solid #E4E4E4;
    border-right: 1px solid #E4E4E4;
}

.button {
    color: rgb(255, 255, 255);
    background-color: rgb(127, 81, 186);
    padding: 1px 2px;
    border: 0;
}

.button.primary {
    color: rgb(255, 255, 255);
    background-color: rgb(127, 81, 186);
    border: 0;
}

.button:hover, .button[href]:hover, .buttonProxy:hover .button {
    color: rgb(255, 255, 255);
    background-color: rgb(104, 60, 160);
    padding: 0;
    margin: 0;
}

.node .nodeIcon {
    margin-top: 16px;
    margin-bottom: 4px;
    margin-left: 12px;
}

.node .nodeText {
    margin: 9px 360px 5px 61px;
}

.node .subForumList {
    margin-left: 61px;
    margin-bottom: 9px;
}

@media (max-width: 610px) {
	.Responsive .node .nodeLastPost {
    	margin: -3px 0 5px 61px;
    }
}

.nodeList .node.level_1 {
    margin-bottom: 16px;
}

.LoggedIn .discussionListItem .unreadLink, .LoggedIn .discussionListItem .ReadToggle {
    left: 16px;
    top: 11px;
}

.discussionListItem .titleText {
    padding: 8px 16px 10px 16px;
}

.LoggedIn .discussionListItem .titleText {
    padding-left: 39px;
}

#AjaxProgress.xenOverlay .content {
    background: rgb(127, 81, 186);
    color: rgb(255, 255, 255);
    display: none;
}

.discussionListItem .title {
    font-size: 1.05em;
}

.discussionListItem {
    border-bottom: 1px solid rgb(237, 237, 237);
    box-sizing: border-box;
}

.discussionListItem .stats dl {
    border: 0;
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: rgb(250, 250, 250);
}

.discussionList .sectionFooter {
    border-right: 1px solid rgb(228, 228, 228);
    border-bottom: 1px solid rgb(228, 228, 228);
    border-left: 1px solid rgb(228, 228, 228);
    background-color: rgb(248, 248, 248);
    padding: 6px 10px;
}

.discussionListItem .stats .major {
    font-size: 1em;
    margin-top: 0;
}

.discussionListItem .lastPostInfo .username {
    font-size: 1em;
}

.discussionListItem .stats .minor {
    margin-bottom: 0;
}

.discussionListItem .stats dl {
    padding: 0 16px;
}

.discussionListItem .lastPostInfo {
    padding: 0 16px;
}

.Responsive .discussionList .lastPost .lastPostInfo dt {
    overflow: hidden;
    text-overflow: ellipsis;
}

.discussionListItem .iconKey span {
    margin-top: 6px;
}

.discussionList .lastPost {
    width: 162px;
}

.PageNav a {
    background-color: rgb(103,103,103);
    border: 1px solid rgb(103,103,103);
}

.messageList li.message:nth-child(even), .messageList li.message:nth-child(even) .messageUserInfo {
    background-color: rgb(248, 248, 248);
}

.messageList .message {
    border-bottom: 1px solid rgb(228, 228, 228);
}

.thread_view .titleBar {
    text-align: left;
}

.titleBar {
    margin-bottom: 16px;
}
}
