@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mastodon.social") {
  
  body {
    color: #000000;
  }

  .column-header__title {
    background-color: #eeeeee;
    
    flex: unset;
    display: inline-block;
    margin-left: auto;
    margin-right: auto;
    padding-left: 6px;
    padding-right: 6px;
  }
  
  .column-header {
    border-style: solid;
    border-width: 2px 1px 4px;
    border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAMAAAAICAIAAACzjdIrAAAAOElEQVQI11XGMQ0AIAxFwdcGI/VaTcjo+iWw1gFMhHDTWdUGJDkQ0YADmQlY1Y7oOZdzvQ1JEp8Df/ESQFbO3JsAAAAASUVORK5CYII=) 2 1 4 fill repeat;
    font-family: "ChicagoFLF";
    
    padding: 0px;
  }
  
  .column-header__icon {
    color: #a4a4a4;
    background-color: #ccccff;
    text-shadow: 1px 1px #333366;
    box-shadow: 1px 1px #333366 inset;
    border: 1px solid #eeeeee;
    padding: 1px;
    margin-left: 6px;
  }
  
  .column-header__buttons {
    display: inline-block;
    height: inherit;
    margin: 0;
    margin-right: 6px;
  }
  
  .column-header__button {
    color: #a4a4a4;
    background-color: #ccccff;
    text-shadow: 1px 1px #333366;
    box-shadow: 1px 1px #333366 inset;
    border: 1px solid #eeeeee;
    padding: 1px;
    width: 27px;
  }
  
  .column-header__button:focus, .column-header__button:hover {
    color: #a4a4a4;
    background-color: #ccccff;
  }
  
  .column-header__button.active {
    background-color: #9292b6;
    color: #000000;
    text-shadow: none;
    box-shadow: none;
  }
  
  .column-header__button.active:focus, .column-header__button.active:hover {
    background-color: #9292b6;
    color: #000000;
  }
  
  .columns-area {
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAAAAABX3VL4AAAADklEQVQIHWNIWMCwIAEABgYCAdjZFREAAAAASUVORK5CYII=);
  }
  
  .column, .drawer {
    box-shadow: 1px 1px #000000;
    padding: 0;
    margin: 10px;
    margin-right: 0;
    border: 1px solid #000000;
  }
  
  .column:first-child, .drawer:first-child {
    padding: 0;
  }
  
  .column > .scrollable {
    background-color: #ffffff;
  }
  
  .empty-column-indicator, .error-column {
    background-color: #ffffff;
  }
  
  .status__display-name strong, .account__display-name strong {
    color: #000000;
  }
  
  .icon-button {
    color: #000000;
  }
  
  .dropdown-menu {
    box-shadow: none;
  }
  
  .dropdown-menu, .boost-modal, .confirmation-modal, .report-modal, .actions-modal, .onboarding-modal, .error-modal, .embed-modal {
    background: #ffffff;
    border-style: solid;
    border-width: 5px;
    border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAALBAMAAABbgmoVAAAAD1BMVEUAAAB6epm7u7vMzP////9X61NRAAAAL0lEQVQI12NggABmY2NjQyClpKSkCKQUBQXBFAgBMQszMs/YWBGuklEQCKBmMAAAqLkEFYOuOAoAAAAASUVORK5CYII=) 5 repeat;
  }
  
  .dropdown-menu__item a {
    color: #000000;
    background-color: #ffffff;
  }
  
  .dropdown-menu__item a:focus, .dropdown-menu__item a:hover, .dropdown-menu__item a:active {
    background-color: #000000;
    color: #ffffff;
  }
  
  .boost-modal__action-bar, .confirmation-modal__action-bar, .report-modal__action-bar {
    background-color: #ffffff;
  }
  
  .button {
    background-color: transparent;
    color: #000000;
    border-style: solid;
    border-width: 7px;
    border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPAgMAAABGuH3ZAAAACVBMVEUAAGcAAAD////NzL25AAAAAXRSTlMAQObYZgAAADxJREFUCNdjYBANYGBgDA1lYGANDXVgEI2aGsAgunJlAEPotMgQhrBZs4BE1jIoCywGlgWrA+sA6wWZAgC8TRK7q69pNgAAAABJRU5ErkJggg==) 7 fill repeat;
    text-transform: none;
    border-radius: 0;
    height: 28px !important;
    font-family: "ChicagoFLF";
    font-size: 12px;
    font-weight: normal;
    line-height: 0 !important;
  }
  
  .button:hover, .button:focus {
    background-color: transparent;
    color: #000000;
  }
  
  .button:active {
    border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPAgMAAABGuH3ZAAAACVBMVEUAAGcAAAD////NzL25AAAAAXRSTlMAQObYZgAAADxJREFUCNdjYBANYGBgDA1lYGANDXVgEI2aGsAgunJlAEPo1MgQhrCpUUAiNAzKAouBZcHqwDrAekGmAACZVxHKelZ4gAAAAABJRU5ErkJggg==) 7 fill repeat;
    color: #ffffff;
  }
  
  .confirmation-modal__action-bar .confirmation-modal__cancel-button {
    background-color: #ffffff;
    color: #000000;
    border: 1px solid #000000;
    border-radius: 4px;
    font-size: 12px;
    font-weight: normal;
    height: 20px !important;
    margin-top: 4px;
  }
  
  .confirmation-modal__action-bar .confirmation-modal__cancel-button:hover,
  .confirmation-modal__action-bar .confirmation-modal__cancel-button:focus {
    background-color: #ffffff;
    color: #000000;
  }
  
  .confirmation-modal__action-bar .confirmation-modal__cancel-button:active {
    background-color: #000000;
    color: #ffffff;
  }
  
  .drawer__header {
    background-color: #eeeeee;
  }
  
  .drawer {
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAIAgMAAAC5YVYYAAAACVBMVEXMzMzd3d3u7u7U7IfxAAAAGklEQVQIHWNIUGAQYGBgYmIAUgoJDIwQNiMAEwUBK9iYnP8AAAAASUVORK5CYII=);
    border: 1px solid white;
  }
  
  .drawer__inner {
    background: none;
  }
  
  .search__input {
    color: #000000;
    background-color: #ffffff;
    border-style: solid;
    border-width: 2px;
    border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAIAAAACDbGyAAAAJUlEQVQI12Ps6OhgQAbI/P///zPBWRAGlM/IyAhhsCBLMjAwAAAO1ww0vbrxggAAAABJRU5ErkJggg==) 2 repeat;
  }
  
  .search {
    margin: 4px;
  }
  
  .autosuggest-textarea__textarea, .spoiler-input__input {
    color: #000000;
    border-radius: 0;
    border: 1px solid black;
  }
  
  .autosuggest-textarea__textarea {
    border-bottom: none;
  }
  
  .compose-form__modifiers {
    border-radius: 0;
    border: 1px solid black;
    border-top: none;
    border-style: solid;
    border-width: 0px 24px 25px 1px;
    border-image: url(data:image/png;base64,) 0 24 25 1 fill repeat;
    background: none;
  }
  
  .drawer__tab {
    color: #a4a4a4;
  }
  
  .drawer__header a:hover {
    background-color: #33337f;
    color: #000000;
  }
  
  .column-subheading {
    background-color: #eeeeee;
    color: #000000;
  }
  
  .column-link, .column-link:hover {
    background-color: #ffffff;
    color: #000000;
  }
  
  .loading-bar {
    background-color: #404040;
    height: 9px;
    border: 1px solid black;
    z-index: 1;
  }
  
  .column-header__back-button, .column-back-button {
    background-color: #eeeeee;
    color: #000000;
  }
  
  .detailed-status {
    background: #ffffff;
  }
  
  .detailed-status__action-bar {
    background: #ffffff;
    border: 0;
    border-bottom: 1px solid black;
  }
  
  .navigation-bar {
    color: #808080;
  }
  
  .navigation-bar strong {
    color: #000000;
  }
}
