css:
@-moz-document domain("www.allthingsweezer.com") {
html {
    background-color: rgb(30, 30, 30);
    color: rgb(200, 200, 200);
}

body,
.searchResult .snippet a {
    color: rgb(200, 200, 200);
}

#content .pageContent {
    background-color: rgb(20, 20, 20);
}

.breadcrumb,
.breadcrumb .crust {
    background-color: rgb(40, 40, 40);
    color: rgb(200, 200, 200);
    border-color: rgb(30, 30, 30);
    border-radius: 6px;
}

.breadcrumb .crust a.crumb {
    background-color: rgb(40, 40, 40);
    color: rgb(200, 200, 200);
    border-color: rgb(30, 30, 30);
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
    text-transform: lowercase;
}

.breadcrumb .crust:hover a.crumb {
    background-color: rgb(70, 70, 70);
    border-color: rgb(30, 30, 30);
}

.breadcrumb .crust .arrow {
    border-left-color: rgb(30, 30, 30);
}

.breadcrumb .crust:hover .arrow {
    border-left-color: rgb(70, 70, 70);
}

.breadcrumb .crust .arrow span {
    border-left-color: rgb(40, 40, 40);
}

.breadcrumb .crust:hover .arrow span {
    border-left-color: rgb(70, 70, 70);
}

.primaryContent {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

.primaryContent a {
    color: rgb(200, 200, 200);
}

.secondaryContent {
    background-color: rgb(40, 40, 40);
    border-bottom-color: rgb(20, 20, 20);
}

.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    background-color: rgb(40, 40, 40);
    border-bottom-color: rgb(20, 20, 20);
}

.threadListItem {
    border-top-color: rgb(20, 20, 20);
}

.profilePostListItem {
    border-top-color: rgb(20, 20, 20);
}

.discussionListItem {
    background-color: rgb(40, 40, 40);
    border-bottom-color: rgb(20, 20, 20);
}

.navPopup .listItem {
    border-bottom-color: rgb(20, 20, 20);
}

a:link,
a:visited {
    color: rgb(200, 200, 200);
}

.PageNav a,
.quickReply {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

.sectionFooter,
.Menu .sectionFooter {
    background-color: rgb(30, 30, 30);
    border-color: rgb(25, 25, 25);
}

.discussionListItem .stats dl {
    border-left-color: rgb(20, 20, 20);
    border-right-color: rgb(20, 20, 20);
}

.pairs dt,
.pairsInline dt,
.pairsRows dt,
.pairsColumns dt,
.pairsJustified dt {
    color: rgb(153, 153, 153);
}

.avatar img,
.avatar .img,
.avatarCropper {
    background-color: rgb(30, 30, 30);
    border-color: rgb(25, 25, 25);
}

#DiscussionListOptionsHandle a {
    background-color: rgb(30, 30, 30);
    border-color: rgb(25, 25, 25);
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
    text-transform: lowercase;
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
}

.messageList .message {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

.message .likesSummary {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
    border-radius: 4px;
}

.messageUserBlock,
.messageUserBlock div.avatarHolder,
.messageUserBlock h3.userText,
.messageUserBlock .extraUserInfo {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
}

.pollBlock .questionMark {
    background: rgb(30, 30, 30);
    color: rgb(200, 200, 200);
}

.pollBlock .question .questionText {
    color: rgb(153, 153, 153);
}

body .itemPageNav a {
    background-color: rgb(30, 30, 30);
    color: rgb(200, 200, 200) !important;
    border-color: rgb(20, 20, 20);
}

body .itemPageNav a:hover {
    background-color: rgb(70, 70, 70);
    color: rgb(200, 200, 200) !important;
    border-color: rgb(20, 20, 20);
}

.dark_postrating_outputlist li strong,
.dark_postrating_thread_rating strong {
    color: rgb(153, 153, 153);
}

.bbCodeQuote .quoteContainer {
    background-color: rgb(30, 30, 30);
}

.bbCodeBlock .type {
    background-color: rgb(50, 50, 50);
    border-color: rgb(20, 20, 20);
}

.bbCodeBlock {
    border-color: rgb(20, 20, 20);
}

.messageUserBlock .arrow {
    border-left-color: rgb(20, 20, 20);
    -moz-border-left-colors: rgb(20, 20, 20);
}

.messageUserBlock .arrow span {
    border-left-color: rgb(30, 30, 30);
    -moz-border-left-colors: rgb(30, 30, 30);
}

.Menu,
.Menu .primaryContent {
    background-color: rgb(40, 40, 40);
    border-color: rgb(20, 20, 20);
}

.Menu .secondaryContent {
    background-color: rgb(40, 40, 40);
}

.secondaryContent a {
    color: rgb(196, 84, 84);
}

.blockLinksList a,
.blockLinksList label {
    color: rgb(200, 200, 200);
}

.bbCodeQuote .quoteContainer .quoteExpand.quoteCut {
    background: linear-gradient(to bottom, rgba(30, 30, 30, 0.69) 0%, rgba(15, 15, 15, 0.88) 52%, rgba(15, 15, 15, 1) 100%);
}

.blockLinksList a:hover,
.blockLinksList a:focus,
.blockLinksList li.kbSelect a,
.blockLinksList label:hover,
.blockLinksList label:focus,
.blockLinksList li.kbSelect label,
.bubbleLinksList a:hover,
.bubbleLinksList a:active,
.navPopup .PopupItemLinkActive:hover {
    background-color: rgb(70, 70, 70);
}

.previewTooltip .posterDate {
    border-top-color: rgb(20, 20, 20);
}

.xenPreviewTooltip .arrow span {
    border-top-color: rgb(40, 40, 40);
}

.sidebar .userList .username.invisible {
    color: rgb(88, 88, 88);
}

body .muted,
body a.muted,
body .muted a {
    color: rgb(88, 88, 88);
}

.dark_postrating_member,
.profilePage .mast .section.infoBlock,
.profilePage .mast .followBlocks .primaryContent.avatarHeap {
    border-color: rgb(30, 30, 30);
    color: rgb(200, 200, 200);
}

.profilePage .tabs.mainTabs {
    background-color: rgb(40, 40, 40);
}

table,
.profilePage .mast {
    border-color: rgb(30, 30, 30);
}

.formPopup .controlsWrapper {
    background-color: rgb(40, 40, 40);
}

.textCtrl {
    background-color: rgb(20, 20, 20);
    color: rgb(200, 200, 200);
    border-color: rgb(40, 40, 40);
}

.textCtrl:focus,
.textCtrl.Focus {
    background-color: rgb(70, 70, 70);
    border-color: rgb(40, 40, 40);
}

.dark_postrating_member td {
    border-color: rgb(20, 20, 20);
}

.dark_postrating_bar {
    border-color: rgb(40, 40, 40);
}

.messageUserBlock .userBanner {
    background-color: rgb(196, 84, 84);
    border-color: rgb(126, 34, 34);
    color: rgb(255, 255, 255);
}

.userBanner.wrapped span.after,
.userBanner.wrapped span.before {
    background-color: rgb(126, 34, 34);
}

.prefix.prefixPrimary {
    color: rgb(200, 200, 200);
    background-color: rgb(20, 20, 20);
    border-color: rgb(70, 70, 70);
}

html .conversation_view .message:first-child,
html .conversation_view .quickReply {
    border-color: rgb(20, 20, 20);
}

.profilePage .primaryUserBlock .lastActivity {
    border-color: rgb(20, 20, 20);
}

.tabs {
    background-color: rgb(40, 40, 40);
    border-bottom: 1px solid rgb(40, 40, 40);
}

.tabs li a,
.tabs.noLinks li {
    background-color: rgb(60, 60, 60);
    border-color: rgb(35, 35, 35);
    color: rgb(200, 200, 200);
}

.profilePage .tabs.mainTabs li.active a {
    background-color: rgb(20, 20, 20);
}

.profilePage .tabs.mainTabs li a:hover {
    background-color: rgb(90, 90, 90);
}

.textHeading,
.xenForm .sectionHeader {
    color: rgb(200, 200, 200);
    border-color: rgb(70, 70, 70);
}

.profilePage .primaryUserBlock {
    border-color: rgb(20, 20, 20);
}

.bestAnswersList {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

.messageList .message.bestAnswer {
    background-color: rgb(52, 79, 62);
}

.inlineBestAnswerBlock .quoteContainer {
    background-color: rgb(52, 79, 62);
}

.bbCodeQuote .quoteContainer .bestAnswerOriginalMessage {
    background: linear-gradient(to bottom, rgba(52, 79, 62, 0) 0%, rgb(0, 0, 0) 80%);
}

.ugc a:hover,
.ugc a:focus {
    color: rgb(200, 200, 200);
    background-color: rgb(20, 20, 20);
}

.emCtrl:hover,
.emCtrl:focus,
.ugc a:hover,
.ugc a:focus {
    box-shadow: 5px 5px 7px #000;
}

.emCtrl:active,
.ugc a:active {
    box-shadow: 2px 2px 7px #000;
}

#calroot {
    background-color: rgb(0, 0, 0);
    box-shadow: 0 0 15px #000;
    border-color: #000;
}

#caltitle {
    color: rgb(200, 200, 200);
}

.dark_postrating_detail {
    background-color: rgb(40, 40, 40);
}

.dark_postrating_column {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
}

.dark_postrating_header {
    color: rgb(200, 200, 200);
    border-bottom: 1px dotted rgb(70, 70, 70);
}

.navPopup .listPlaceholder ol.secondaryContent.Unread {
    background-color: rgb(94, 43, 43);
}

#StackAlerts .stackAlertContent {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

ul.autoCompleteList {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

ul.autoCompleteList li:hover,
ul.autoCompleteList li.selected {
    background-color: rgb(70, 70, 70);
}

.profilePage .mast .sectionFooter {
    border-color: rgb(20, 20, 20);
}

a.PageNavPrev,
a.PageNavNext {
    color: rgb(200, 200, 200);
    background-color: rgb(20, 20, 20);
}

.xenForm fieldset + .ctrlUnit,
.xenForm .formGroup + .ctrlUnit,
.xenForm .submitUnit,
.xenForm fieldset,
.xenForm .formGroup {
    border-color: rgb(40, 40, 40);
}

.hasJs .bbCodeSpoilerText {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
}

.bbCodeBlock pre,
.bbCodeBlock .code {
    background-color: rgb(30, 30, 30);
}

body .muted,
body a.muted,
body .muted a {
    color: rgb(108, 108, 108);
}

.pollBlock .pollOption label:hover {
    background: rgb(30, 30, 30);
}

.DiscussionListOptions {
    border-color: rgb(20, 20, 20);
}

#jumpMenu .blockLinksList ul,
#jumpMenu .nodeList li.d0,
.xenOverlay #jumpMenu .nodeList {
    border-color: rgb(20, 20, 20);
}

.tagList .tag {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
    color: rgb(200, 200, 200);
}

.tagList .tag .arrow {
    border-right-color: rgb(30, 30, 30);
}

.tagList .tag:after {
    background-color: rgb(20, 20, 20);
    border-color: rgb(30, 30, 30);
}

.tagList .tag .arrow:after {
    border-right-color: rgb(40, 40, 40);
}

.pollBlock .pollOptions,
.pollBlock .pollResults {
    border-color: rgb(30, 30, 30);
}

.pollBlock .buttons .button {
    color: rgb(200, 200, 200);
}

.redactor_toolbar {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
}

.redactor_box {
    border-color: rgb(20, 20, 20);
}

.redactor_toolbar li.redactor_btn_group ul {
    background-color: rgb(90, 90, 90);
    border-color: rgb(70, 70, 70);
    box-shadow: 1px 1px 1px rgba(0, 0, 0, .5)
}

.redactor_toolbar li a:hover {
    background-color: rgb(120, 120, 120);
    border-color: rgb(90, 90, 90);
}

.thread_view .threadAlerts {
    border-color: rgb(30, 30, 30);
}

.thread_view .threadAlerts dd {
    color: rgb(153, 153, 153);
}

.messageList .newMessagesNotice {
    background-color: rgb(40, 40, 40);
    border-color: rgb(30, 30, 30);
}

.messageNotices li {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
}

.discussionList .sectionHeaders {
    text-transform: lowercase;
    font-size: 12px;
    padding-top: 5px;
    padding-bottom: 5px;
    margin-top: 5px;
    background-color: rgb(30, 30, 30);
    color: rgb(196, 84, 84);
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}

.discussionList .sectionHeaders a span {
    padding-top: 7px;
    padding-bottom: 5px;
    color: rgb(196, 84, 84);
}

.breadcrumb .jumpMenuTrigger {
    color: rgb(200, 200, 200);
}

.prefixListPrefixes {
    background-color: rgb(20, 20, 20);
}

.bbCode>dl>dd {
    background-color: rgb(30, 30, 30);
    border-color: rgb(20, 20, 20);
}

.navigationSideBar a:hover {
    background-color: rgb(90, 90, 90);
    color: rgb(200, 200, 200);
}

.nodeList .categoryStrip {
    background-color: rgb(30, 30, 30);
}

.nodeList .categoryStrip .nodeTitle,
.nodeList .categoryStrip .nodeTitle a {
    color: rgb(196, 84, 84);
    text-transform: lowercase;
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
}

.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    background-color: rgb(30, 30, 30);
    color: rgb(200, 200, 200);
}

input.textCtrl.disabled,
textarea.textCtrl.disabled,
.disabled .textCtrl {
    background-color: rgb(120, 120, 120);
}

input.button.disabled,
a.button.disabled,
input.button.primary.disabled,
a.button.primary.disabled,
html .buttonProxy .button.disabled {
    background-color: rgb(20, 20, 20);
}

.Menu.tabMenu:before {
    border-bottom-color: rgb(20, 20, 20);
}

.prefix.prefixYellow {
    background-color: #edc500;
    border-color: #967d00;
}

.prefix.prefixRed {
    background-color: #912121;
    border-color: #ff0000;
}

.prefix.prefixBlue {
    background-color: #1a4ca8;
    border-color: #2b75ff;
}

.tabs li.active a,
.tabs.noLinks li.active {
    background-color: rgb(20, 20, 20);
}

.tabs li:hover a,
.tabs.noLinks li:hover {
    background-color: rgb(90, 90, 90);
}

.subHeading {
    background-color: rgb(30, 30, 30);
}

.subHeading a {
    color: rgb(196, 84, 84);
}

.navTabs {
    background-color: rgb(30, 30, 30);
    border-color: rgb(30, 30, 30);
}

.navTabs .navTab.selected .tabLinks,
.navTabs .navTab.selected .navLink,
.footer .pageContent {
    background-color: rgb(40, 40, 40);
}

.Responsive .navTabs .navTab.PopupClosed .navLink,
.navTabs .navTab .SplitCtrl .fa,
.navTabs .navTab.selected .tabLinks a,
.navTabs .navTab.selected .navLink {
    color: rgb(200, 200, 200);
}

.errorPanel {
    background-color: rgb(126, 34, 34);
    border-color: rgb(196, 84, 84);
    color: rgb(200, 200, 200);
}

.pairs dt,
.pairsInline dt,
.pairsRows dt,
.pairsColumns dt,
.pairsJustified dt {
    text-transform: lowercase;
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
}

.titleBar h1 {
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
    color: rgb(200, 200, 200);
}

.authorEnd {
    display: none;
}

@media only screen and (max-width: 414px) {
    .discussionListItem .posterAvatar .avatar img, .discussionList .posterAvatar {
        display: none;
        width: 0px;
        height: 0px;
    }
}

.navTabs .publicTabs .navLink,
.sectionFooter,
.PageNav,
.pageNavLinkGroup,
a.callToAction span,
.navTabs .navTab.selected .tabLinks a,
.sidebar .section .secondaryContent h3,
.sidebar .section .secondaryContent h3 a,
.message .publicControls,
.message .privateControls,
.footer .pageContent,
.heading,
.xenForm .formHeader,
.profilePage .primaryUserBlock .followBlock,
.messageSimple .messageMeta,
html .textWithCount.subHeading .text {
    text-transform: lowercase;
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
}

.navTabs .navTab.account .navLink .accountUsername,
.Menu .primaryContent h3,
.pairsJustified dd,
.profilePage .primaryUserBlock h1 {
    font-family: 'Century Gothic', 'Open Sans', sans-serif;
}

.nodeList .categoryStrip,
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.sidebar .section .secondaryContent,
.subHeading {
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}

a.callToAction span,
.button,
.xenOverlay .sectionFooter .button,
.xenOverlay .sectionFooter .buttonContainer,
.button.primary,
.submitUnit .button,
input.button,
input[type="button"] {
    border-radius: 6px;
}

.messageSimple .comment,
.messageSimple .secondaryContent {
    border-radius: 4px;
    background-color: rgb(30, 30, 30);
}

.PageNav a {
    border-radius: 4px;
}

.discussionList .sectionFooter {
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
}

.sidebar .visitorPanel .secondaryContent {
    background-color: rgb(20, 20, 20);
}

.sidebar .section .secondaryContent h3,
.sidebar .section .secondaryContent h3 a,
html .textWithCount.subHeading .text {
    color: rgb(200, 200, 200);
}

@media only screen and (min-width: 900px) {
    .pageWidth {
        max-width: 1280px;
    }
    #logo img {
        max-height: 307px;
    }
}

#logo {
    background: #141414;
    /* Old browsers */
    
    background: -moz-linear-gradient(top, #141414 0%, #191919 50%, #1e1e1e 100%);
    /* FF3.6-15 */
    
    background: -webkit-linear-gradient(top, #141414 0%, #191919 50%, #1e1e1e 100%);
    /* Chrome10-25,Safari5.1-6 */
    
    background: linear-gradient(to bottom, #141414 0%, #191919 50%, #1e1e1e 100%);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

#copyright .Tooltip:after {
    content: ". Dark theme modifications by guesswho."
}

.heading,
.xenForm .formHeader {
    font-weight: 400;
}

.profilePage .profileContent,
#ProfilePanes,
.messageSimple {
    background-color: rgb(40, 40, 40);
}

.textWithCount.subHeading .count {
    background-color: rgb(20, 20, 20);
    border-color: rgb(25, 25, 25);
    color: rgb(196, 84, 84);
}

.profilePage .mainProfileColumn {
    border-left-color: rgb(30, 30, 30);
}
}
