css:
@-moz-document domain("betaseries.com") {
/* Scrollbar */
::-webkit-scrollbar {
    width: 10px;
    border-radius: 0px;
    background-color: transparent;
}
::-webkit-scrollbar-thumb {
    border: 1px solid #0c1217;
    border-radius: 0;
    background-color: #233d52;
}
::-webkit-scrollbar-track {
    width: 10px;
    border-radius: 0px;
    background-color: #0c1217;
}
/* Header */
.theme-top_color {
    background-color: #182733;
}
.menu-icon path,
.menu-item path {
    fill: #5a7790;
}
.menu-icon:hover path,
.menu-item:hover path {
    fill: #fff;
}
.menu-icon--bell path:nth-child(1) {
    fill: transparent;
}
div[class$="show_searchResult"] > div[class^="theme-greyMedium"],
div[class$="movie_searchResult"] > div[class^="theme-greyMedium"],
div[class$="member_searchResult"] > div[class^="theme-greyMedium"] {
    color: #fff;
}
/* Notification */
.has-notifications path {
    fill: #cbd8e2;
}
.has-notifications:after {
    border: 1px solid #3b8dd0;
    background-color: #3b8dd0;
}
.notifications {
    background-color: #15232d;
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.4);
}
.notification:hover {
    background-color: #202f3a;
}
.notificationsWrapper:after {
    border-bottom-color: #15232d;
}
.notifications p {
    color: #fff;
}
.notification:not(.notification__push) + .notification {
    border-top: 1px #1c2b38 solid;
}
.notification.notification--push,
.notification.notification--delete-all {
    background-color: #1a2e40;
    border-top-color: #233d52;
}
.notification.notification--standalone {
    background-color: rgba(255, 255, 255, .9)!important;
}
.notification.notification--standalone p {
    color: #0D151C!important;
}
.notification.notification--unread {
    background: rgba(193, 225, 250, .15);
}
.notificationsTitle {
    color: #fff;
}
/* Couleurs des dates des épisodes */
.episode-date {
    color: #f1f1f1;
    text-align: center;
    height: 20px;
    line-height: 20px;
    font-size: 9pt;
    border-radius: 0.1875rem;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
    text-shadow: 2px 2px 3px rgba(0, 0, 0, .3);
    letter-spacing: 1px;
}
.episode-date[title^="lun."] {
    background-color: #9E2720;
}
.episode-date[title^="mar."] {
    background-color: #DB6C24;
}
.episode-date[title^="mer."] {
    background-color: #6A936D;
}
.episode-date[title^="jeu."] {
    background-color: #325CA2;
}
.episode-date[title^="ven."] {
    background-color: #5FA9CF;
}
.episode-date[title^="sam."] {
    background-color: #6A32A2;
}
.episode-date[title^="dim."] {
    background-color: #777777;
}
/* Couleur pour les épisodes spéciaux */
span.ep-special.tooltip-wrapper + button.btn-reset {
    color: #89a3e8!important;
}
/* boutons Vu/Archiver/etc... */
.episode-side .markas {
    background-color: #1a2e40;
}
.episode-side .markas:hover {
    background-color: #1e374e;
}
.episode-side .btn-dl {
    background-color: #334f67;
}
.episode-side .btn-dl:hover {
    background-color: #162427;
}
.episode-side .btn-dl,
.showItem .actions .record,
.actionButton,
.episode-side .btn-dl.dled {
    background-color: #0d151c;
}
.showItem .actions .delete:hover,
.showItem .actions .etags:hover,
.showItem .actions .fastforward:hover,
.showItem .actions .unarchive.unarchived,
.showItem .actions .unarchive:hover {
    background-color: rgb(52, 64, 74);
}
.showItem .actions .fastforward.ffed {
    background-color: #3b8dd0;
}
.showItem .actions .fastforward.ffed img {
    filter: invert(1);
}
.actionButton > a > img,
.notificationsWrapper .notification .btn-reset {
    filter: invert(.7);
}
/* Boîte de réception */
#contenu #right .messages .post .message-post-container {
    background-color: #1d2933;
}
/* Modal des sous-titres */
.episode .box.srt {
    box-shadow: 0px 0px 5px 0px #0d151c;
}
.episode .box.srt a:hover {
    color: #3b8dd0;
}
/* Bar de prograssion */
.episode-side .vu .percent,
#contenu #right #stats_container #details #progressbar #bar,
#contenu #right #competition .match .player .percent {
    background-color: #3b8dd0;
    border-radius: 4px;
    background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.05));
    background-image: -moz-linear-gradient(top, rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.05));
    background-image: -o-linear-gradient(top, rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.05));
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.05));
    -webkit-box-shadow: 0 0 1px 1px rgba(0, 0, 0, 0.25), inset 0 1px rgba(255, 255, 255, 0.1);
    box-shadow: 0 0 1px 1px rgba(0, 0, 0, 0.25), inset 0 1px rgba(255, 255, 255, 0.1);
}
/* Gestion des séries */
.showItem .completion {
    background-color: #67696b;
}
.showItem .completion.green,
.tagsList .tags span.add {
    background-color: #72885d;
}
.showItem .completion.red {
    background-color: #9c5e5e;
}
.episodes_list .item_season a {
    color: #e6e6e6;
}
.episodes_list .item_episode {
    background-color: #101519;
}
.episodes_list .item_episode.seen {
    background-color: #141a1f;
}
/* Bouton Saison dans la liste des épisodes d'une série */
.button-action.sss_toggle.hl,
.button-action.ss_toggle.hl {
    background-color: #3b8dd0;
    color: #fff;
}
/* Bouton annuler */
.undothings,
.undothings .content {
    background: #3886c7;
}
.undothings .title {
    border-right-color: #171f26;
    background: #3b8dd0;
}
.undothings .title a {
    color: #fff;
}
/* Profil des utilisateurs */
.fields.fields--cols > .field {
    box-shadow: inset 0 1px 2px #0d151c;
    background-color: #212930;
}
.field-icon {
    box-shadow: 0 1px 2px #0d151c;
    background-color: #212930!important;
}
.badge__illu {
    box-shadow: 0 1px 2px #0d151c;
    background-color: #212930;
}
.match .player.winner {
    background-color: #3a576f;
}
/* Quelques fix */
.maincontent h2,
.maincontent h3 {
    border: 1px solid #213342;
    border-radius: 3px;
    color: #a5bcd0;
    background-color: #182733;
}
#contenu #right #stats_container #details #progressbar {
    background-color: #212930;
    box-shadow: inset 0 1px 2px #212930;
}
#contenu #right .items_list p.none_item {
    color: #fff;
    background-color: #212930;
}
.episode {
    border-bottom-color: #1c232a;
}
.sidebar .singlemovie-affiche,
.col-movie .movie.border {
    background: #0d151c;
    border-color: #0d151c;
    box-shadow: 0 1px 2px #0c0f13;
}
.episode-titre {
    border-left-color: transparent;
}
#contenu #right .items_list p.none_item {
    box-shadow: 0 1px 2px #101519;
}
}
