css:
@-moz-document domain("imgur.com") {
.navPrev {
    background-color: #353535;
    width: 45px;
}
body, html {
    background-color: #424242;
}
#topbar .upload-button-container:hover {
    border: 1px solid rgba(27, 183, 110, 0.63);
    background: rgba(27, 183, 110, 0.83);
}
#topbar {
	box-shadow: 0px 4px 5px #212121;
    background-color: #212121;
    height: 60px;
}
#secondary-nav {
    top: 5px;
}
#topbar .account {
    top: 4px;
}
#topbar .upload-button-container {
    /* position: relative; */
    top: 4px;
}
#topbar .logo {
    margin-top: 10px;
}
#topbar .icon-arrow-down {
    padding: 13px 0 0 9px;
}
#footer, #footer-links {
    -webkit-text-fill-color: #9E9E9E;
}
.caption-gradient-wrapper .caption-gradient {
    background: none;
}
.post-header {
    background: #212121;
}
.post-image-container--spacer {
    padding-bottom: 0px;
    background: #2c2f34;
    margin-bottom: 20px;
}
.post-image {
    background: #2a2a2a;
}
.post-grid-image, .post-images {
    background-color: #2a2a2a;
}
.post-image-meta {
    background: #262626;
}
.post-action {
    background-color: #262626;
}
form.caption-create {
    background: #2e2e2e;
    border: 2px solid #2a2a2a;
    border-radius: 4px;
}
#captions .usertext {
    background: #323232;
}
#captions .usertext:before {
    border-color: transparent #353535;
}
a, a:hover {
    color: #1bb76e;
}
.btn-action {
    background: #1bb76e;
}
#hahascrollbitch{
  overflow:   scroll;
}
::-webkit-scrollbar {
    width: 0px;
    background: transparent; 
}
.sg-item.list.selected, .sg-item.list:hover {
    background-color: #323232;
}
a.sg-item.selected, a.sg-item:hover {
    background-color: #323232;
}
.sg-item.list {
    background-color: #262626;
}
.sg-item.grid {
    background-color: #262626;
}
.sg-item-title {
    background: #262626;
}
a.sg-item.selected .sg-item-title, a.sg-item:hover .sg-item-title {
    background-color: #323232;
}
#post-jump .box {
    background-color: #353535;
}
.post-action-tags-tag {
    background: #383838;
    border-radius: 5px;
}
.btn-action:hover {
    background: rgba(27, 183, 110, 0.83);
}
.posts {
    background-color: #262626;
}
#topbar .search-focused, #topbar .search-focused:hover {
    background-color: #2a2a2a;
}
#topbar .global-search-footer div, #topbar .global-search-footer span, #topbar .global-search-section a, #topbar .global-search-section span {
    background-color: #2a2a2a;
}
#outside-tagging-showhide {
    background-color: #2a2a2a;
}
.message-list-heading {
    background-color: #2a2a2a;
    border-right: 0px solid #232326;
}
.message-conversation {
    background-color: #2a2a2a;
}
.message-list-scrollable {
    background-color: #323232;
}
.notifications-dropdown {
    background: #353535;
}
.notifications-dropdown .notification-card.unread {
    background: #383838;
    border-bottom: 1px solid rgba(226, 229, 234, 0.13);
}
.notifications-dropdown {
    -webkit-text-fill-color: #fff;
}
#topbar .notifications-header .notifications-header-markall {
    -webkit-text-fill-color: #00b774;
}
.notifications-header {
    border-bottom: 1px solid #7c7e81;
}
.textbox {
    background-color: #262626;
}
.carousel-button {
    background-color: #262626;
}
.jcarousel {
    background-color: #262626;
    border: 2px solid #303337;
}  
.right .panel.menu .button.textbox {
    -webkit-text-fill-color: #1bb76e;
}
.stat {
    color: #1bb76e;
}
a strong, a:active, a:visited {
    color: #1bb76e;
}
.navigation #next {
    background: #00b774;
}
.combobox .options, .like-combobox-but-not .options {
    background-color: #373a3e;
    color: #f2f2f2;
}
.combobox .item.selected, .like-combobox-but-not .item.selected {
    background-color: #333;
}
.unpublished-info {
    background: #333;
}
.nano>.pane>.slider {
    background: rgba(0, 0, 0, 0);
}
.nano>.pane {
    background: rgba(0, 0, 0, 0);
}

}
