css:
@-moz-document domain("resetera.com") {


.formPopup {
    background-color: #5d5d5d;
}
.MultiQuoteItem .messageGradient {
    position: absolute;
    top: 0px;
    left: 0px;
    right: 0px;
    height: 120px;
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
    background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
    background: -o-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
}
.redactor_toolbar li.redactor_btn_group ul {
    background-color: rgb(56, 56, 56);
    padding: 1px;
    margin: 0;
    overflow: hidden;
    *zoom: 1;
    box-shadow: none;
}
.redactor_box {
    font-family: "Helvetica", sans-serif;
    border: 1px solid rgb(99, 99, 99);
    line-height: 0;
    position: relative;
    overflow: hidden;
}
.bbCodeBlock pre,
.bbCodeBlock .code {
    background-color: rgb(44, 44, 44);
}
.Responsive .redactor_toolbar li a {
    text-indent: 0px;
    text-align: center;
    background-image: none !important;
    background: #848484;
    /* color: white; */
    
}
.redactor_toolbar {
    font-size: 0;
    background-color: rgb(56, 56, 56);
    padding: 0 0 0 2px;
    margin: 0;
    border-bottom: 1px solid rgb(34, 34, 34);
    position: relative;
    left: 0;
    top: 0;
    line-height: 0;
    list-style: none;
    overflow: hidden;
}
.bbCodeBlock .type {
    font-size: 11px;
    font-family: Arial, sans-serif;
    color: rgb(205, 205, 205);
    background-color: rgb(90, 74, 102);
    padding: 5px 8px;
}
.pollBlock .pollOption label:hover {
    background: rgb(122, 60, 170);
    color: rgb(255, 255, 255);
    border-radius: 2px;
}
.pollBlock .questionMark {
    background: rgb(122, 60, 170);
}
.pollBlock .question .questionText {
    float: left;
    font-size: 16pt;
    color: rgb(213, 209, 215);
}
.textCtrl {
    font-size: 13px;
    font-family: "Arial", Verdana, Geneva, Helvetica, sans-serif;
    color: #e7e7e7;
    background-color: rgb(93, 93, 93);
    padding: 6px 5px;
    margin-bottom: 2px;
    border: 1px solid rgb(204, 204, 204);
    outline: 0;
}
.ugc a:link,
.ugc a:visited {
    font-weight: normal;
    color: rgb(189, 137, 255);
    padding: 3px;
    transition: all 0.4s ease-in-out;
}
.tabs {
    border-bottom: 1px solid rgb(142, 80, 190);
}
.username.author {
    display: none;
}

html {
    color: white;
}
.profilePage .tabs.mainTabs li.active a {
    background-color: #8e50be;
}
.profilePage .tabs.mainTabs {
    background: rgb(51, 51, 51);
}
.profilePage .profileContent {
    background: rgb(51, 51, 51);
    padding: 10px 20px;
    color: #c8c8c8;
}
.tabs li a,
.tabs.noLinks li {
    background-color: rgb(78, 80, 83);
}
.profilePage .mast,
.profilePage .mainProfileColumn,
.profilePage .primaryUserBlock {
    border: none;
}
.button.primary {
    color: white;
    background-color: rgb(122, 60, 170);
    border: 1px solid rgb(142, 80, 190);
}
#QuickSearch {
    background-color: #5d5d5d;
    border: none;
}
.formPopup .controlsWrapper {
    background: #5d5d5d;
    margin: 5px 0;
    font-size: 11px;
    border: 3px solid #5d5d5d;
}
body {
    background-color: #222;
    color: white;
    font-family: "Helvetica", sans-serif;
}
#header {
    background-color: #222;
}
#content .pageContent {
    color: rgb(152, 152, 152);
    background-color: #222;
}

.messageUserBlock a.username {
    color: rgb(255, 255, 255);
}

.messageList .message {
    background-color: #3b3b3b;
    color: white;
}
.titlebar h1 {
    color: rgb(212, 212, 212);
}
.messageUserInfo {
    background-color: rgb(59, 59, 59);
    float: left;
    width: 140px;
}

.messageList li.message:nth-child(2n),
.messageList li.message:nth-child(2n) .messageUserInfo {
    background-color: rgb(78, 78, 78);
}
.bbCodeQuote .quoteContainer {
    background-color: rgb(38, 38, 38);
}
.bbCodeQuote .attribution {
    color: rgb(186, 186, 186);
    background-color: rgb(63, 63, 63);
    border: 0 none #000;
}
.bbCodeQuote {
    background-color: #e3e2e2;
    border: 1px groove #595757;
    overflow: auto;
}
.quickReply {
    background-color: rgb(56, 56, 56);
    padding: 10px;
    border: 1px solid rgb(99, 98, 98);
}
.bbCodeQuote .quoteContainer .quoteExpand {
    background-color: #000;
}
.bbCodeQuote .quoteContainer .quoteExpand {
    color: rgb(201, 201, 201);
    background: -webkit-linear-gradient(top, rgba(40, 40, 40, 0) 0%, rgb(90, 90, 90) 80%);
    background: -moz-linear-gradient(top, rgba(40, 40, 40, 0) 0%, rgb(90, 90, 90) 80%);
    background: -o-linear-gradient(top, rgba(40, 40, 40, 0) 0%, rgb(90, 90, 90) 80%);
    background: linear-gradient(to bottom, rgba(41, 41, 41, 0) 0%, rgb(90, 90, 90) 80%);
}
.PageNav a {
    color: rgb(255, 255, 255)!important;
    background-color: rgb(78, 42, 111);
    border: 1px solid rgb(88, 52, 121);
    border-radius: 3px;
}
.PageNav a.currentPage {
    color: rgb(52, 39, 68);
    background-color: #56326D;
    border-color: #874DB4;
    position: relative;
}
.titleBar h1 {
    font-weight: bold;
    font-family: 'Helvetica', sans-serif;
    letter-spacing: .2px;
    font-size: 14pt;
    color: rgb(228, 228, 228);
    padding-bottom: 12px;
    overflow: hidden;
    zoom: 1;
}
.navTabs {
    background-color: #292929;
}

.navTabs .navLink {
    color: #7a7a7a;
}

.navTabs .navTab.selected .navLink {
    color: #8F4DBA;
}
.navTabs:hover .navTab.selected:hover .navLink:hover {
    color: #8F4DBA;
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    color: rgb(255, 255, 255);
    background-color: #56326d;
}

.navTabs .navTab.selected.PopupOpen .navLink {
    color: rgb(255,255,255);
    background-color: transparent;
}

.navTabs .navTab.PopupOpen .navLink {
    color: white;
}

.breadcrumb {
    background-color: rgb(86, 50, 109);
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    background-color: rgb(51, 51, 51);
    border-right: 1px solid #292929;
    border-bottom: 1px solid #292929;
    border-left: 1px solid #292929;
    padding: 0;
}
.sidebar {
    float: right;
    font-size: 11px;
    background-color: #333;
    width: 100%;
}
.nodeList > li:last-child {
    border-bottom: 1px solid #2F2D2D;
}
.nodeList > li:first-child {
    border-top: 1px solid #292929;
}
.secondaryContent {
    color: rgb(207, 207, 207);
    background-color: rgb(51, 51, 51);
    padding: 10px;
}
.nodeList .categoryStrip,
.subHeading,
.discussionList .sectionHeaders,
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.footer .pageContent {
    background-color: rgb(86, 50, 109);
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}

.node .nodeLastPost {
    color: rgb(119, 119, 119);
    background-color: rgb(51, 51, 51);
    border: 1px solid rgb(51, 51, 51);
    border-radius: 3px;
}
.discussionListItem .posterAvatar,
.discussionListItem .stats {
    background-color: rgb(51, 51, 51);
}
.discussionListItem .stats dl {
    padding: 0 10px;
    border-left: 0px solid #e4e4e4;
    border-right: 0px solid #e4e4e4;
}
.discussionListItem,
.primaryContent {
    background-color: rgb(51, 51, 51);
    border-right: 0px solid #3c3c3c;
    border-bottom: 1px solid #3c3c3c;
    border-left: 1px solid #3c3c3c;
}

.discussionListItem .lastPostInfo {
    padding: 0 10px 3px 5px;
}

body .itemPageNav a:link, a:visited {
    font-size: 11px;
    color: #cecece;
    background-color: transparent;
    padding: 0;
    border: transparent;
    border-radius: 0px;
}

body .itemPageNav a:hover {
    color: #cd96f2;
    background-color: transparent;
    padding: 0;
    border: transparent;
    border-radius: 0px;
}

.discussionListItem .secondRow {
    font-size: 11px;
    clear: both;
    padding-top: 2px;
    padding-left: 5px;
}

body .PageNav a {
    background-color: rgb(22, 22, 22);
    border: 1px solid rgb(22, 22, 22);
}



a.PageNavNext {
    border: none;
}
.sectionFooter {
    overflow: hidden;
    zoom: 1;
    font-size: 11px;
    color: rgb(255, 255, 255);
    background-color: rgb(33, 33, 33);
    background-repeat: repeat-x;
    background-position: top;
    padding: 4px 10px;
    line-height: 16px;
}
.heading .ToggleTrigger {
    padding: 10px;
}
.heading,
.xenForm .formHeader {
    font-weight: bold;
    font-size: 11pt;
    color: rgb(255, 252, 252);
    background-color: rgb(103, 103, 103);
    padding-right: 10px;
    padding-left: 10px;
    padding-top: 10px;
    padding-bottom: 10px;
}
.textHeading,
.xenForm .sectionHeader {
    font-weight: bold;
    color: rgb(224, 224, 224);
    padding-bottom: 2px;
    margin: 10px auto 5px;
    border-bottom: 1px solid rgb(39, 39, 39);
}

/* Navigation Bar */

.navTabs .navTab.PopupClosed:hover {
    background-color: rgb(81, 81, 81);
}
.navTabs .navTab.PopupClosed:hover {
    background-color: rgb(74, 74, 74);
    color: white;
}
.navTabs .navTab.PopupClosed .navLink:hover {
    color: rgb(208, 147, 255);
}

#headerMover #headerProxy
  {
    background-color: #222;
  }

.navTabs
  {
    padding: 0 7% 0 7%;
    position: fixed;
    z-index: 1000;
    width: auto;
    left: 0 !important;
    right: 0 !important;
    background-color: #333333;
  }

#header .pageWidth .pageContent:after
  {
    clear: none;
  }

#logo img
  {
    padding-top: 130px;
  }

  #searchBar
  {
    top: 150px;
  }

/* Hides title bar in forum view  */

#content.forum_view .titleBar {
    display: none;
}

#content.thread_view .titleBar {
    margin-top: 12px;
    margin-bottom: 0px;
}

#content.thread_view .titleBar h1 {
    padding-bottom: 0px;
}


#content.thread_view .titleBar #pageDescription {
    display: none;
}

/* "Watch Thread" */
.pageNavLinkGroup .linkGroup.SelectionCountContainer {
   /* display: none; */
    padding-top: 10px;
    padding-right: 15px
}

.pageNavLinkGroup .linkGroup.SelectionCountContainer a {
    color: #A2A2A3;
}

/* pagination */
.pageNavLinkGroup {
    display: flow-root;
    table-layout: fixed;
    box-sizing: border-box;
    font-size: 11px;
    margin: 10px 0;
    line-height: 16px;
}

.PageNav {
    display: block;
    width: 500px;
    float: left;
    padding-top: 5px;
    margin: 5px auto;
}

.nodeListNewDiscussionButton {
    display: block;
    float: right;
    margin-bottom: 15px;
}

.Menu .primaryContent,
.Menu .secondaryContent {
    background-color: #333;
    color: white;
}
.blockLinksList a {
    color: rgb(245, 245, 245);
}
html {
    background-color: rgb(34, 34, 34);
    -webkit-font-smoothing: antialiased;
    text-rendering: optimizeLegibility;
    overflow-y: scroll !important;
}
.Menu {
    background-color: #333;
    font-size: 11px;
    border: 1px solid #333;
    border-top: 10px solid #333;
    overflow: hidden;
    box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.5);
    min-width: 200px;
    *width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
    border-radius: 0px;
    border-right: 5px solid #333;
}
.hasJs .bbCodeSpoilerText {
    display: none;
    color: white;
    background-color: black;
    padding: 2px 2px 2px 0.5em;
    margin-top: 5px;
    margin-right: 140px;
    margin-bottom: 5px;
    border: black;
    overflow: auto;
}
.Menu .sectionFooter {
    background-color: #333333;
}
.Menu .sectionFooter a {
    color: #eee;
}
.secondaryContent a {
    color: rgb(179, 179, 179);
}
.navPopup .PopupItemLinkActive:hover {
    margin: 0 -8px;
    padding: 5px 8px;
    border-radius: 3px;
    background-color: rgb(60, 44, 69);
    cursor: pointer;
}
.LoggedIn .discussionListItem .unreadLink,
.LoggedIn .discussionListItem .ReadToggle {
    color: rgb(133, 71, 171)!important;
}
.primaryContent a {
    color: rgb(255, 255, 255);
}
.button {
    color: rgb(255, 255, 255);
    font: 11px 'Helvetica', sans-serif;
    background-color: rgb(135, 135, 135);
    border: 1px solid rgb(99, 99, 99);
    border-radius: 3px;
}
a.redactor_modal_btn.redactor_btn_modal_close.button {
    padding-top: 6px;
    color: rgb(255, 255, 255);
    font: 11px 'Helvetica', sans-serif;
    background-color: rgb(135, 135, 135);
    border: 1px solid rgb(99, 99, 99);
    border-radius: 3px;
}
.button.primary {
    color: rgb(255, 255, 255);
    background-color: rgb(86, 50, 109);
    border: 1px solid rgb(99, 99, 99);
    border-radius: 3px;
}
.button:hover {
    color: rgb(255, 255, 255);
    color: rgb(239, 232, 248);
    border: 1px solid rgb(122, 60, 170);
    border-radius: 3px;
    background: #965dc2;
}
.button:active {
    color: rgb(255, 255, 255);
    color: rgb(239, 232, 248);
    border: 1px solid rgb(122, 60, 170);
    border-radius: 3px;
    background: #965dc2;
}
.messageList .newMessagesNotice {
    margin: 10px auto;
    padding: 5px 10px;
    border-radius: 5px;
    border: 1px solid rgb(113, 48, 120);
    font-size: 11px;
    color: #edbdf5;
}
.bbCodeQuote {
    border-style: groove;
}

/* Handles 'post new thread' button in thread view */ 
a.callToAction span {
     font-weight: normal;
     font-size: 10px;
     font-family: 'Helvetica', sans-serif;
     color: rgb(255, 255, 255);
     background-color: rgb(86, 50, 109);
     padding: 2px 15px;
     border-width: 1px;
     border-style: none;
     border-radius: 3px;
     display: block;
 }


.primaryContent a {
    font: 'Arial', sans-serif;
}

.node .nodeText .nodeTitle {
    padding-top: 7px;
    font-size: 13pt;
}
.node .unread .nodeText .nodeTitle {
    font-weight: normal;
}

.node .nodeDescription {
    color: #878787;
}

.node .subForumList li .nodeTitle::before {
    color: #878787;
}

.alerts h3 {
    display: inline;
    color: #878787;
}

.navigationSideBar.ToggleTriggerAnchor a.primaryContent, a.secondaryContent {
    color: white;
}

/* Conversation/PM view */



/* Hide footer stuff, looked tacky */
.footer {
    visibility: visible;
}

.footer .pageContent {
    background-color: transparent;
}

.footerLegal {
    visibility: hidden;
}
.footerLinks a.homeLink  {
    display: none;
}

.footerLinks a {
    display: none;
}

.footerLinks a.topLink {
    visibility: visible !important;
    display: inline
}

iframe.redactor_textCtrl.redactor_MessageEditor.redactor_BbCodeWysiwygEditor.redactor_ > *[contenteditable="true"] {
    background-color: white;
}

/* remove bg color from search icon */
#QuickSearch::after {
    background: none;
}
.LoggedIn .discussionListItem .titleText {
    padding-left: 25px;
}

.discussionListItem .titleText {
    padding-top: 12px;
    padding-bottom: 10px;
    overflow: hidden;
    zoom: 1;
    position: relative;
}

/* Unread thread titles */
.discussionListItems .unread .title {
    font: 14px 'Helvetica', sans-serif;
    letter-spacing: .3px;
    color: white;
}
.discussionListItems .unread .lastPostInfo .username {
    font-weight: normal;
}
.discussionListItem .lastPostInfo .username {
    font-size: 1.1em;
    line-height: 24px;
    color: rgb(164, 118, 193);
}

.discussionListItem li {
    padding-right: 6px;
}

/* sticked threads */
span.fa.fa-thumb-tack.fa-lg.fa-fw {
    color: #874DB4;
}

.discussionListItem.visible.sticky {
    border-bottom: 1px solid #353535;
    background-color: #3f3f3f;
}

.discussionListItem.visible.sticky .listBlock.stats.pairsJustified {
    background-color: #3f3f3f;
}

.discussionListItem.visible.sticky .listBlock.main {
    border-left: 4px solid #874db4;
}

.threadListSeparator, .threadListSeparator sticky {
    display:none;
}

.discussionListItem .stats dl {
    padding: 0 0px;
}

/* User info (left side of posts) */

.messageUserInfo {
    float: left;
    width: 135px; /* Need to edit width of .message .messageInfo accordingly */
    border-right: 1px solid #666;
    min-height: 180px;
}
.messageUserBlock {
    font: bold 14px 'Helvetica', sans-serif;
    display: block;
    letter-spacing: .3px;
    margin-right: 0px;
}

.messageUserBlock a.username {
    color: #dfcff9;
}

.messageUserBlock a.username:hover {
    color: #dfcff9;
    text-decoration: underline;
}

/* Where it says "Member" */
.messageUserBlock .userBanner {
    font-size: 11px;
    color: #fc942d;
}

.messageUserBock .avatarHolder {
    padding: 12px 5px 5px;
}

/* makes tags orange but keeps roles white, secondary banner is for admin ribbon */
.messageUserBlock .userBanner.bannerSecondary.wrapped {
    background-color: #59336C;
    border-color: #59336C;
    color: white !important;
}
.messageUserBlock .userBanner.bannerHidden.wrapped {
    color: white !important;
}
/*Post content text */
.messageText {
    font-size: 11pt;
    letter-spacing: .1px;
    font-family: 'Arial', sans-serif;
    color: rgb(233, 233, 233);
    line-height: 1.45em;
    word-spacing: 0.5px;
    margin-bottom: 30px;
}

/* Post Content border and padding */
.message .messageInfo {
    padding: 12px 12px 12px 16px;
    margin-left: 135px;
    border-left: 1px #666 solid;
}

.message .messageContent {
    padding-top: 0px !important;
}

/* Bottom post border */
.messageList .message {
    padding: 0px;
    border-bottom: 1px #666 solid;
}

/* Meta data at bottom of posts */
.message .messageMeta {
    font-size: 11px;
    margin: 5px 0px 0px;
    padding: 0px; 
    overflow: hidden;
    zoom: 1;
    clear: both;
}

/*h hides 'posted at' timestamp */
.pageNavLinkGroup .linkGroup a.postsRemaining {
	display: none;
}

/* Section Headers for Thread list */
.discussionList .sectionHeaders {
    font-weight:bold;
}

/* First Unread / Target */

.messageList .message:target {
    background-color: #5a4d60 !important;
}

.messageList .message:target .messageUserInfo {
    background-color: #5a4d60 !important;
}

.messageList .message:target .postCount a.item.muted.postNumber.hashPermalink.OverlayTrigger {
    color: #bcbcbc;
}

/* User quoted by others */

.bbCodeBlock.bbCodeQuote[data-author="Angelus Errare"] {
    border: 1px solid #7c7c7c;
}

.bbCodeBlock.bbCodeQuote[data-author="Angelus Errare"] .attribution.type{
	background-color: rgb(174, 145, 201) !important;
    color: black;
}

.bbCodeBlock.bbCodeQuote[data-author="Angelus Errare"] .quoteContainer {
	background-color: #5a4d60 !important;
}

/* quote & reply */
.message .publicControls .MultiQuoteControl {
    color: #bcbcbc;
}

.message .publicControls .ReplyQuote.item.control.reply {
    color: #bcbcbc;
}

.message .privateControls .item.control.edit {
    color: #bcbcbc;
}


/* Quick fix for Quick Reply border */
.quickReply.message .messageUserInfo {
    border: none;
    background-color: inherit;
}

/* Private message conversation view */
.conversation_view .messageList {
    border: none;
    padding-right: 0px;
}


/* Hide report button because I'll never use it, too lazy */
a.OverlayTrigger.item.control.report {
   display: none;
}

/* Poll Stuff */
.pollResult .bar {
    background: rgb(133, 71, 171);
    height: 9px;
    display: block;
    zoom: 1;
}

.pollResult .barContainer {
    height: 11px;
    display: inline-block;
    zoom: 1;
    vertical-align: middle;
    border: 1px solid rgb(195,195,195);
    max-width: 100px;
    width: 100%;
    box-sizing: border-box;
    margin-right: 5px;
    font-size: 1px;
}

/* Avatar size for control panel drop down */

.navTab .accountPopup img {
    width: 25px;
    height: 25px;
    margin-top: 9px;
}

/* Unread message flash */
.navPopup .listPlaceholder ol.secondaryContent.Unread {
    background-color: rgb(127, 81, 186);
}

.breadcrumb .crust:first-child a.crumb,
.breadcrumb .crust.firstVisibleCrumb a.crumb {
    border-bottom-left-radius: 0px !important;
}

fieldset.breadcrumb {
    border-radius: 0;
}

body .faint, body a.faint, body .faint a {
    color: #7a7a7a;
}

body .muted, body a.muted, body .muted a {
    color: #7a7a7a;
}

.idstats dt {
    display: block;
    color: #878787;
}

.idstats dd {
    display: block;
    color: #878787;
}

#AjaxProgress.xenOverlay .content {
    display: none;
}
}

@-moz-document domain("resetera.com") {
.search_results .titleBar a:link, .listBlock.main .snippet a:link, .meta a:link {
