css:
@-moz-document domain("hackforums.net") {
#content {
    background: rgba(26,26,26,0.9);
}
#logo img {
    display: none;
}
#panel {
    background: rgba(26,26,26,0.9);
}
#panel .upper {
    background: rgba(26,26,26,0.9);
}
#panel .lower {
    background: rgba(26,26,26,0.9);
}
#wrapper {
    background: rgba(26,26,26,0.9);
}
.header__toplinks {
    background: rgba(26,26,26,0.9);
}
.thead {
    background: #814949;
}
.trow1 {
    background: rgba(39,39,39,0.9);
}
.trow2 {
    background: rgba(39,39,39,0.9);
}
a.button.new_thread_button {
    background-color: #814949;
    background-image: none;
}
a.button.new_reply_button {
    background-color: #814949;
    background-image: none;
}
a.button.add_buddy_button {
    background-color: #814949;
    background-image: none;
}
a.button.remove_buddy_button {
    background-color: #814949;
    background-image: none;
}
a.button.remove_ignore_button {
    background-color: #814949;
    background-image: none;
}
a.button.add_ignore_button {
    background-color: #814949;
    background-image: none;
}
a.button.report_user_button {
    background-color: #814949;
    background-image: none;
}
.pagination .pagination_current {
    border: 1px solid, #814949;
    border-top-color: #814949;
    border-right-color: #814949;
    border-left-color: #814949;
    border-bottom-color: #814949;
}
.pagination a {
    border: 1px solid, #814949;
    border-top-color: #814949;
    border-right-color: #814949;
    border-left-color: #814949;
    border-bottom-color: #814949;
}
.sidenav {
    background: rgb(26,26,26);
}
#footer .lower {
    background: rgba(26,26,26,0.9);
}
#footer .upper {
    background: rgba(26,26,26,0.9);
}
}
