css:
@-moz-document url-prefix("https://trello.com/b"), url-prefix("https://trello.com/c") {
/*===========================================================
=					Trello - Easy Arabic					=
=============================================================

  @author: Amer Bakeer
  @date: 25 Oct 2017

*/

body,
.list,
textarea,
.new-comment-input,
textarea.field,
.js-text,
.list-card-composer-textarea,
.checklist-new-item-text {
	font-family: 'segoe UI', 'Droid Arabic Naskh', 'Tahoma' !important;
}


.list,
.body-custom-board-background #header,
.body-board-view #header,
.boards-drawer.is-shown.boards-drawer-pinned
{
	background: rgba(0, 0, 0, 0.75);
}

.list-header textarea {
	line-height: 1.1em;
	color: #fff;
	font-size: 1.1em;
	margin: 2px 0px;
}
.list-header textarea:focus {
	color: #000;
}

/*.list-card {
	font-size: 16px !important;
	font-weight: bold;
	line-height: 25px !important;
}
*/
.list-card .list-card-title
{
	font-size: 16px !important;
	font-weight: bold;
}

  
/* Make checklist, text areas display mixed Arabic & English text correctly: */

.checklist-item-details .checklist-item-row .checklist-item-text-and-controls
{
    display: inline-flex;
    flex-flow: row-reverse;
}

.checklist-item-row,
textarea.js-checkitem-input,
textarea.js-description-draft,
textarea.card-back-description,
textarea.checklist-new-item-text,
textarea.js-new-checklist-item-input,
.checklist-item-details-text 
 {
	direction: rtl;
	text-align: initial !important;
	unicode-bidi: plaintext;
} 
.action-comment p {
	direction: rtl;
	unicode-bidi: plaintext;
}
  
 /* For Comments: */
 .action-comment 
{
    width: 100%;
}
 /* For Lists inside Comments: */
.markeddown ul>li 
{
  margin: 8px 20px;
}
.adgMarkdown .markdown ol,
.adgMarkdown .markdown ul,
.adgMarkdown .markeddown ol,
.adgMarkdown .markeddown ul 
{
    padding: 0px 24px;
}
/* ******************************************************************** */
/* Pinned Boards Side Panel: ************ */
.boards-drawer.is-shown.boards-drawer-pinned .sidebar-section-header-title
{
    color: #fff;
    font-weight: normal;
}
.sidebar-section-header
{
    margin-left: 0;
}

/*.boards-drawer.is-shown.boards-drawer-pinned .icon-lg,
.boards-drawer.is-shown.boards-drawer-pinned .icon-sm
{
     color: #fff;
}
*/
.boards-drawer.is-shown.boards-drawer-pinned {
    width: 190px;
}
@media only screen and (max-width: 1280px) and (min-width: 901px), only screen and (min-width: 1281px)
{
    .body-pinned-drawer #surface
    {
       margin-left: 280px;
    }
}

/* Links: */
.boards-drawer.is-shown.boards-drawer-pinned a.quiet-button,
a.open-card-composer .icon-sm,
a.open-card-composer
{
    color:rgba(255,255,255,0.5);
    background: none;
}
.boards-drawer.is-shown.boards-drawer-pinned a.quiet-button:hover,
a.open-card-composer:hover,
a.open-card-composer:hover .icon-sm
{
    color:rgba(255,255,255,0.9);
    background: none;
}
}
