css:
@-moz-document url-prefix("https://www.tumblr.com/") {
.elevator-wrapper {
    width: 20px;
}
.popover_header,
.recommended_tumblelogs,
.radar,
.controls_section.controls_section_radar,
.ui_search,
.logo,
.notification.single_notification.alt.takeover-container.big-margin,
.sidebar-ad-container,
.sponsored_post,
.image-ad {
    visibility: hidden !important;
    height: 0px!important;
    min-height: 0px!important;
    width: 0px!important;
    min-width: 0px!important;
}
.identity {
    background-image: none;
    background-color: #213f52;
}
.right_column {
    width: auto !important;
    height: auto;
}
.post_content {
    transform: translateX(-1px)
}
.l-container,
.l-container--two-column-dashboard,
.l-content,
.l-container--two-column,
.left_column {
    background-color: transparent !important;
}
.conversation-header-image {
    background-image: none;
}
.post_control.share {
    display: inline-block;
}
.l-header-container.l-header-container--refresh {
    box-shadow: none;
}
.new_post_buttons_container {
    visibility: hidden;
    height: 0px !important;
}
.left_column>.posts {
    transform: translateY(-60px)
}
.post_permalink{
    background: transparent !important;
    border-left:1px solid #ffd800 !important;
    border-bottom:1px solid #ffd800!important;
    border-right:1px solid #213f52!important;
    border-top:1px solid #213f52!important;
    border-radius: 3px !important
}
/*colors*/

.note_item {
    border-top-color: #ffd800!important;
    border-left-color: #ffd800!important;
    border-right-color: #ffd800!important;
    border-bottom-color: #ffd800!important;
}
.reblog-list {
    border-top-color: #ffd800;
}
.reblog-list-item {
    border-bottom-color: #ffd800
}
.audio_player {
    background-color: #f2992e !important;
}

/*text color*/

.asker,
.new_post_label_text,
.left_column>.posts,
.post-source-name-prefix,
.liked:before,
.reblogged:after,
.reblog_icon:before,
.alternative_timestamp {
    color: #ffd800 !important;
}
/*Link Color*/

.post_tag,
.recommendation-reason-link,
.name,
.post_ask_me_link,
.like:before,
.reblog:before,
.reply:before,
.share:before,
.reblog-graph-control:before,
.post_info_link,
.note_link_current,
.popover_menu_item_anchor,
.blog-sub-nav-item-label,
.blog-list-item-info,
.sidebar_link,
.post_full.is_quote,
.post_title,
li.section_header{
    color: #f2992e !important;
}
.right_column,
.post_avatar_image,
.post_avatar,
.tumblelog-avatar,
.post_avatar_link,
.left_column>#posts.posts>.post_container>.post,
.post-source-footer,
.note_item,
.reblogged::after,
.popover_inner,
.popover_menu_item,
.l-header-container,
.popover-inner,
.ui_note.is_friend,
.date_header,
.conversation-main,
.conversation-compose,
.compose-text-input-container{
    background-color: transparent !important;
    background: transparent;
}
.compose-button {
    fill: #f2992e
}
.tab_notice{
        background: #f2992e !important
}
.right_column:after,
.text-input-container:first-child,
.drawer,
.activity-notification.is_friend{
    background: transparent !important;
}
.white_pane,
.text-input,
.popover.popover_menu.popover_gradient.popover--active,
.popover--inbox-popover,
.info_popover,
.popover--reblog-graph,
.popover--messaging-share-post,
.conversation-main,
.compose-text-input-container,
.conversation-header,
.keycommand-guide,
.header-container{
    background-color: rgba(255, 255, 255, 0.7) !important;
}
}
