@-moz-document regexp("http(s)?://boards\.(4chan|4channel)\.org/(\\d|\\w{1,4})/"){
  /*board index*/
  .board *{line-height:100%!important;}
  .board>hr{display:none!important;}
}

@-moz-document regexp("http(s)?://boards\.(4chan|4channel)\.org.*"){
  .board{background:#000!important;}
  /*psot op*/.post.op,div.post.op div,div.post.op blockquote{background:#000!important;}
  /*psot op*/.post.op div.file .fileText{display:none!important;}
  /*psot op*/.post.op div.file .fileThumb{margin:0px 10px!important;}
  /*psot op*/.post.op div.postInfo>input{display:none!important;}
  /*psot op*/.post.op div.postInfo>span.subject{color:#a11!important;font-size:28px!important;}
  /*psot op*/.post.op blockquote.postMessage{font-size:24px!important;margin:0px!important;padding:0px 30px 5px 30px!important;}
  /*psot op*/.thread>span.summary{width:100%!important;background:#333!important;margin:0px!important;display:block!important;padding:0px 0px 2px 0px!important;}
  /*psot op*/div.thread>div:nth-of-type(2)>div.reply{margin:0px!important;}

  .thread>.replyContainer:nth-of-type(2n) .post:not(:target),
  .thread>.replyContainer:nth-of-type(2n) .postInfo,
  .thread>.replyContainer:nth-of-type(2n) .postMessage{background:#030!important;}
  /*reply*/.replyContainer *{margin:0px!important;padding:0px!important;}
  /*reply*/.replyContainer>.sideArrows{display:none!important;}
  /*reply*/.postContainer.replyContainer>.post.reply{width:100%!important;}
  /*reply*/.post.reply{background:#040!important;}
  /*reply info*/.post.reply>.postInfo{padding:0px 20px 0px 30px!important;width:initial!important;}
  /*reply info*/.post.reply>.postInfo .name{color:#0af!important;font-size:20px!important;}
  /*reply info*/.post.reply>.postInfo .dateTime{color:#666!important;}
  /*reply info*/#post-menu{border:2px solid #c60!important;}
  /*reply info*/#post-menu li{background:#000!important;}
  /*reply info*/#post-menu li:hover{background:#052!important;}

  /*reply img*/.post.reply>.file:not(.image-expanded){padding:0px 20px 0px 30px!important;}
  /*reply img*/.post.reply>.file:not(.image-expanded) .fileThumb{padding:5px 10px!important;}
  /*reply img*/.post.reply>.file .fileText{color:#666!important;}
  /*reply img*/.post.reply>.file .fileText a{color:#560!important;}
  /*reply img*/.post.reply>.file .fileText a:hover{color:#960!important;}
  /*reply img expanded*/.file.image-expanded{margin:auto!important;}
  /*reply img expanded*/.file.image-expanded img.expanded-thumb{max-width:900px!important;}

  /*reply target*/   .post.reply:target,
  /*reply target*/   .post.reply:target .file,
  /*reply target*/   .post.reply:target .postInfo,
  /*reply target*/   .post.reply:target .postMessage{background:#222!important;}
  /*reply highlight*/.post.reply.highlight .file,
  /*reply highlight*/.post.reply.highlight .postInfo,
  /*reply highlight*/.post.reply.highlight .postMessage{background:#333!important;}


  /*reply content*/.post.reply blockquote.postMessage{font-size:28px!important;line-height:100%!important;padding:0px 30px 0px 50px!important;}
  /*reply content*/.post.reply blockquote.postMessage .quotelink{font-size:18px!important;line-height:100%!important;top:0px!important;position:relative!important;height:initial!important;}
  /*reply content*/.post.reply div.postInfo>input{display:none!important;}

  /*quote preview*/.reply:target, .reply.highlight{background:#000!important;border:none!important;}
  /*quote preview*/.burichan_new .backlink a, .yotsuba_b_new .backlink a{font-size:16px!important;color:#0a0!important;}
  /*quote preview*/.yotsuba_new #quote-preview.highlight,#quote-preview {border-width:0px!important;}
  /*quote preview*/.yotsuba_new #quote-preview.highlight{border-color:#000!important;}
  /*quote preview*/#quote-preview{background:#000!important;}
  /*quote preview*/#quote-preview *{margin:0px!important;}
  /*quote preview*/#quote-preview .postMessage{padding-right:30px!important;}

  /*bottom pages*/
  div.pagelist{width:100%!important;background:#000!important;}
  div.pagelist{font-size:34px!important;line-height:100%!important;}
  div.pagelist div{color:#000!important;}
  div.pagelist a:hover{background:#053!important;}
  div.pagelist strong a{color:#a11!important;}

  /*setting*/
  .extPanel.reply{background:#030!important;}

}

