css:
@-moz-document domain("nim-lang.org"), domain("nimyaml.org") {
body {
    background-color: #151515;
    color: #dddddd;
}

a {
    color: #ffe953;
}

table.docinfo {
    color: #c3c3c3;
}

table.docutils th {
    background-color: #3e3e3e;
}

a.toc-backref {
    color: white;
}
a:visited {
    color: #85a7b9;
}
a:hover {
    color: #697c86;
}
p.module-desc {
    color: #DDDDDD;
}
span.Other {
    color: white;
}
dt pre > span.Identifier ~ span.Identifier, dt pre > span.Operator ~ span.Identifier {
    color: #CCCCCC;
}
dd > pre {
    background-color: #202020;
}

pre span.Identifier {
    color: #CCCCCC;
}

pre span.Comment, span.LongComment {
    font-style: italic;
    font-weight: 400;
    color: #484a86;
}

pre span.Punctuation {
	color: white;
}

pre span.Operator {
    color: white;
}
pre span.EscapeSequence {
    color: #49afab;
}
pre.listing {
    background: #202020;
    border: none;
}

blockquote {
    background-color: #202020;
    color: #EEEEEE;
}
div.search_results {
    background-color: #151515;
}
dt > pre {
    background-color: #212121;
}
}
