css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("topito.com") {
  
  
  html, body {
	background: #272321;
}
	
  body {
	color: #e2e2e2 !important;
} 
	
	.navbar-inverse .navbar-text {
	color: #e2e2e2;
}
	
  #content-container {
	background-color: #161616;
	border: 1px solid #6A5F5A;
}
  
  
  #topi-bar, #footer, #content-container {
	-webkit-box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
	box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
}
  .col-main {
	border-right: 1px solid #6A5F5A;
	border-top: 1px solid #6A5F5A;
}
	
  a {
	color: #e2e2e2;
}
  
  .infobulle.tops-une span {
	background-color: #151d5c;
}
  
  .fb_likes_box {
	color: #fff;
}
  
  .col-sidebar {
	border-top: 1px solid #6A5F5A;
	background: #161616;
}
  
	.widget-topito-live .content {
	-webkit-box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
	box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
}
	
	.bicolor-title {
	color: #cbc8c6; 
}
	
	.widget-topito-live header h3 span {
	background-color: #161616;
}
	
	#main-sidebar > li {
	border-bottom: 1px solid #6A5F5A;
}
	
	.widget-topito-live ul, .widget-topito-live li {
	border: 1px dashed #6A5F5A;
}
	
	.widget-topito-live li a {
	color: #e2e2e2;
}
	
	.mh-v1::before {
	top: 20px;
	border-top: 3px solid  #6A5F5A /* #374248; */
}
	
	.mh-v1 {
	background-color: #161616;
}
	
	.widget-topitruc .content {
	-webkit-box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
	box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
}
	
	.block-topito-quoi {
	border-top: 2px solid #161616;
	background: linear-gradient(#161616,#272321,#272321,#272321,#161616);
}
	
	.more a:hover {
	color: #0696b5 !important;
}
	
	.block-topito-quoi span.counter {
	color: #e2e2e2;
}
	
	.block-top-home::after {
	border-left: 1px solid #6A5F5A;
}
	
	.cat-reste-du-monde, .block-top-home {
	border-top: 1px solid #6A5F5A;
}
	
	.block-top-home .magic-header::before {
	border-top: 3px solid #6A5F5A;
}
		
	.block-top-home .magic-header {
	background-color: #161616;
}
	
	.top-user-block h4 {
	color: #e2e2e2;
}
	
	.block-top-topiteurs {
		-webkit-box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
	box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
}
	
	.block-top-topiteurs h3 {
 background-color: #161616; 
}
	
	.block-topiteur .bt-container {
	background: #161616;
}
	
	.block-topiteur {
	border: 1px solid #6A5F5A;
}
	
	.bt-tops,.bt-list > li a ,.bt-name a {
	font-weight: bold;
	color: #e2e2e2;
}
	
	.bt-button {
	-webkit-box-shadow: inset 0 4px 0 0 #161616;
	box-shadow: inset 0 4px 0 0 #161616;		
}
	
	.block-topiteurs-home, 	.block-home-dossiers {
	border-bottom: 1px solid #6A5F5A;
}
	
	.block-social-newsletter {
	border: 1px solid #6A5F5A;
}
	
	.block-derniers-tip-tops .magic-header::before {
	border-top: 3px solid #6A5F5A;
	}
	
	.block-derniers-tip-tops .magic-header {
	background-color: #161616;
}
	
	.block-derniers-tip-tops {
	background-color: #161616;
}
	
	.loop-micro h3 a {
	color: #e2e2e2;
}
	
	.block-best-of h2 {
	 background: linear-gradient(#161616,#272321,#272321,#272321,#161616);
}
	
	.block-best-of {
	    background: linear-gradient(#161616,#272321,#272321,#272321,#161616);
}
	
	.mh-v1::before {
	border-top: 3px solid #6A5F5A;
}
	
	.bbo-list .magic-header {
	background-color: #161616;
}
	
	.bbo-list > div + div::before {
	border-left: 1px solid #6A5F5A;
}
	
	.minilist-content li + li h4 a, .mc-illu h4 {
	color: #e2e2e2;
}
	
	#footer {
	position: relative;
	background-color: #02023e;
	z-index: 2;
}
	
	.grid-footer a {
	font-size: 11px;
	color: #fff;
}
	
	.logo-footer {
	filter:  grayscale(100%)  brightness(200%) contrast(200%) saturate(200%)
}
	
	
	
	
	/*     article      */
	#breadcrumbs-container #breadcrumbs div a {
	color: #e2e2e2;
}
	#post-navigation-single a .text span, #post-navigation-single a .text {
	color: #fff;
}
	
	#post-navigation-single a {
	border: #6A5F5A 1px solid;
	background: #02023e;
}
	
	#single-share-float {
	background-color: #02023e;
	box-shadow: 1px 0px 10px  #6A5F5A !important;
}
	
	.partager-top {
	color: #fff;
}
	
#single-share-float .logo img, #single-share-float .logo img::after, #single-share-float .logo img::before {
  filter:  grayscale(500%) contrast(900%) invert(100%)
	}

	.navbar-primary .navbar-nav .dropdown-menu {
	background-color: #151d5c;
}

	.navbar-primary .navbar-nav > li.listes .dropdown-menu h3 {
	border-bottom: 1px solid #02023e;
	color: #fff;
}
	
	.navbar-primary .navbar-nav > li.listes .dropdown-menu ul li a {
	color: #fff;
}
	
	.navbar-primary .navbar-nav > li.listes .dropdown-menu .col {
	border-right: 1px solid #02023e;
}
	
	#social-slide-box {
	background: #161616;
	border-top: #02023e 5px solid;
}
	
	.le_input {
	background-color: #272321;
	box-shadow: inset 0 0 0 1px #d8dcde;
}

	#social-slide-box .options {
	border-top: #FFF 1px solid;
	border-bottom: rgba(0,0,0,0.1) 1px solid;
	background-color: #02023e;
	font-weight: bold;
	color: rgba(0,0,0,0.5);
}
	
	
	
	/*   	Commentaires au top   */
	.fb_iframe_widget iframe {
	background-color: #fff;
		filter: brightness(75%)
}
	
	.single-pagi a {
		background-color: #02023e;
	border: #6A5F5A 1px solid;
}
	
	.single-pagi > *::before, .single-pagi > *::after {
	color: #fff; 
}
	
	.post-content {
	color: #e2e2e2;
}
	
	.single-post-header h1, .single-post-header h2, .trucdesc h1 {
	color: #e2e2e2;
}
	
	.single-post-header .single-post-header-author .shares {
	color: #fff;
}
	
	
	.link-list-categories { 
	color: #e2e2e2;
}
	
	.plusdetops a {
	color: #fff;
}
	
	.bg-ball {
  background: #0798d6;
	border-radius: 45px; 
		
}
	.bg-ball .user-nb-tops strong, .bg-ball > *, topilikeslabel, .bg-ball .user-nb-tops a {
	color: #fff;
}
		
	/*Topitrucs  */  

h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
	color: #e2e2e2;
}	
	.page-cat .cms-header, .page-cms .cms-header p {
	color: #e2e2e2;
}
	
	.block-tri .sortby span {
	color: #e2e2e2;
}
	
	.cssn_pagination li.pagination-next-prev, .cssn_pagination li li span, .cssn_pagination li li strong, .cssn_pagination li li a {
		background-color: #02023e;
	border: 1px solid #6A5F5A;
	color: #fff;
}
	
	.cssn_pagination *, .cssn_pagination span.disabled {
	color: #fff;
	opacity: 1;
}
	
	#content-container .cssn_pagination li li strong, #content-container .cssn_pagination li li .current, #content-container .cssn_pagination li li a:focus, #content-container .cssn_pagination li li a:hover, .cssn_pagination li.pagination-next-prev:hover {
	border: 2px solid #6A5F5A;
	color: #02023e;
	background-color: #fff;	
}
	
	.cssn_pagination li.morepages ul {
	background: #161616;
	border: 2px solid #6A5F5A;
}
	
	.post-content ol li, 	.post-content ol li::before, .post-content ul li::before {
	color: #e2e2e2;
}
	
	
	
	/*Comedy Night */  
	
	#mc_embed_signup {
	background: #272321;
}
	
	#mc_embed_signup input {
	border: 1px solid #ABB0B2;
		background: #161616;
}

	.widget-txt .content {
	background-color: #161616;
	-webkit-box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
	box-shadow: inset 0 0 0 2px #6A5F5A,inset 0 0 0 5px #161616,inset 0 0 0 6px #6A5F5A;
}
	
	.widget-txt .content h3 span {
	background-color: #161616;
}
	
	.topi_form.float_form .box label, 	.widget-txt.mh-green .content ol strong:first-child, .widget-txt.mh-green .content ul strong:first-child, .widget-txt ol strong:first-child, .widget-txt ul strong:first-child {
	color: #e2e2e2;
}
		
	.topi_form .box small {
	background-color: #161616;
}
}

	
	@-moz-document domain("shop.topito.com") {
		
	html,  body {
	background: #272321;
}
	
		.wrapper {
	background: #272321;
}
	
	.account-cart-wrapper {
	background-color: transparent;
}
	
	.skip-link {
	background: #02023e;
	color: #fff !important;
	border: 1px solid #6A5F5A;
}
	
	.nav-primary a {
	color: #fff;
}
	
	.nav-primary li.level0 ul {
	background: #02023e;
	border: solid 1px #6A5F5A;
}
		
	.breadcrumbs a,	.breadcrumbs strong {
	color: #e2e2e2;
}
	
	.page-title h1, .page-title h2, .product-name h1, .product-name .h1 {
	color: #e2e2e2;
	border-bottom: 1px solid #ededed;
}
		
	.block-layered-nav .block-content > dl > dt {
	color: #fff;
	border-color: #6A5F5A;
	background-color: #02023e;
		font-weight: bold; 
}
		
	.block-layered-nav .block-content > dl > dt:hover {
	color: #fff;
	background-color: transparent;
}
	
	.block-layered-nav .block-content > dl > dd {
		background-color: #161616;
	border-color: #6A5F5A;
}
	
	.products-grid > li  {
	background-color: #161616;
	border: 1px solid #6A5F5A;}
	
		.products-list > li {
	background-color: #161616;
	border: 1px solid #6A5F5A;
}
		
		h2.product-name a, h3.product-name a, h4.product-name a, h5.product-name a, p.product-name a {
	color: #e2e2e2;
}

		.toolbar, .pager {
	color: #e2e2e2;
}
		
		.toolbar {
	border-bottom: 1px solid #6A5F5A;
	border-top: 1px solid #6A5F5A;
	background: #161616;
}

		body, button, input, select, table, textarea{
	color: #e2e2e2;
		}
		
	select {
	border-color: #6A5F5A;
	background-color: #272321;
		}
		
		.sorter > .sort-by .sort-by-switcher {
  filter:  grayscale(500%) contrast(900%) invert(100%)
		}
		
		.pages .current, .pages .current:hover {
	color: #fff;
	border: 1px solid #6A5F5A;
	background-color: #02023e;
}
		
	.product-view {
	border-color: #6A5F5A;
	background-color: #161616;
	-webkit-box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
	box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
}

		p.required, .validation-advice {
	color: #ff4c2f;
}
		
		.product-options,	.product-options-bottom {
	background-color: #000;
	border: 1px solid #6A5F5A;
}
		
		.product-view .sharing-links a.link-facebook {
	background-color: #fff;
}
		
		.tabs {
		border-color: #6A5F5A;
	background-color: #161616;
	-webkit-box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
	box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
}
				
		.product-collateral .toggle-tabs {
	border: 1px solid #6A5F5A;
	background-color: #272321;
}
				
		.product-collateral .toggle-tabs li.current > span {
	background-color: #02023e;
}
		
		.product-collateral .toggle-tabs li.current span, .product-collateral .toggle-tabs li span {
	color: #fff;
}
		
		.product-collateral .toggle-tabs li.current {
	border: 1px solid #6A5F5A;
}
		
		.product-collateral > dl > dd {
	border: unset;
}
		
		.cart {
		border-color: #6A5F5A;
	background-color: #161616;
	-webkit-box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
	box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
}
		
		.messages .success-msg li {
	color: #e2e2e2;
	border-bottom: 1px solid #6A5F5A;
	border-right:  1px solid #6A5F5A;
	border-top:   1px solid #6A5F5A;
  background-color: #000;
}
		
		input.qty[type="number"], input.qty[type="text"] {
	border: 1px solid #6A5F5A;
	background-color: #272321;
}
		
		.cart-totals, .cart-forms .giftcard, .cart-forms .shipping {
	border-top:   1px solid #6A5F5A;
  background-color: #000;
}
		
 .cart-forms .discount {
	background-color: #02023e;
}	
		
	#discount-coupon-form label  { 
		color: #fff;
		font-weight: bold; 
}
			
		input[type="email"], input[type="search"], input[type="number"], input[type="password"], input[type="tel"], input[type="text"] {
	border: 1px solid #6A5F5A;
	background-color: #272321;
}
		
		.col-main {
		border-color: #6A5F5A;
	background-color: #161616;
	-webkit-box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
	box-shadow: 0 0 0 4px rgba(106,95,90,0.1);
}
		
	#discount-coupon-form .input-text {
	background: #fff; 
	color: #000;
	font-weight: bold; 
}
}
