css:
@-moz-document url-prefix("https://keepersecurity.com/vault") {
/* Background gradients */
body.blue_and_green {background-image: radial-gradient(ellipse at bottom left,#009450,#0078C0)}
body.classic {background-image: radial-gradient(ellipse at center bottom, rgb(27, 120, 223) 20%, rgb(0, 72, 153) 100%)}
body.red_and_blue {background-image: radial-gradient(ellipse at center bottom, rgb(253, 24, 70) 20%, rgb(23, 185, 254) 100%)}
body.burgundy {background-image: radial-gradient(ellipse at center bottom, rgb(68, 39, 90) 20%, rgb(114, 76, 110) 100%)}
body.flame {background-image: radial-gradient(ellipse at center bottom, rgb(231, 99, 67) 20%, rgb(144, 78, 149) 100%)}
body.green {background-image: radial-gradient(ellipse at center bottom, rgb(44, 164, 65) 20%, rgb(170, 145, 60) 100%)}
body.deep_ocean {background-image: radial-gradient(ellipse at center bottom, rgb(20, 181, 198) 20%, rgb(68, 60, 169) 100%)}
body.glam {background-image: radial-gradient(ellipse at center bottom, rgb(248, 172, 168) 20%, rgb(67, 59, 170) 100%)}
body.aluminum {background-image: radial-gradient(ellipse at center bottom, rgb(119, 119, 118) 20%, rgb(36, 36, 37) 100%)}
body.phoenix {background-image: radial-gradient(ellipse at center bottom, rgb(253, 82, 1) 20%, rgb(253, 137, 3) 100%)}

/* Tweaks for background change */
.left-menu {background: transparent !important;}
.left-pane,.root-target, #identity-payments .sources, #identity-payments .sources .optionsBar  {background: rgba(0,0,0,.25) !important}
#security-audit {background: rgba(0,0,0,.25)}

/* Clear content-pane tweaks */
.content-pane, .datavault-content-list .viewport {background: transparent !important;}
#company_logo {max-height: 54px !important;}
/* Company Logo [Option A]- normal with white background */
/*
.logo-container {background: rgba(255,255,255,.5) !important;padding: 5px; border-radius: 5px}*/
#company_logo {filter: drop-shadow(0 0 .5px #000) drop-shadow(0 0 .5em #fff) drop-shadow(0 0 .5em #fff) }
/* */
/* Company Logo [Option B]- inverted, for transparency */
#xcompany_logo {max-height: 54px !important; filter: invert(100%) hue-rotate(180deg) brightness(150%) drop-shadow(0px 0px 10px rgba(0,0,0,.75)) drop-shadow(0px 0px 2px black);}
/* */
body {color: #fff !important;}
.folder-details .permissions_header {background: rgba(0,0,0,.25)}

.right-pane, .details-shown .details, #main .control-bar {background: rgba(0,0,0,.35)}
.record-details label{color: #aaa}
.record-details .masked-value {color: #fff}
.file-header, .file-size, .date-deleted, .undelete-record {color: #fff !important;}
.record-details{padding: 0px 0px 0px 0px}
.record-details .mainBar {padding: 16px 16px 16px 16px; background: rgba(0,0,0,.25)}
.record-details .viewport {padding: 0px 0px 0px 16px;}
.record-details .viewport .scrolling, .personal-info-details .scrolling-container {padding: 0px 16px 0px 0px}
.personal-info-details {margin-right: -16px}
.personal-info-details .actions {padding-right: 16px}
.record-details .breadcrumb-container {margin-top: 0px}
.formGroup{margin: 0; padding-top: 12px; padding-bottom: 12px; border-bottom-width: 1px; border-bottom-style: solid; }
.formGroup label{margin:5px 0px}
.formGroup .passwordStrengthMeter {margin-top: 0px; margin-bottom: 10px }
.record-details .files .file .view-mode {background: rgba(0,0,0,.24) }
.record-details .files .file .view-mode:hover {box-shadow: 0 0 6px 0 rgba(255,255,255,.5); background: rgba(255,255,255,.24)}
.ext {background: rgba(0,0,0,.4)}
textarea, input.masked-value, input.toggle-shown, .search-field, input[name="username"], .phone-input input, .textDropdown, input[name="email"], input[name="shared_folder_name"] {background: rgba(0,0,0,.5) !important; color: #fff !important }
.search-field textarea, textarea::placeholder, .textDropdown.country-dropdown textarea, .account .textDropdown, .vault-split .textDropdown, .audit-list .textDropdown, .grow-content {background: transparent !important; color: #fff !important;}
textarea:hover, textarea:selected {box-shadow: 0 0 6px 0 rgba(255,255,255,.24) !important; }

.mdl-slider__background-lower {background: white}
.card {background: rgba(0,0,0,.35);  border: 4px solid rgba(0,0,0,.25) !important; border-radius: 8px !important;}
.card:hover {background: rgba(30,30,30,.35);border-color: rgba(255,255,255,.25) !important}
.card .icon svg, .card .circlex {filter: drop-shadow(0px -1px .5px rgba(0,0,0,.75)) drop-shadow(0px 1px .5px rgba(0,0,0,.75)) drop-shadow(0px 0px 20px white)}
.card .circle {background-color: rgba(0,0,0,.25) !important}
.card .circleText {color: #fff !important}
.cardCaption{color: #aaa !important;}
#main .control-bar, .listHeader {box-shadow: none !important;}
.account {background: rgba(0,0,0,.25) !important; padding: 5px; border-radius: 4px}
.folder-details .optionsBar .folderSettings {color: #ccc; text-shadow: 0px 0px 2px rgba(0,0,0,.75)}
.folder-details .optionsBar .folderSettings svg {fill: #ccc; }
.content-pane .folder-icon svg {filter: invert(100%) hue-rotate(180deg) saturate(150%) drop-shadow(0px 1px 1px rgba(0,0,0,.35)) drop-shadow(0px -1px 1px rgba(0,0,0,.35)) !important}
body button, body div, body input, body li, body select, body textarea {border-color: rgba(255,255,255,.25) !important}
.nav-component .content-pane .component-content .split-pane .right-pane {border-color: rgba(255,255,255,.25) !important; border-width:0px}
.root-target, .datavault-content-list li, .breadcrumb-segment-name {color: #fff !important;}
.foldertoggle svg, .root-target svg, .textDropdown svg, svg.back-marker, .folderInfo svg, .customEdit svg, .add-file svg, .addRow svg, .tumbling_dice svg, .mask-toggle svg, .info-button svg, .breadcrumb-segment svg, .shared svg, .card svg, svg.launch, #record-version-details-view svg, svg.delete, .add-item svg, .phone-input svg, .address-input svg, svg.download, svg.move, svg.searchIcon, .card-edit svg, .result-filters svg, .deletedItems svg, .login-account-icon svg {fill: #fff !important}
.display svg {filter: invert(100%) hue-rotate(180deg)}
div[role="button"]:hover span, a:hover span {color: #ffc700;text-shadow: 0px 0px 4px #ffc700 !important;}
div[role="button"]:hover svg, a:hover svg, li.folder:hover svg, li.passwordli:hover svg {fill: #ffc700 !important;}
span:hover, li:hover, .root-target:hover {color: #ffc700 !important;text-shadow: 0px 0px 4px #ffc700 !important;}
.root-target:hover svg {fill: #ffc700 !important}
.ui-widget-content li:hover a {background: rgba(60,60,60,.75) !important;color: #ffc700 !important;}


.formGroup label, .mask-wrap .masked-value, #security-audit .audits .audit-list .overview li.audit-list-item .name .login {color: #fff}
.phone-input {margin: 0}
.phone-input .number {padding: 12px 15px 0 15px}
.phone-input > svg {margin: 0px 0px 26px 12px}
.payment-card-item.selected {background: rgba(60,60,60,.5)!important}


.vaultPopup, #delete_forever_popup, #record-history-view, #record-version-details-view, #login .contents #login-account-selection .content, .tfa-content, #create-folder-popup {background:rgba(0,0,0,.85);}
.totp.tfa-content {background: rgba(0,0,0,.5)}
.vaultPopup.popup-menu, .ui-widget-content, .ui-widget-content li a {background:rgba(30,30,30,1) !important;}
.vaultPopup span, .ui-autocomplete-category {color: #fff}
.vaultPopup svg, .search-icon svg.icon-large, .permission svg.icon-large {fill: #fff !important}
.vaultPopup .selected, #login .contents #login-account-selection .content .selected {background: rgba(60,60,60,.5)!important}
.vaultPopup.popup-menu, .folder-selector-input {color:#fff}
.vaultPopup.alert{color: #fff}
#settings-overlay .right-pane {padding-right: 10px}
.settings-popup {padding-top: 32px !important; padding-left: 20px !important; padding-right: 20px !important;}
#general-settings, #security-settings, #settings-keeperfill, #import-settings, #backup {margin: 0 10px 0 0px}
#record-version-details-view .col-end, .owner {color: #aaa !important}
.folder-selector-selection{background: rgba(30,30,30,1) !important}
.folder-selector-suggestions {background: rgb(30,30,30,1)}
.folder-selector-selection svg.icon-dropdown, .folder svg {fill: #fff}
.folder-selector-suggestions li:hover, .folder-selector-suggestions li.selected {background: rgba(60,60,60,.5)!important}

a, .forgot-password, .create-an-account {color: #81bdff !important;}

.recent-activity .activity-row:nth-child(2n) {background: #222}
.selected_format {color: #aaa}
.login-box {background: rgba(20,20,20,.5) !important;}
svg.logo{filter: drop-shadow(0 0 1em #fff)  !important}

.listHeader, .header {background-color: rgba(0,0,0,.5) !important}
.launch-button {color: #fff !important;background: rgba(0,0,0,.25) !important; box-shadow: inset 0 -1px 0 0 #fff, inset -1px 0 0 0 #fff, inset 1px 0 0 0 #fff, inset 0 1px 0 0 #fff;}
.launch-button svg, .files svg {fill: #fff !important}
#main .overview li:hover, #main .overview li.selected {background: rgba(0,0,0,.25) !important}
.component-content {background: rgba(0,0,0,.25)}
#miAdminConsole .text {color: #fff}
.left-menu .menuItem:hover svg, .left-menu div:hover svg, #popup-menu-overlay .create-new-popup .menuItem:hover svg, .secureAddOns:hover svg{fill: #ffc700 !important}
.left-menu div:hover .text, .left-menu a:hover .text, #popup-menu-overlay .create-new-popup .menuItem:hover {color: #ffc700 !important}
.left-menu > div:hover, .left-menu > a:hover {background: rgba(0,0,0,.5) !important}
.left-menu > div.selected {background: rgba(0,0,0,.5)}
.left-menu > div:hover svg {fill:#fff !important}
#popup-menu-overlay .create-new-popup .menuItem:hover {background: rgba(60,60,60,.5)}
.secureAddOns svg {fill: #fff !important}

.record-details.read-only .formGroup .display-text {box-shadow: none !important; background-color: rgba(0,0,0,.25); margin-top: 5px; padding-left: 5px !important; border-radius: 4px}
.mask-wrap .masked-content {word-break: keep-all !important; }

.token {color: #fff !important}
.token:hover span, .allow-copy:hover div, .allow-copy:hover div a{color: #ffc700 !important; text-shadow: 0px 0px 4px #ffc700 !important }
.countdown-circle .base {stroke: rgba(0,0,0,.25)}
.countdown-circle {width: 50px; height: 50px}
.passwordli .shared svg, .passwordli .files svg, .passwordli .totp-icon svg, .passwordli .favorite svg, .totp-icon svg.icon-large {fill: #fff}

.set-profile-pic img, .personal-info-details .profile-pic {width: auto !important; height: auto!important; max-width: 200px!important; max-height: 200px!important;;}
.set-profile-pic img {margin-bottom: 40px; }
.personal-info-details .manage-image {margin-bottom: 0px}
.listHeader, .header {background-color: transparent !important}
.phone-input .number {padding: 0px 12px 0 12px}

.personal-info-details .formGroup, .payment-card-details .formGroup {border-bottom-width: 0px !important; padding-bottom: 4px; padding-top: 4px;}
.personal-info-details .formGroup .mask-wrap .display-text, #identity-payments .payment-card-details.read-only .formGroup .display-text  {box-shadow: none !important; background: rgba(0,0,0,.25); padding: 4px 8px; border-radius: 8px; }
.section-header, .read-only .header, #identity-payments .payment-card-details .card-view .phone-billing-address > label {border-bottom-width: 1px; border-color: rgba(255,255,255,.25); border-style: solid; padding-bottom: 6px;}
#identity-payments .payment-card-details .card-view .phone-billing-address > label {color: #fff; padding-top: 10px; margin-bottom: -8px}
.address-input {border-top: 0px }
.address-input .collapsed-section .names .formGroup, .state-form, .zip-form {border-color: transparent !important;}
#identity-payments .profile-list-item.selected {background: rgba(0,0,0,.25); box-shadow: none}
#identity-payments .cards-list .payment-card-item {background: rgba(0,0,0,.0); box-shadow: none; border-bottom: 1px solid rgba(255,255,255,.25);  }
.payment-card-item.selected {background: rgba(0,0,0,.5) !important}
.payment-card-item:hover {background: rgba(0,0,0,.25) !important;}
#identity-payments .details .read-only .mask-wrap {margin-top: 0px}
svg.icon-large {fill: #fff}
.personal-info-details .address-section, .personal-info-details .phone-section {margin-top: 16px}
.address-view {margin-top: 0}
#identity-payments .details {box-shadow: none}
#identity-payments .cards-list .card-image {width: 70px; height: 40px; background-size: 100%; border: none; }
#identity-payments .cards-list .payment-card-item .card-image {margin: -4px 16px 0px 16px}
#identity-payments .card-view .card-image {width: 70px; height: 40px; background-size: 100%; margin-left: 10px; margin-top: 0px !important }
#identity-payments .payment-card-details .card-view .form-number .number-wrap {height: 50px;}
#identity-payments .payment-card-details .card-view .form-number .number-wrap .mask-wrap {margin-left: -80px; margin-top: 0px; }
#identity-payments .payment-card-details .card-view .form-number .number-wrap .mask-wrap .display-text {padding-left: 100px; padding-top: 14px; padding-bottom:14px; }

.folder-details .records .record:hover {background: rgba(0,0,0,.25)}
.folder-details .optionsBar .folderSettings.enabled {color: #fff}
.folder-details .optionsBar .folderSettings.enabled svg {fill:#fff}
.search-div {background: rgba(0,0,0,.5)}
.lookup-search .search-div textarea {background: transparent !important}
.folder-details .perm.selected {background: rgba(0,0,0,.5)}
.folder-details .perm:hover {background: rgba(0,0,0,.25)}
.vault-checkbox.cb-checked {fill: #ffc700}
.deleteRow span {color: #fff !important}
.folderSettings span {color: #fff}

.tile .grid-card {background: rgba(0,0,0,.25); border-width: 4px; border-radius: 8px}
.tile .grid-card:hover {background: rgba(0,0,0,.5)}
.tile .grid-card .card-footer .card-login {color: #ccc}
.tile .grid-card .tile-logo {margin-bottom: 12px}
.tile .grid-card .card-title {padding-bottom: 4px; margin-bottom: 0px !important}
.tile .grid-card .card-login {padding-bottom: 4px; margin-bottom: -10px !important}
.grid-card button svg {fill: #fff !important }
.tile .grid-card:hover {box-shadow: none}


.grid-card .tile-logo svg {filter: drop-shadow(0 0 1px #000) drop-shadow(0 0 .5px #000) drop-shadow(0 0 1.5em #fff) drop-shadow(0 0 1.5em #fff) !important}
.grid-card .tile-logo .circle {background-color: rgba(255,255,255,.25) !important; height: 85px; width: 85px; margin-top: -20px}
.grid-card .tile-logo .circleText {font-size: 75px; color: rgba(255,255,255,.25); margin-top: 0px;}
.grid-card .launch-button:hover {color: #ffc700 !important; box-shadow: inset 0 -1px 0 0 #ffc700, inset -1px 0 0 0 #ffc700, inset 1px 0 0 0 #ffc700, inset 0 1px 0 0 #ffc700} 

#security-audit .audits .audit-list .overview {box-shadow: none; background: rgba(0,0,0,.25)}
.record-details .passwordStrengthMeter {background-color: rgba(0,0,0,.25)}
#security-audit .audits .audit-summary .result-filters .filter.active {background-color: rgba(0,0,0,.25)}
#security-audit .percentage .progress-arc {filter: invert() hue-rotate(180deg) brightness(200%) contrast(150%)}

.modal-overlay .logo {filter: drop-shadow(0 0 6px #fff)drop-shadow(0 0 1em #fff)  !important}
#breachwatch-container .title-header {background: rgba(0,0,0,.25); box-shadow: none}
.breachwatch-onboard-content {background: rgba(0,0,0,.25); padding: 20px}

.two-factor-list {background: transparent}
.two-factor-list .two-factor-row {box-shadow: none; border-bottom: 1px solid rgba(255,255,25,.25);}
.two-factor-list .two-factor-row.selected {background: rgba(0,0,0,.5)}
.two-factor-list .two-factor-row:hover {background: rgba(0,0,0,.25) }
.two-factor-list .two-factor-row .details {background: transparent}
.totp-field .token-delete .token-container {box-shadow: none}
.totp-field {border: none}
}
