@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("liveleak.com") {
  /*              Color Scheme               */
	/*f0f0f0 c8c8c8 b0aba0 757571 181712 6abedb*/

	/* Featured Items */
  ul.item_list h1 strong,
	/* Covers everything with Wildcard. */
	*:not(.vjs-text-track-display):not(:link):not(:visited):not(strong):not([class*="firstlevel"]):not([class*="vjs"]):not([class*="ra1"]):not([class*="pw"]):not(img):not([class*="tabs"]):not([class*="currect"]):not([id*="search"]):not([onclick*="#top_section_tabs li a"]):not([onclick*="#more_channel_list_tabs"]) {
		background: #000 !important;
		color: #757571 !important;}
	
	.vjs-text-track-display {display: none !important;}
	
	/* A Settings */
	a:not([onclick*="#top_section_tabs li a"]):not([onclick*="#more_channel_list_tabs"]) {
		color: #6abedb !important;}
	a:visited:not([onclick*="#top_section_tabs li a"]):not([onclick*="#more_channel_list_tabs"]) {
	color: #b0aba0 !important;}
	a:hover {
		text-decoration: underline !important;
		color: #c8c8c8 !important;}
	[class*="#player_file"],
	
	
	
	
	/* Button - Safe Mode */
	.red_button,
	/* Tabs - In the news, Yoursay, Must See */
	a[onclick*="#top_section_tabs li a"],
	/* Tabs - Featured, Current Events */
	a[onclick*="#more_channel_list_tabs"],
	/* Tabs - Related, User */
	a[onclick*="#more_item_list_tabs"],
	/* Tabs - Item Info, Links */
	a[onclick*="#item_detail_tabs"],
	/* Tabs - Home, Recent Items, Channels */
	#nav li a.firstlevela {
		border-radius: 4px;
		font-weight: normal;
		background: #000 !important;
		background: linear-gradient(#181712,rgb(12, 12, 9), #181712) !important;
		border:  1px #181712 solid !important;
		color: #909090 !important;}
	
	/* Button - Form Comments */
  .form_button,
	/* Tabs - Featured, Current Events, In the news, Yoursay, Must See = Current */
	ul.tabs li a.current, 
	/* Tabs - Home, Recent Items, Channels = Current */
	#nav li.current a {
		border-radius: 4px;
		font-weight: normal;
		background: #181712 !important;
		background: linear-gradient(#181712,rgb(12, 12, 9), #181712) !important;
		border:  1px #757571 solid !important;
		color: #6abedb !important;}

	/* Facebook Tweet Google+ Darkened */
	.button-type-looknative.pw-size-large .pw-button-counter-top, .button-type-looknative.pw-size-medium .pw-button-counter-top, .button-type-looknative.pw-size-small .pw-button-counter-top {
		filter: invert(100%) contrast(100%) brightness(125%);}
	.pw-widget {
		filter:  brightness(80%) contrast(110%);
		background: #000 !important;}
	/* Facebook Tweet Google+ Darkened - Line Indicators */
	.button-type-looknative.pw-size-large .pw-button-counter-top__brd, .button-type-looknative.pw-size-medium .pw-button-counter-top__brd, .button-type-looknative.pw-size-small .pw-button-counter-top__brd {
		display: none !important;}
	
	/* Page Navigation - Active Page Showed */
	.pagenav li.current {
		filter: invert(100%) hue-rotate(180deg) contrast(300%) brightness(25%);}

	/* Featured Items - Line Seperator */
	ul.item_list li {border-bottom: 1px solid #757571;}
	ul.item_list .item_info_column h4 {border-top: 1px solid #757571;}
	.scroll_list ul li {border-bottom: 1px solid #757571;}
	
	/* Search Button and Input Window */
	#search_submit {
		margin-top: 2mm;
		filter: brightness(60%) contrast(110%);}
	#search_input {  
		margin-top: 2.5mm;
		background: #111 !important;
		color: #909090 !important;
		border: 1px solid #111;}

	/* Image Edits - Playlist */
	img[src*="file_embed_playlist"],
	/* Image Edits - Next and Prev */
	img[src*="right.gif"],
	img[src*="left.gif"] {
		filter: invert(100%) hue-rotate(180deg) contrast(200%) brightness(50%);}

	/* Image Edits - Other */
	img[src*="rss"],
	img[src*="hd_video_icon"] {
		filter: invert(100%) hue-rotate(180deg) contrast(200%) brightness(80%);}
	img[src*="premium_star_icon.gif"] {
		filter: invert(100%) hue-rotate(180deg) contrast(100%) brightness(75%);}

	/* Logo */
	#logo{
		margin-top: 3mm;
		filter: brightness(65%) contrast(150%);}
	
	/* You need to be registered in order to add comments! */
	.msg {border: 1px #181712 solid;}
	
	/* Comments! */
	#comments li {
	padding: 0 0px 10px 0;
	margin: 0 0 10px 0;
	border-bottom: 1px solid #757571;
	clear: both;
	overflow: hidden;}
	
	/* Google ads Removed id="google_ad_228535994045"*/
	[id*="google_ad"]{
		display: none !important;}
	
	/* Ads */
	tbody,
	/* Theme Author Removed */
	#neado {display: none !important;}
	
}
