css:
@-moz-document domain("www.carsales.com.au") {
:root {
	--col1: #151515;
	--col2: #1b1b1b;
	--col3: #424242;
	--col4: #303030;
}
body,
#desktop,
.slick-slide img,
.main-list,
article,
aside,
details,
figcaption,
figure,
footer,
header,
main,
menu,
nav,
section,
summary,
.media-motive-block,
.details-page .mrec-rightpane,
.component--hero-image,
.component--ad-panel *,
.top-panel__inner-container,
.listings-page #r-contents .r-left-right,
.listings-page .header-container,
.refinement-pane,
.component__wrapper,
.details-page,
.layout-header-wrapper,
.site-nav-wrapper,
#enquiry-content .seller-details .contact,
.WriteReview---this---3iSF-,
.tabs-header .is-active a,
.upper-footer,
#r-footer ul.footcrumb,
#r-footer .enhanced-seo-footer,
#r-footer .footcrumb-container,
.site-nav .menu-links,
.listings-page .footer-divider,
.listings-page .actions-panel,
.membership-toolbox,
.listings-page .toolbar,
.layout-sidebar--scrolling__scroller,
.accordion-inner {
	background: var(--col1) !important;
}
.table-striped tbody tr:nth-child(even) td,
.table-striped tbody tr:nth-child(even) th,
.table-striped-inverse tbody tr:nth-child(odd) td,
.table-striped-inverse tbody tr:nth-child(odd) th,
.component--enquiry.horizontal .enquiry-form__right-area .sidebar .seller-details.dealer .contact,
.site-nav .nav-cars-for-sale .menu-panel,
.site-nav .menu-links a:hover,
.btn,
.component--homepage-search .select-box,
.table-striped tbody tr:nth-child(even) td,
.table-striped tbody tr:nth-child(even) th,
.table-striped-inverse tbody tr:nth-child(odd) td,
.table-striped-inverse tbody tr:nth-child(odd) th,
.component--enquiry.horizontal .enquiry-form__right-area .sidebar .seller-details.dealer .contact,
.site-nav .nav-cars-for-sale .menu-panel,
.site-nav .menu-links a:hover,
.btn,
.component--homepage-search .select-box,
.component--homepage-search .select-display,
.component--homepage-search .select-box:after,
.component--homepage-search .keyword-search,
.editorial-slider,
.editorial-videos,
.editorial-latest,
.carpool-latest,
#demo-right,
.suggested-cars__item__body,
.suggested-cars__item__hero,
.listings-page .listing-item,
.listings-page .finance-repayments,
.listings-page .tabbed-pagination,
.listings-page .tabbed-pagination .pagination-container .pagination-jump-to .csn-select .select-box,
.listings-page .tabbed-pagination .csn-select .select-menu,
.listings-page .tabbed-pagination .pagination .current span,
.listings-page .tabbed-pagination .result-count-switch ul li a,
.listings-page .pmg-sponsoredlinks,
.listings-page .refinement-pane .guided-nav-wrapper,
.sort-type,
.listings-page .refinement-pane .guided-nav .dimension .dimension-selection,
.breadcrumb-value span,
.listings-page .refinement-pane .guided-nav .dimension .dimension-selection .aspect-label .icon-breadcrumb-hook,
.listings-page .refinement-pane .guided-nav .dimension .dimension-selection .breadcrumb-value .icon-breadcrumb-hook,
.listings-page .refinement-pane .guided-nav-pane .search-reset,
.listings-page .refinement-pane .guided-nav .dimension .dimension-summary,
.listings-page .refinement-pane .guided-nav .dimension .dimension-selection ul[class^=tier] .expand-link,
.listings-page .refinement-pane .guided-nav .dimension,
.listings-page .refinement-pane .guided-nav .dimension .add-another,
select,
.seller-details,
.details-page .sponsored-ads-container,
.listings-page .search-results-pane .sort-type .csn-select .select-menu,
.main-search-form__item__inner {
	background: var(--col2) !important;
}
.remove {
	background-color: var(--col2) !important;
}
.listings-page .refinement-pane .guided-nav .dimension .dimension-selection.postcode-search .postcode-input input,
.component--enquiry.horizontal .enquiry-form__right-area .sidebar .seller-details .private-sellers-contact-details,
.listings-page .search-results-pane .sort-type .csn-select .select-menu .selected,
.listings-page .search-results-pane .sort-type .csn-select .select-menu a:hover,
.module-media-gallery .gallery-canvas .gallery-next span,
.module-media-gallery .gallery-canvas .gallery-prev span {
	background: var(--col4) !important;
}
#demo-right {
	border: 10px solid var(--col2) !important;
}
.component--homepage-search {
	border-right: 1px solid var(--col1) !important;
}
.seller-details .section {
	border-top: 1px solid var(--col1) !important;
}
.tabs-header .is-active a {
	border-left: 1px solid var(--col3) !important;
	border-right: 1px solid var(--col3) !important;
}
.tabs-header,
.component--enquiry-docking-bar {
	border-bottom: 1px solid var(--col3) !important;
}
.main-banner__logo,
#sitelogo,
.sprite-base-logo-large,
.csn-site-nav-header .csn-site-nav-logo,
.csn-site-nav-logo-alt,
#footcrumb-home {
	filter: invert(1) !important;
}
* {
	color: #ffffff !important;
	border-color: var(--col1) !important;
}
h1,
h2,
h3,
.listings-page .refinement-pane .guided-nav .dimension .dimension-selection ul[class^=tier] .breadcrumb-value span,
.price-value {
	color: #ffffff !important;
}
.component--showroom .component__wrapper .showroom .showroom__list__link {
	color: #ffffff !important;
}
/* fonts */

textarea,
input {
	background: var(--col2) !important;
}
.component--enquiry.vertical .enquiry-form .module-enquiry-form .control input,
.component--enquiry.vertical .enquiry-form .module-enquiry-form .control textarea {
	color: #ffffff !important;
}
/* enquire form */
}
