css:
@-moz-document url-prefix("https://www.lumosity.com/app/v4/games/") {
#gameCanvas,
#content {
    width: 100% !important;
    height: 700px !important;
    text-align: center !important;
}
#content a {
    display: none;
}
#game-container {
    width: 100% !important;
    text-align: center !important;
    height: 700px !important;
}
#sidebar {
    width: 100% !important;
    text-align: center !important;
    margin-top: 65px !important;
}
}

@-moz-document url-prefix("https://www.lumosity.com/") {
body {
    background-color: #ececec !important;
}
.authed .masthead #account-nav .unlock-access-button {
    display: none !important;
}
}

@-moz-document url("https://www.lumosity.com/app/v4/brain_profile/") {
#lumosity-value {
    display: none !important;
}
}

@-moz-document url("https://www.lumosity.com/app/v4/games") {
.overlay,
.corner-lock {
    display: none !important;
    pointer-events: none !important;
    cursor: default !important;
}
.locked a,
.locked {
    pointer-events: none !important;
    cursor: default !important;
}
.unlocked_games {
    position: absolute !important;
    top: -5% !important;
    border-bottom: none !important;
    width: 100%;
}
.unlocked_games .unlocked:hover {
    background-color: #146c78 !important;
}
.masthead-wrapper {
    height: 300px !important;
}
.unlocked_games h4 {
    display: none !important;
}
.unlocked_games .game-item-grid {
    border: none !important;
}
.unlocked_games h6,
.unlocked_games h5 {
    color: #fff !important;
}
.unlocked_games .brain-attribute {
    color: black !important;
}
}
