css:
@-moz-document domain("thatjapanesecourse.com") {
body,
.site
{
    background: #181818 !important;
    color: white !important;
}
.wpcw_fe_quiz_box_pending,
table
{
    background: #111 !important;
    color: white !important;
    border: solid black;
}
#wpcw_fe_course .wpcw_fe_module td,
cite span
{
    background: rgba(158, 158, 158, .5) !important;
}
h2,
h3,
h1.entry-title
{
    color: inherit;
    text-align: center;
}
.site-header,
div.page-header
{
    background: black;
    color: white;
    text-align: center;
}
.site-text-logo
{
    color: #03c4eb !important;
    margin-right: auto;
    margin-left: auto;
}
.page-header .entry-title,
.page-header .page-title
{
    text-align: center
}
#primary
{
    border: none
}
#site-navigation
{
    margin-right: auto !important;
    margin-left: auto !important;
    align-content: center;
    text-align: center
}
#comments .comment .comment-wrapper,
#comments textarea
{
    background: black;
    color: white;
}
#comments .comment .comment-wrapper::before
{
    border-color: black;
}
::selection
{
    background-color: gray
}
.onepress-menu li a
{
 	color: white
}
.onepress-menu>li
{
    position: relative;
 	left: 15%;
    line-height: 2em
}
.wpcw_widget_progress #wpcw_fe_course,
.sidebar .widget,
div#secondary,
widget-area sidebar,
wpcw_course_progress-2
{
 	float: left;
}
.wpcw_widget_progress #wpcw_fe_course
{
	width: 150%;
    display: inline-block;
    margin: auto;
}
}
