css:
@-moz-document domain("www.webassign.net") {
/* Questions Page */

body {
	background-color: #1c1c1c;
}
input[type="submit"],
sButton {
	background: #003ea3;
	color: #fff !important;
	border: none;
	border-radius: 5px;
	box-shadow: 0 2px 5px #00225b;
	display: inline-block;
	padding: 8px 8px;
	font-size: 16px;
	text-align: center;
	text-decoration: none;
	cursor: pointer;
}
input[type="submit"]:disabled,
sButton:disabled{
	background: #365c9b;
}
input[type="submit"]:active {
	transform: translateY(2px);
}
div.standard.qContent.container {
	background-color: #2d2d2d;
}
.waQBox>.qUtility>.qButtons {
	background: #2d2d2d;
}
.watexline.span,
.studentQuestionBox,
.studentQuestionContent {
	color: #fff;
}
.watexinlineblock.span {
	color: #fff !important;
}
div.pageLeft.container h1 {
	color: #fff !important;
}
h2 {
	color: #686868;
}
p,
h4 {
	color: #fff !important;
}
img {
	filter: invert(100%);
	-webkit-filter: invert(100%);
}
.figure {
	filter: invert(100%);
	-webkit-filter: invert(100%);
}
.first {
	background-color: #1c1c1c !important;
}
.first a {
	color: #484adb;
}
.crumbs a {
	color: #484adb;
}
.finalLinks a {
	color: #484adb;
}
.top div {
	background: #003ea3 !important;
}
.top-nav--logo {
	filter: invert(100%);
	-webkit-filter: invert(100%);
}
#studentNav {
	background: #003ea3 !important;
	box-shadow: 0 2px 5px #00225b;
}
#studentNav div {
	color: #fff;
	background: #003ea3 !important;
}
#studentNav a {
	color: #fff;
}
#studentNav a:hover {
	color: #777777;
}
.studentNavBorder {
	display: none;
}
.badge {
	filter: invert(100%);
	-webkit-filter: invert(100%);
}
.assignmentQuestionDetails {
	filter: invert(100%);
	-webkit-filter: invert(100%);
}
.columnContent td.points {
	filter: invert(100%);
	-webkit-filter: invert(100%);
}
font {
	color: #fff;
}
span {
	color: #fff !important;
}
.extraContent p >:first-child {
	content: url("https://i.imgur.com/iDnDkkA.png");
}
a,
a:link,
a:active,
a:visited,
.previousQuestionLink span font,
.nextQuestionLink span font {
	color: #7c7c7c;
}
.waBox .subTools {
	background: #2d2d2d;
}
.rowLeft a {
	color: #d1d1d1;
}
.elasticTable.assignmentQuestionDetails .columnContent td.points {
	background-color: #636363;
}
.full_credit {
	background-color: #3c5e37 !important;
}
.no_credit {
	background-color: #5e3737 !important;
}
/* Current Assignments Page */

.subTask.container {
	background: #2d2d2d !important;
}
.row.container {
	background: #5e5e5e !important;
}
.link a {
	color: #484adb !important;
}
a strong {
	color: #003ea3;
	/*color: #484adb;*/
}
}

@-moz-document domain("www.webassign.net") {
/** Blue.css **/

#studentNav {
	background: #003ea3;
}
/* waBox StudentSide
-------------------------------------*/

.waBox .bar {
	border-bottom: solid 1px #003ea3;
	background: #003ea3;
}
.waBox .bBorder {
	border-bottom: 1px solid #003ea3;
}
.waBox .studentBoxBorder {
	border-left: 1px solid #003ea3;
	border-right: 1px solid #003ea3;
}
.waBox .subTask {
	background: #eee;
}
/* Final Submit box
-------------------------------------*/

.finalSubmitBox {
	background: #003ea3;
}
.finalSubmitBox .finalSubmitBoxBorder {
	background: #003ea3;
	border: 1px solid #003ea3;
	border-top: 0;
	border-bottom: 0;
}
.finalSubmitBox .bBorder {
	background: #003ea3;
	border-bottom: 1px solid #003ea3;
}
.finalSubmitBox .tBorder {
	border-bottom: 1px solid #003ea3;
}
.finalSubmitBox .lTopCorner {
	display: none;
}
.finalSubmitBox .rTopCorner {
	display: none;
}
.finalSubmitBox .bBox {
	display: none;
}
.finalSubmitBox .borderWrap {
	display: none;
}
/* New box styles (replaces studentBox)
-------------------------------------*/

.waBox .content {
	border-left: 1px solid #003ea3;
	border-right: 1px solid #003ea3;
}
.waBox .top .border {
	background: #003ea3;
}
.waBox .bottom .border {
	border-bottom: 1px solid #003ea3;
}
.waBox .title {
	background: #003ea3;
}
.waBox .top > .left {
	display: none;
}
.waBox .top > .right {
	display: none;
}
.waBox .bottom > .left {
	display: none;
}
.waBox .bottom > .right {
	display: none;
}
/* New question box styles (replaces studentQuestionBox)
-------------------------------------*/

.waQBox .top .border,
.waQBox .bottom .border {
	background: #003ea3;
}
.waQBox .top .border {
	border-top: 1px solid #003ea3;
}
.waQBox .bottom .border {
	border-bottom: 1px solid #003ea3;
}
.waQBox .title {
	font: normal 12px/22px arial, helvetica, sans-serif;
	background: #003ea3;
	border-left: 1px solid #003ea3;
	border-right: 1px solid #003ea3;
}
.waQBox .top > .left {
	display: none;
}
.waQBox .top > .right {
	display: none;
}
.waQBox .bottom > .left {
	display: none;
}
.waQBox .bottom > .right {
	display: none;
}
/* communication skin styles
-------------------------------------*/

#comWrap {
	border-top: 3px solid #003ea3;
}
/* DESCRIPTION: theme styles for Assignment Editor
--------------------------------------*/
/* form grid styles
--------------------------------------*/

.form .settingsGrid {
	border-bottom: 1px solid #003ea3;
}
.form .settingsGrid th {
	border-top: 1px solid #003ea3;
	border-right: 1px solid #003ea3;
	background: #d9e6ef;
}
.form .settingsGrid td {
	border-top: 1px solid #003ea3;
	border-right: 1px solid #003ea3;
}
.form .settingsGrid th.first,
.form .settingsGrid td.first {
	border-left: 1px solid #003ea3;
}
.form .settingsGrid .tableDivide {
	background: #369;
}
#assignmentSettings .form .na {
	color: #003ea3;
}
/* question list 
--------------------------------------*/

.questionList .masterHeader .settings {
	background: #4684c1;
}
.questionList .masterHeader .settingDesc {
	background: #003ea3;
}
.questionList table .qs,
.questionList table .qps,
.questionList table .qip {
	background: #003ea3;
}
.questionList .questionLinks {
	background: #003ea3;
}
.questionList .qBox .content {
	background: #c1d6e7;
}
/* Show My Work theme styles 
--------------------------------------*/

.smw {
	background: #d9e6ef;
}
.smw textarea {
	border: 1px solid #003ea3;
}
.claro .dijitEditor {
	margin-bottom: 1em;
	border: 1px solid #003ea3;
}
.claro .dijitToolbar {
	background-color: #003ea3;
	border-bottom: 1px solid #003ea3;
}
/* WA/View/templates/assignment/grader_grade_work.tpl */

.paginationBar {
	background: #C1D6E7;
}
.studentDivider {
	height: 5px;
	background: #369;
}
/* Global Color Classes
  Sets a few common colors based on theme
--------------------------------------*/

.theme-dark {
	border-color: #003ea3;
	background-color: #003ea3;
}
.theme-light {
	border-color: #003ea3;
	background-color: #003ea3;
}
}
