css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bugzilla.com") {
  /* Solarized color definitions */
    :root {
        --base03: #002b36;
        --base02: #073642;
        --base01: #586e75;
        --base00: #657b83;
        --base0: #839496;
        --base1: #93a1a1;
        --base2: #eee8d5;
        --base3: #fdf6e3;
        --yellow: #b58900;
        --orange: #cb4b16;
        --red: #dc322f;
        --magenta: #d33682;
        --violet: #6c71c4;
        --blue: #268bd2;
        --cyan: #2aa198;
        --green: #859900;
    }
    body {
        background: none;
        background-color: var(--base02);
        overflow-x: hidden;
    }
    #bugzilla-body {
        background-color: var(--base02);
        border-color: var(--base02);
        color: #c0c0c0
    }
    .bz_alias_short_desc_container {
        background-color: var(--base03);
        border-color: #60666a;
        color: #c0c0c0;
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    .bz_time_tracking_table th {
        background-color: #9d9c9c;
        border-color: #60666a;
        color: #030202
    }
    #titles {
        background-color: var(--base03);
        border-color: #60666a;
        color: #c0c0c0
    }
    #header .links,
    #footer {
        background-color: var(--base03);
        border-color: #60666a;
        color: #c0c0c0;
    }
    #footer li {
        padding: 2px;
    }
    #header .links {
        border: none;
    }
    #footer {
        border: none;
        padding: 1px;
    }
    .bz_row_odd {
        background-color: #6b6a6a;
        border-color: #60666a;
        color: #030202
    }
    .bz_row_even {
        background-color: #9d9c9c;
        border-color: #60666a;
        color: #030202
    }
    input.required,
    select.required,
    span.required_explanation {
        background-color: #1C1F22;
        color: #c0c0c0
    }
    #Create .comment {
        color: #c0c0c0
    }
    #message {
        border-color: #60666a;
        color: #c0c0c0
    }
    /*   Hyperlinks section */
    
    a {
        color: var(--blue);
    }
    a:visited {
        color: var(--violet);
    }
    a:hover {
        color: var(--orange);
    }
    /*   Attachment table section */
    
    #attachment_table {
        border-collapse: separate;
        color: var(--base2);
        border-radius: 3px;
        -webkit-border-radius: 3px;
    }
    #attachment_table th,
    .bz_attach_footer,
    .bz_time_tracking_table th {
        background-color: var(--base03);
        border-color: var(--base01);
    }
    #attachment_table td {
        border: none;
        color: var(--base2);
    }
    .bz_closed {
        text-decoration: none;
        background-color: #859900;
    }
    .bz_closed a {
        color: var(--base02);
    }
    .bz_closed a:visited {
        color: var(--base03);
    }
    /*   Quotes inside comments */
    
    .quote {
        display: block;
        background-color: #c7c5c5;
        font-style: italic;
        color: var(--orange);
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    /*   Comment section */
    
    .bz_first_comment_head {
        background-color: var(--base03);
        padding: 3px;
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    .bz_comment_head {
        background-color: var(--base03);
        padding: 3px;
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    .bz_comment {
        background-color: var(--base01);
        border-color: #60666a;
        padding: 5px;
        -webkit-border-radius: 3px;
        border-radius: 3px;
        color: var(--base03);
    }
    .bz_comment_text a {
        color: var(--base02);
    }
    .bz_comment_text a:visited {
        color: var(--base03);
    }
    .bz_comment_time {
        color: var(--base1);
    }
    /*   Input and text area */
    
    input {
        padding: 5px;
        border: 2px solid #ccc;
        background-color: var(--base2);
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    input:focus {
        border-color: #333;
    }
    textarea {
        padding: 5px;
        border: 2px solid #ccc;
        background-color: var(--base2);
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    textarea:focus {
        border-color: #333;
    }
    input[type=submit] {
        padding: 2px 10px;
        background: #ccc;
        border: 0 none;
        cursor: pointer;
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
    select {
        padding: 2px;
        border: 2px solid #ccc;
        background-color: var(--base2);
        -webkit-border-radius: 3px;
        border-radius: 3px;
    }
}
