css:
@-moz-document url-prefix("https://f.tabtter.jp/") {

    div.frt,
    div.u,
    div.u em,
    span.u em {
        font-size: 10px !important;
        color: #999999 !important;
    }

    span.susa {
        font-size: 10px !important;
        color: #333 !important;
    }

    p.repex {
        font-size: 10px !important;
    }

    #googlead {
        opacity: 0.01 !important;
    }

    #scol {
        width: 0 !important;
        height: 0 !important;
    }

    #main,
    #tabs,
    #tabpanel,
    #tweetbox {
        left: 0 !important;
        margin-left: 0 !important;
        width: 100% !important;
    }

    #tweetbody {
        min-width: 400px !important;
    }

    #scol-handler {
        width: 0 !important;
        display: none !important;
    }

    img.tip,
    img.p,
    img.tpb {
        opacity: 0.5;
    }


}
