css:
@-moz-document domain("4chan.org") {
.adg-rects>span.mobile {
    display: none!important;
}
body {
    background: #36393e!important;
    color: #87909c!important;
}
div#boardNavMobile {
    -webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, .2), 0 2px 0 rgba(0, 0, 0, .06)!important;
    background: #36393f!important;
    box-shadow: 0 1px 0 rgba(0, 0, 0, .2), 0 2px 0 rgba(0, 0, 0, .06)!important;
    border-bottom: none!important;
}
.pageJump a,
div.post div.postInfoM span.postNum a,
div.postInfo span.postNum a,
.postMenuBtn,
div.postLink span.info,
.abbr a {
    color: #87909c!important;
}
div.post div.postInfoM span.dateTime {
    color: hsla(0, 0%, 100%, .2)!important;
}
div#boardNavMobile select,
div#boardNavMobile option,
select[size],
select[multiple],
select[size][multiple] {
    background-color: #4b4d53;
    border-color: transparent;
    color: #b9c0ca;
}
a.mobilePostFormToggle,
.mobileib,
#globalToggle,
div.postLink a,
.button.m-depagelink,
#postForm input[type="submit"],
.UIPanel button,
div.mPagelist a.button,
.yotsuba_new .mobilebtn .btn-wrap,
.futaba_new .mobilebtn .btn-wrap {
    border-radius: 3px;
    font-weight: normal;
    border-color: #7289da;
    background: #7289da;
    color: #fff!important;
}
.button a,
a.button:hover,
.button a:visited,
a.linkified,
.mPagelist.mobile > .pages strong > a {
    color: #fff!important;
}
div.post div.postInfoM {
    border-bottom: none;
    background-color: #282b30;
    -webkit-box-shadow: inset 0 -1px 3px rgba(0, 0, 0, .1);
    box-shadow: inset 0 -1px 3px rgba(0, 0, 0, .1);
}
div.opContainer,
div.reply {
    background-color: #2f3136;
    border: none!important;
}
div.postLink,
.backlink.mobile {
    background-color: rgb(42, 44, 49);
    border-top: none;
}
.reply:target,
.reply:focus,
.reply.highlight {
    border: 1px solid rgba(79, 84, 92, .6)!important;
    background: rgba(79, 84, 92, .6)!important;
}
.quoteLink,
.quotelink,
.deadlink,
.burichan_new .backlink a,
.yotsuba_b_new .backlink a,
.abbr a,
#captchaFormPart>td>div a,
.globalMessage a,
#settings-exp-all a,
div.mPagelist a,
a.replylink:not(:hover),
div#absbot a:not(:hover),
a[href="javascript:postBack()"],
div.post blockquote.postMessage a[data-cmd="embed"],
#announce a,
.top-box a,
#ft li a,
#copyright a {
    color: #7289da!important;
}
s,
s a:not(:hover) {
    color: #202225!important;
    background: #202225!important;
}
div.post div.postInfoM span.subject {
    color: hsla(0, 0%, 100%, .6);
}
.ws .dd-menu ul {
    -webkit-box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
    background-color: #fff;
    border: 1px solid hsla(0, 0%, 75%, .2);
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, .1);
}
.ws .dd-menu li {
    border-bottom: 1px solid rgba(0, 0, 0, .1);
}
td>input[type="text"],
td>textarea,
td>input[type="password"] {
    background-color: hsla(218, 5%, 47%, .3)!important;
    color: #fff;
}
div.mPagelist,
hr,
.panelHeader {
    border-color: #43464b!important;
}
.nameBlock.capcodeMod span.name,
span.capcodeMod a span.name,
span.capcodeMod span.postertrip,
span.capcodeMod strong.capcode {
    color: #cc3dcc!important;
}
div.post div.postInfoM span.nameBlock span.name,
div.postInfo span.nameBlock span.name {
    color: #2cb56f;
}
#announce,
.top-box,
.panel {
    background: #2f3136;
    color: #72767d;
    border: none;
}
#announce .boxbar,
.boxbar {
    background: #282b30;
    color: hsla(0, 0%, 100%, .6);
    border: none;
}
.c-thread img {
    border-color: #72767d;
}
.meta .postMenuBtn {
    display: inline-block;
}
table.postForm>tbody>tr>td:first-child {
    font-weight: normal;
    border-color: #202225;
    background: #202225;
    color: hsla(0, 0%, 100%, .3);
}
div#absbot {
    color: #96b4aa;
}
a,
#absbot a,
.button {
    text-shadow: none;
    /* Stupid Halloween */
}
.reply:target,
.reply:focus,
.reply.highlight {
    background: rgb(31, 32, 35)!important;
}
.preview {
    background-color: #1c1d1f;
    border: 1px solid #1c1d1f!important;
}
}
